import React from "react";

const ChatbotPanel = ({
  chatbotReady,
  chatHistory,
  inputValue,
  setInputValue,
  handleUserResponse,
  currentNodeId
}) => {
  const handleSubmit = (e) => {
    e.preventDefault();
    const trimmed = inputValue.trim();
    if (trimmed) {
      handleUserResponse(trimmed);
      setInputValue("");
    }
  };

  if (!chatbotReady) {
    return (
      <div style={{ width: "400px", borderLeft: "1px solid #ccc", padding: 20, background: "#f7fdf9", fontFamily: "Segoe UI, sans-serif", display: "flex", alignItems: "center", justifyContent: "center" }}>
        <p style={{ color: "#065f46", fontWeight: "bold" }}>Submit a flow to activate chatbot.</p>
      </div>
    );
  }

  return (
    <div style={{ width: "400px", borderLeft: "1px solid #ccc", display: "flex", flexDirection: "column", background: "#f0fdf4" }}>
      <div style={{ background: "#10b981", padding: "10px 16px", color: "white", fontWeight: "bold", borderTopRightRadius: "10px", borderBottomRightRadius: "10px", fontSize: 16 }}>
        ðŸ’¬ MindPeace Chatbot
      </div>

      <div style={{ flex: 1, overflowY: "auto", padding: "16px", fontFamily: "Segoe UI, sans-serif" }}>
        {chatHistory.map((msg, idx) => (
          <div
            key={idx}
            style={{
              display: "flex",
              justifyContent: msg.sender === "user" ? "flex-end" : "flex-start",
              marginBottom: 10
            }}
          >
            <div
              style={{
                background: msg.sender === "user" ? "#dcfce7" : "#e0f2fe",
                color: "#111827",
                padding: "8px 12px",
                borderRadius: "16px",
                maxWidth: "80%",
                boxShadow: "0 1px 4px rgba(0,0,0,0.1)",
                whiteSpace: "pre-wrap"
              }}
            >
              {msg.message}
            </div>
          </div>
        ))}
      </div>

      <form
        onSubmit={handleSubmit}
        style={{
          borderTop: "1px solid #ccc",
          padding: "12px",
          background: "#ffffff",
          display: "flex",
          gap: 10
        }}
      >
        <input
          type="text"
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
          placeholder="Type your message..."
          style={{
            flex: 1,
            padding: "10px",
            border: "1px solid #d1d5db",
            borderRadius: "20px",
            fontSize: "14px"
          }}
          disabled={!currentNodeId}
        />
        <button
          type="submit"
          style={{
            background: "#10b981",
            color: "white",
            border: "none",
            padding: "10px 16px",
            borderRadius: "20px",
            fontWeight: "bold",
            cursor: "pointer"
          }}
          disabled={!currentNodeId}
        >
          Send
        </button>
      </form>
    </div>
  );
};

export default ChatbotPanel;
