import React, { useState, useCallback, useMemo } from "react";
import ReactFlow, {
  ReactFlowProvider,
  addEdge,
  applyNodeChanges,
  applyEdgeChanges,
  Background,
  Controls,
  MiniMap,
} from "reactflow";
import "reactflow/dist/style.css";

const nodeColors = {
  message: "#4F46E5",
  choice: "#2563EB",
  multi_choice: "#059669",
  capture: "#D97706",
  gpt: "#9333EA",
  end: "#EF4444",
};

let id = 0;
const getId = () => `${++id}`;

export default function App() {
  const [nodes, setNodes] = useState([]);
  const [edges, setEdges] = useState([]);
  const [selectedNodeId, setSelectedNodeId] = useState(null);
  const [selectedEdgeId, setSelectedEdgeId] = useState(null);
  const [history, setHistory] = useState([]);
  const [redoStack, setRedoStack] = useState([]);
  const [chatMessages, setChatMessages] = useState([]);
  const [userInputs, setUserInputs] = useState({});
  const [chatActive, setChatActive] = useState(false);
  const [currentNodeId, setCurrentNodeId] = useState(null);
  const [multiSelections, setMultiSelections] = useState([]);
  const [systemPrompt, setSystemPrompt] = useState(
    "You are MindPeace, a compassionate mental health assistant. Based on the user's shared info and emotional state, continue the conversation empathetically."
  );
  const [gptModel, setGptModel] = useState("gpt-3.5-turbo");

  const onNodesChange = useCallback((changes) => {
    pushToHistory();
    setNodes((nds) => applyNodeChanges(changes, nds));
  }, [nodes]);

  const onEdgesChange = useCallback((changes) => {
    pushToHistory();
    setEdges((eds) => applyEdgeChanges(changes, eds));
  }, [edges]);

  const onConnect = useCallback((params) => {
    pushToHistory();
    setEdges((eds) => addEdge({ ...params, label: "next" }, eds));
  }, [edges]);

  const pushToHistory = () => {
    setHistory((prev) => [...prev, { nodes, edges }]);
    setRedoStack([]);
  };

  const undo = () => {
    if (history.length === 0) return;
    const prevState = history[history.length - 1];
    setRedoStack((prev) => [...prev, { nodes, edges }]);
    setNodes(prevState.nodes);
    setEdges(prevState.edges);
    setHistory((prev) => prev.slice(0, -1));
  };

  const redo = () => {
    if (redoStack.length === 0) return;
    const nextState = redoStack[redoStack.length - 1];
    setHistory((prev) => [...prev, { nodes, edges }]);
    setNodes(nextState.nodes);
    setEdges(nextState.edges);
    setRedoStack((prev) => prev.slice(0, -1));
  };

  const addNode = () => {
    const newNode = {
      id: getId(),
      type: "default",
      position: {
        x: Math.random() * 400,
        y: Math.random() * 400,
      },
      data: {
        label: "",
        type: "message",
        capture: "",
      },
      style: {
        backgroundColor: nodeColors["message"],
        color: "white",
        padding: 10,
        borderRadius: 20,
        boxShadow: "0 4px 8px rgba(0,0,0,0.2)",
        width: 300,
      },
    };
    pushToHistory();
    setNodes((nds) => [...nds, newNode]);
  };
  const deleteNode = () => {
    if (!selectedNodeId) return;
    pushToHistory();
    setNodes((nds) => nds.filter((node) => node.id !== selectedNodeId));
    setEdges((eds) =>
      eds.filter((edge) => edge.source !== selectedNodeId && edge.target !== selectedNodeId)
    );
    setSelectedNodeId(null);
  };

  const updateNodeField = (field, value) => {
    pushToHistory();
    setNodes((nds) =>
      nds.map((node) =>
        node.id === selectedNodeId
          ? {
              ...node,
              data: {
                ...node.data,
                [field]: value,
              },
              style: {
                ...node.style,
                backgroundColor: nodeColors[value] || node.style.backgroundColor,
              },
            }
          : node
      )
    );
  };

  const updateEdgeField = (value) => {
    pushToHistory();
    setEdges((eds) =>
      eds.map((edge) =>
        edge.id === selectedEdgeId ? { ...edge, label: value } : edge
      )
    );
  };

  const exportToJson = () => {
    const json = {
      settings: {
        system_prompt: systemPrompt,
        gpt_model: gptModel,
      },
      nodes: nodes.map((node) => ({
        id: node.id,
        message: node.data.label,
        type: node.data.type,
        capture: node.data.capture || "",
        options: edges
          .filter((e) => e.source === node.id && e.label !== "next")
          .map((e) => ({
            label: e.label,
            next: e.target,
          })),
        next:
          edges.find((e) => e.source === node.id && e.label === "next")?.target || null,
      })),
    };
    const blob = new Blob([JSON.stringify(json, null, 2)], { type: "application/json" });
    const link = document.createElement("a");
    link.href = URL.createObjectURL(blob);
    link.download = "conversation_data.json";
    link.click();
  };

  const fetchGPTResponse = async (message, context) => {
    const res = await fetch("http://localhost:5000/gpt", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        system_prompt: systemPrompt,
        gpt_model: gptModel,
        message,
        context,
      }),
    });
    const data = await res.json();
    return data.reply;
  };

  const submitFlow = () => {
    const start = nodes[0];
    if (!start) return alert("Please add at least one node.");
    setCurrentNodeId(start.id);
    setChatMessages([{ sender: "bot", text: start.data.label }]);
    setChatActive(true);
  };
  const handleUserResponse = async (response) => {
    const currentNode = nodes.find((node) => node.id === currentNodeId);
    const updatedInputs = { ...userInputs };

    if (currentNode.data.capture) {
      updatedInputs[currentNode.data.capture] = response;
      setUserInputs(updatedInputs);
    }

    const edge = edges.find(
      (e) =>
        e.source === currentNodeId &&
        (e.label === response || e.label === "next")
    );

    if (!edge) {
      setChatMessages((msgs) => [
        ...msgs,
        { sender: "user", text: response },
        { sender: "bot", text: "I'm sorry, I didn't understand that." },
      ]);
      return;
    }

    const nextNode = nodes.find((node) => node.id === edge.target);

    if (!nextNode) return;

    if (nextNode.data.type === "gpt") {
      const contextSummary = Object.entries(updatedInputs)
        .map(([k, v]) => `${k}: ${v}`)
        .join(", ");
      const gptResponse = await fetchGPTResponse(response, contextSummary);
      setChatMessages((msgs) => [
        ...msgs,
        { sender: "user", text: response },
        { sender: "bot", text: gptResponse },
      ]);
    } else {
      setChatMessages((msgs) => [
        ...msgs,
        { sender: "user", text: response },
        { sender: "bot", text: nextNode.data.label },
      ]);
    }

    setCurrentNodeId(nextNode.id);
  };

  return (
    <div style={{ width: "100vw", height: "100vh", display: "flex" }}>
      {/* Left Panel */}
      <div style={{ flex: 1, position: "relative" }}>
        <ReactFlowProvider>
          <ReactFlow
            nodes={nodes}
            edges={edges}
            onNodesChange={onNodesChange}
            onEdgesChange={onEdgesChange}
            onConnect={onConnect}
            onNodeClick={(_, node) => setSelectedNodeId(node.id)}
            onEdgeClick={(_, edge) => setSelectedEdgeId(edge.id)}
            fitView
          >
            <Background />
            <MiniMap />
            <Controls />
          </ReactFlow>
        </ReactFlowProvider>

        {/* Buttons */}
        <div style={{ position: "absolute", top: 20, left: 20, display: "flex", gap: 10 }}>
          <button onClick={addNode} style={{ backgroundColor: "#3B82F6", color: "white", padding: "8px 12px", borderRadius: 8 }}>Add Node</button>
          <button onClick={exportToJson} style={{ backgroundColor: "#10B981", color: "white", padding: "8px 12px", borderRadius: 8 }}>Export JSON</button>
          <button onClick={deleteNode} style={{ backgroundColor: "#EF4444", color: "white", padding: "8px 12px", borderRadius: 8 }}>Delete Node</button>
          <button onClick={undo} style={{ backgroundColor: "mediumpurple", color: "white", padding: "8px 12px", borderRadius: 8 }}>Undo</button>
          <button onClick={redo} style={{ backgroundColor: "orange", color: "white", padding: "8px 12px", borderRadius: 8 }}>Redo</button>
          <button onClick={submitFlow} style={{ backgroundColor: "#22C55E", color: "white", padding: "8px 12px", borderRadius: 8 }}>Submit Flow</button>
        </div>

        {/* Node Editor */}
        {selectedNodeId && (
          <div style={{
            position: "absolute", top: 80, left: 20, padding: 10, background: "#f1f5f9",
            borderRadius: 8, boxShadow: "0 2px 6px rgba(0,0,0,0.2)", minWidth: 250
          }}>
            <strong>Editing Node: {selectedNodeId}</strong>
            <div>
              <div>Message:</div>
              <textarea
                value={nodes.find((n) => n.id === selectedNodeId)?.data.label}
                onChange={(e) => updateNodeField("label", e.target.value)}
                rows={4}
                style={{ width: "100%" }}
              />
            </div>
            <div>
              <div>Type:</div>
              <select
                value={nodes.find((n) => n.id === selectedNodeId)?.data.type}
                onChange={(e) => updateNodeField("type", e.target.value)}
              >
                <option value="message">message</option>
                <option value="choice">choice</option>
                <option value="multi_choice">multi_choice</option>
                <option value="capture">capture</option>
                <option value="gpt">gpt</option>
                <option value="end">end</option>
              </select>
            </div>
            <div>
              <div>Capture Field:</div>
              <input
                type="text"
                value={nodes.find((n) => n.id === selectedNodeId)?.data.capture || ""}
                onChange={(e) => updateNodeField("capture", e.target.value)}
              />
            </div>
          </div>
        )}

        {/* Edge Editor */}
        {selectedEdgeId && (
          <div style={{
            position: "absolute", top: 80, left: 300, padding: 10, background: "#f8fafc",
            borderRadius: 8, boxShadow: "0 2px 6px rgba(0,0,0,0.2)", minWidth: 150
          }}>
            <strong>Editing Edge: {selectedEdgeId}</strong>
            <div>
              <input
                type="text"
                value={edges.find((e) => e.id === selectedEdgeId)?.label || ""}
                onChange={(e) => updateEdgeField(e.target.value)}
                style={{ width: "100%" }}
              />
            </div>
          </div>
        )}
      </div>

      {/* Right Panel â€“ Chat + Prompt Settings */}
      <div style={{ width: 400, padding: 20, background: "#f8fafc", overflowY: "auto" }}>
        <h3>Prompt Settings</h3>
        <div>
          <div>System Prompt:</div>
          <textarea
            value={systemPrompt}
            onChange={(e) => setSystemPrompt(e.target.value)}
            rows={5}
            style={{ width: "100%" }}
          />
        </div>
        <div>
          <div>GPT Model:</div>
          <input
            type="text"
            value={gptModel}
            onChange={(e) => setGptModel(e.target.value)}
            style={{ width: "100%" }}
          />
        </div>

        <h3>MindPeace Chat</h3>
        {chatActive ? (
          <div>
            {chatMessages.map((msg, i) => (
              <div key={i} style={{ margin: "10px 0", textAlign: msg.sender === "user" ? "right" : "left" }}>
                <div style={{
                  display: "inline-block",
                  background: msg.sender === "user" ? "#DCF8C6" : "#E2E8F0",
                  padding: "8px 12px",
                  borderRadius: 12,
                  maxWidth: "90%",
                }}>
                  {msg.text}
                </div>
              </div>
            ))}
            <input
              type="text"
              placeholder="Type your message..."
              onKeyDown={(e) => {
                if (e.key === "Enter") handleUserResponse(e.target.value);
              }}
              style={{ width: "100%", padding: 8, marginTop: 10 }}
            />
          </div>
        ) : (
          <p>Please submit your flow to activate the chatbot.</p>
        )}
      </div>
    </div>
  );
}
