import React, { useCallback, useState, useMemo, useRef } from "react";
import ReactFlow, {
  MiniMap,
  Controls,
  Background,
  useNodesState,
  useEdgesState,
  addEdge,
  MarkerType
} from "reactflow";
import "reactflow/dist/style.css";

const initialNodes = [
  {
    id: "1",
    data: {
      message:
        "Hi there, I’m MindPeace — your friendly mental health support assistant. Would you like to begin our conversation?",
      type: "choice",
      options: {},
      capture: "",
      next: ""
    },
    position: { x: 250, y: 0 },
    type: "default"
  }
];

const initialEdges = [];
let nodeCounter = 2;

export default function App() {
  const [nodes, setNodes, onNodesChange] = useNodesState(initialNodes);
  const [manualEdges, setEdges, onEdgesChange] = useEdgesState(initialEdges);
  const [selectedNodeId, setSelectedNodeId] = useState(null);
  const [selectedEdgeId, setSelectedEdgeId] = useState(null);
  const fileInputRef = useRef(null);

  const selectedNode = nodes.find((n) => n.id === selectedNodeId);
  const selectedEdge = manualEdges.find((e) => e.id === selectedEdgeId);

  const getNodeStyle = (type) => {
    const baseStyle = {
      borderRadius: 10,
      padding: 10,
      boxShadow: "2px 2px 8px #ccc",
      whiteSpace: "pre-wrap",
      wordWrap: "break-word",
      fontFamily: "Segoe UI, sans-serif",
      minWidth: 180,
      maxWidth: 300
    };

    switch (type) {
      case "choice":
        return { ...baseStyle, background: "#E0F2FE", border: "1px solid #38bdf8" };
      case "input":
        return { ...baseStyle, background: "#DCFCE7", border: "1px solid #22c55e" };
      case "multi_choice":
        return { ...baseStyle, background: "#FEF9C3", border: "1px solid #eab308" };
      case "gpt":
        return { ...baseStyle, background: "#F3E8FF", border: "1px solid #8b5cf6" };
      case "end":
        return { ...baseStyle, background: "#FEE2E2", border: "1px solid #f87171" };
      default:
        return { ...baseStyle, background: "#F3F4F6", border: "1px solid #d1d5db" };
    }
  };

  const autoGeneratedEdges = useMemo(() => {
    const existingConnections = new Set(manualEdges.map((e) => `${e.source}->${e.target}`));
    const autoEdges = [];

    for (const node of nodes) {
      const target = node.data?.next;
      if (target && !existingConnections.has(`${node.id}->${target}`)) {
        autoEdges.push({
          id: `auto-${node.id}-${target}`,
          source: node.id,
          target,
          animated: false,
          type: "default",
          style: { strokeDasharray: "5,5", stroke: "#999" },
          markerEnd: { type: MarkerType.ArrowClosed },
          label: "next"
        });
      }
    }
    return autoEdges;
  }, [nodes, manualEdges]);

  const allEdges = [...manualEdges, ...autoGeneratedEdges];

  const onConnect = useCallback(
    (params) => {
      const label = prompt("Enter option key for this connection (e.g., yes, no, Stress):", "");
      if (!label) return;

      const edgeId = `e${params.source}-${params.target}`;
      setEdges((eds) =>
        addEdge(
          {
            ...params,
            id: edgeId,
            markerEnd: { type: MarkerType.ArrowClosed },
            label
          },
          eds
        )
      );

      setNodes((nds) =>
        nds.map((node) => {
          if (node.id === params.source) {
            const updated = { ...node };
            if (node.data.type === "choice" || node.data.type === "multi_choice") {
              updated.data.options = { ...updated.data.options, [label]: params.target };
            } else {
              updated.data.next = params.target;
            }
            return updated;
          }
          return node;
        })
      );
    },
    [setEdges, setNodes]
  );

  const addNode = () => {
    const newId = nodeCounter.toString();
    nodeCounter++;

    const position = selectedNodeId
      ? {
          x: nodes.find((n) => n.id === selectedNodeId).position.x + 220,
          y: nodes.find((n) => n.id === selectedNodeId).position.y + 50
        }
      : { x: Math.random() * 400, y: Math.random() * 400 };

    const newNode = {
      id: newId,
      data: {
        message: `This is node ${newId}`,
        type: "choice",
        options: {},
        capture: "",
        next: ""
      },
      position,
      type: "default"
    };
    setNodes((nds) => [...nds, newNode]);
  };

  const deleteNode = () => {
    if (!selectedNodeId) return;
    setNodes((nds) => nds.filter((n) => n.id !== selectedNodeId));
    setEdges((eds) => eds.filter((e) => e.source !== selectedNodeId && e.target !== selectedNodeId));
    setSelectedNodeId(null);
  };

  const onNodeClick = (_, node) => {
    setSelectedNodeId(node.id);
    setSelectedEdgeId(null);
  };

  const onEdgeClick = (_, edge) => {
    setSelectedEdgeId(edge.id);
    setSelectedNodeId(null);
  };

  const updateNodeField = (field, value) => {
    setNodes((nds) =>
      nds.map((node) =>
        node.id === selectedNodeId
          ? { ...node, data: { ...node.data, [field]: value } }
          : node
      )
    );
  };

  const updateEdgeLabel = (newLabel) => {
    if (!selectedEdge) return;

    const oldLabel = selectedEdge.label;
    const sourceId = selectedEdge.source;
    const targetId = selectedEdge.target;

    setEdges((eds) =>
      eds.map((edge) => (edge.id === selectedEdge.id ? { ...edge, label: newLabel } : edge))
    );

    setNodes((nds) =>
      nds.map((node) => {
        if (node.id === sourceId) {
          const updated = { ...node };
          if (updated.data.type === "choice" || updated.data.type === "multi_choice") {
            const newOptions = { ...updated.data.options };
            delete newOptions[oldLabel];
            newOptions[newLabel] = targetId;
            updated.data.options = newOptions;
          } else {
            updated.data.next = targetId;
          }
          return updated;
        }
        return node;
      })
    );
  };

  const exportJSON = () => {
    const output = {
      settings: {
        system_prompt:
          "You are MindPeace, a compassionate mental health assistant. Based on the user's shared info and emotional state, continue the conversation empathetically. Always respond warmly and supportively.",
        gpt_model: "gpt-3.5-turbo"
      },
      nodes: {}
    };

    nodes.forEach((node) => {
      const { id, data } = node;
      const { message, type, options, capture, next } = data;
      const nodeObj = { message, type };
      if (options && Object.keys(options).length > 0) nodeObj.options = options;
      if (capture) nodeObj.capture = capture;
      if (next) nodeObj.next = next;
      output.nodes[id] = nodeObj;
    });

    const blob = new Blob([JSON.stringify(output, null, 2)], {
      type: "application/json"
    });
    const url = URL.createObjectURL(blob);
    const link = document.createElement("a");
    link.href = url;
    link.download = "conversation_data.json";
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  const importJSON = (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = () => {
      const content = JSON.parse(reader.result);
      const newNodes = Object.entries(content.nodes).map(([id, node], i) => ({
        id,
        data: {
          message: node.message,
          type: node.type,
          options: node.options || {},
          capture: node.capture || "",
          next: node.next || ""
        },
        position: { x: 100 + i * 50, y: 100 + i * 50 },
        type: "default"
      }));
      setNodes(newNodes);
      setEdges([]);
      nodeCounter = Math.max(...newNodes.map((n) => parseInt(n.id))) + 1;
    };
    reader.readAsText(file);
  };

  return (
    <div style={{ width: "100vw", height: "100vh", fontFamily: "Segoe UI, sans-serif" }}>
      <div
        style={{
          position: "absolute",
          top: 10,
          left: "50%",
          transform: "translateX(-50%)",
          background: "#f3f4f6",
          padding: "10px 20px",
          borderRadius: "8px",
          boxShadow: "0 2px 6px rgba(0,0,0,0.1)",
          fontSize: "18px",
          zIndex: 20
        }}
      >
        <strong>Your AI Chatbot Designer — Fast. Visual. Code-Free</strong>
      </div>

      <div style={{ position: "absolute", top: 60, left: 10, zIndex: 10 }}>
        <button onClick={addNode} style={{ marginRight: 10, background: "#38bdf8", color: "#fff", padding: "8px 14px", borderRadius: "6px", border: "none" }}>
          Add Node
        </button>
        <button onClick={deleteNode} style={{ marginRight: 10, background: "#f87171", color: "#fff", padding: "8px 14px", borderRadius: "6px", border: "none" }}>
          Delete Node
        </button>
        <button onClick={exportJSON} style={{ marginRight: 10, background: "#10b981", color: "#fff", padding: "8px 14px", borderRadius: "6px", border: "none" }}>
          Export JSON
        </button>
        <button onClick={() => fileInputRef.current.click()} style={{ background: "#6366f1", color: "#fff", padding: "8px 14px", borderRadius: "6px", border: "none" }}>
          Import JSON
        </button>
        <input ref={fileInputRef} type="file" accept=".json" onChange={importJSON} style={{ display: "none" }} />
      </div>

      {selectedNode && (
        <div
          style={{
            position: "absolute",
            top: 110,
            left: 10,
            zIndex: 10,
            background: "#f3f4f6",
            padding: 10,
            border: "1px solid #ccc",
            borderRadius: "10px",
            width: "300px",
            boxShadow: "0 2px 6px rgba(0,0,0,0.1)"
          }}
        >
          <h4>Editing Node: {selectedNode.id}</h4>
          <div>
            <label>Message:</label>
            <textarea
              value={selectedNode.data.message}
              onChange={(e) => updateNodeField("message", e.target.value)}
              style={{ width: "100%", height: 60 }}
            />
          </div>
          <div>
            <label>Type:</label>
            <select
              value={selectedNode.data.type}
              onChange={(e) => updateNodeField("type", e.target.value)}
              style={{ width: "100%" }}
            >
              <option value="choice">choice</option>
              <option value="input">input</option>
              <option value="multi_choice">multi_choice</option>
              <option value="gpt">gpt</option>
              <option value="end">end</option>
            </select>
          </div>
          <div>
            <label>Capture Field:</label>
            <input
              type="text"
              value={selectedNode.data.capture}
              onChange={(e) => updateNodeField("capture", e.target.value)}
              style={{ width: "100%" }}
            />
          </div>
          <div>
            <label>Next Node ID:</label>
            <input
              type="text"
              value={selectedNode.data.next}
              onChange={(e) => updateNodeField("next", e.target.value)}
              style={{ width: "100%" }}
            />
          </div>
        </div>
      )}

      {selectedEdge && (
        <div
          style={{
            position: "absolute",
            top: 110,
            left: 10,
            zIndex: 10,
            background: "#f9fafb",
            padding: 10,
            border: "1px solid #ccc",
            borderRadius: "8px",
            width: "300px",
            boxShadow: "0 2px 6px rgba(0,0,0,0.1)"
          }}
        >
          <h4>Editing Edge</h4>
          <div>
            <label>Option Label:</label>
            <input
              type="text"
              value={selectedEdge.label}
              onChange={(e) => updateEdgeLabel(e.target.value)}
              style={{ width: "100%" }}
            />
          </div>
        </div>
      )}

      <ReactFlow
        nodes={nodes.map((n) => ({
          ...n,
          data: { ...n.data, label: n.data.message },
          style: getNodeStyle(n.data.type)
        }))}
        edges={allEdges}
        onNodesChange={onNodesChange}
        onEdgesChange={onEdgesChange}
        onConnect={onConnect}
        onNodeClick={onNodeClick}
        onEdgeClick={onEdgeClick}
        fitView
        edgeLabelMode="always"
      >
        <MiniMap />
        <Controls />
        <Background />
      </ReactFlow>
    </div>
  );
}
