// App.js
import React, { useState, useMemo, useRef } from "react";
import ReactFlow, {
  MiniMap,
  Controls,
  Background,
  useNodesState,
  useEdgesState,
  addEdge,
  MarkerType
} from "reactflow";
import "reactflow/dist/style.css";

const initialNodes = [
  {
    id: "1",
    data: {
      message:
        "Hi there, Iâ€™m MindPeace â€” your friendly mental health support assistant. Would you like to begin our conversation?",
      type: "choice",
      options: {},
      capture: "",
      next: ""
    },
    position: { x: 250, y: 0 },
    type: "default"
  }
];

const initialEdges = [];
let nodeCounter = 2;

export default function App() {
  const [nodes, setNodes, onNodesChange] = useNodesState(initialNodes);
  const [manualEdges, setEdges, onEdgesChange] = useEdgesState(initialEdges);
  const [selectedNodeId, setSelectedNodeId] = useState(null);
  const [selectedEdgeId, setSelectedEdgeId] = useState(null);
  const [chatbotReady, setChatbotReady] = useState(false);
  const [submittedFlow, setSubmittedFlow] = useState(null);
  const [currentNodeId, setCurrentNodeId] = useState(null);
  const [chatHistory, setChatHistory] = useState([]);
  const [userInputs, setUserInputs] = useState({});
  const [inputValue, setInputValue] = useState("");
  const [gptMode, setGptMode] = useState(false);

  const history = useRef([{ nodes: initialNodes, edges: initialEdges }]);
  const historyIndex = useRef(0);

  const selectedNode = nodes.find((n) => n.id === selectedNodeId);
  const selectedEdge = manualEdges.find((e) => e.id === selectedEdgeId);

  const pushToHistory = (newNodes, newEdges) => {
    const currentState = { nodes: newNodes, edges: newEdges };
    const newHistory = history.current.slice(0, historyIndex.current + 1);
    newHistory.push(currentState);
    history.current = newHistory;
    historyIndex.current++;
  };

  const undo = () => {
    if (historyIndex.current > 0) {
      historyIndex.current--;
      const { nodes, edges } = history.current[historyIndex.current];
      setNodes(nodes);
      setEdges(edges);
    }
  };

  const redo = () => {
    if (historyIndex.current < history.current.length - 1) {
      historyIndex.current++;
      const { nodes, edges } = history.current[historyIndex.current];
      setNodes(nodes);
      setEdges(edges);
    }
  };

  const getNodeStyle = (type) => {
    const baseStyle = {
      borderRadius: 10,
      padding: 10,
      boxShadow: "2px 2px 8px #ccc",
      whiteSpace: "pre-wrap",
      wordWrap: "break-word",
      fontFamily: "Segoe UI, sans-serif",
      minWidth: 180,
      maxWidth: 300
    };
    switch (type) {
      case "choice":
        return { ...baseStyle, background: "#E0F2FE", border: "1px solid #38bdf8" };
      case "input":
        return { ...baseStyle, background: "#DCFCE7", border: "1px solid #22c55e" };
      case "multi_choice":
        return { ...baseStyle, background: "#FEF9C3", border: "1px solid #eab308" };
      case "gpt":
        return { ...baseStyle, background: "#F3E8FF", border: "1px solid #8b5cf6" };
      case "end":
        return { ...baseStyle, background: "#FEE2E2", border: "1px solid #f87171" };
      default:
        return { ...baseStyle, background: "#F3F4F6", border: "1px solid #d1d5db" };
    }
  };

  const autoGeneratedEdges = useMemo(() => {
    const existingConnections = new Set(manualEdges.map((e) => `${e.source}->${e.target}`));
    const autoEdges = [];
    for (const node of nodes) {
      const target = node.data?.next;
      if (target && !existingConnections.has(`${node.id}->${target}`)) {
        autoEdges.push({
          id: `auto-${node.id}-${target}`,
          source: node.id,
          target,
          animated: false,
          type: "default",
          style: { strokeDasharray: "5,5", stroke: "#999" },
          markerEnd: { type: MarkerType.ArrowClosed },
          label: "next"
        });
      }
    }
    return autoEdges;
  }, [nodes, manualEdges]);

  const allEdges = [...manualEdges, ...autoGeneratedEdges];

  const getExportJSON = () => {
    const output = {
      settings: {
        system_prompt:
          "You are MindPeace, a compassionate mental health assistant. Based on the user's shared info and emotional state, continue the conversation empathetically.",
        gpt_model: "gpt-3.5-turbo"
      },
      nodes: {}
    };
    nodes.forEach((node) => {
      const { id, data } = node;
      const { message, type, options, capture, next } = data;
      const nodeObj = { message, type };
      if (options && Object.keys(options).length > 0) nodeObj.options = options;
      if (capture) nodeObj.capture = capture;
      if (next) nodeObj.next = next;
      output.nodes[id] = nodeObj;
    });
    return output;
  };

  const submitFlow = async () => {
    const payload = getExportJSON();
    try {
      const res = await fetch("http://localhost:5000/submit", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload)
      });
      if (res.ok) {
        const firstMsg = payload.nodes["1"]?.message || "Let's begin.";
        setSubmittedFlow(payload);
        setChatbotReady(true);
        setChatHistory([{ sender: "bot", message: firstMsg }]);
        setUserInputs({});
        setInputValue("");
        setCurrentNodeId("1");
        setGptMode(false);
        alert("Flow submitted successfully.");
      } else alert("Submission failed.");
    } catch (err) {
      alert("Backend error.");
    }
  };

  const handleUserResponse = async (response) => {
    if (!submittedFlow || (!currentNodeId && !gptMode)) return;
    const updatedHistory = [...chatHistory, { sender: "user", message: response }];

    if (gptMode) {
      try {
        const res = await fetch("http://localhost:5000/chat", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({
            message: response,
            summary: Object.entries(userInputs).map(([k, v]) => `${k}: ${v}`).join("\n"),
            system_prompt: submittedFlow.settings.system_prompt,
            gpt_model: submittedFlow.settings.gpt_model
          })
        });
        const data = await res.json();
        const gptReply = data.reply || "No response from GPT.";
        setChatHistory([...updatedHistory, { sender: "bot", message: gptReply }]);
        setInputValue("");
      } catch (err) {
        setChatHistory([...updatedHistory, { sender: "bot", message: "Error contacting GPT." }]);
        setInputValue("");
      }
      return;
    }

    const currentNode = submittedFlow.nodes[currentNodeId];
    const newInputs = { ...userInputs };
    if (currentNode.capture) newInputs[currentNode.capture] = response;
    let nextId = currentNode.next || currentNode.options?.[response];
    const nextNode = submittedFlow.nodes?.[nextId];

    if (!nextNode) {
      setChatHistory([...updatedHistory, { sender: "bot", message: "Conversation complete." }]);
      setCurrentNodeId(null);
      return;
    }

    if (nextNode.type === "gpt") {
      const summaryText = Object.entries(newInputs).map(([k, v]) => `${k}: ${v}`).join("\n");
      try {
        const res = await fetch("http://localhost:5000/chat", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({
            message: response,
            summary: summaryText,
            system_prompt: submittedFlow.settings.system_prompt,
            gpt_model: submittedFlow.settings.gpt_model
          })
        });
        const data = await res.json();
        const gptReply = data.reply || "No response from GPT.";
        setChatHistory([
          ...updatedHistory,
          { sender: "bot", message: nextNode.message },
          { sender: "bot", message: gptReply }
        ]);
        setCurrentNodeId(null);
        setGptMode(true);
        setUserInputs(newInputs);
        setInputValue("");
      } catch (err) {
        setChatHistory([
          ...updatedHistory,
          { sender: "bot", message: nextNode.message },
          { sender: "bot", message: "Error contacting GPT." }
        ]);
        setCurrentNodeId(null);
      }
    } else {
      setUserInputs(newInputs);
      setChatHistory([...updatedHistory, { sender: "bot", message: nextNode.message }]);
      setCurrentNodeId(nextId);
    }
  };

  const renderChatInput = () => (
    <div>
      <input
        value={inputValue}
        onChange={(e) => setInputValue(e.target.value)}
        onKeyDown={(e) => e.key === "Enter" && handleUserResponse(inputValue)}
        placeholder="Type your message..."
        style={{
          width: "100%",
          padding: 8,
          marginBottom: 8,
          borderRadius: 6,
          border: "1px solid #ccc"
        }}
      />
      <button
        onClick={() => handleUserResponse(inputValue)}
        style={{
          width: "100%",
          background: "#38bdf8",
          color: "white",
          borderRadius: 6,
          padding: "8px 12px",
          border: "none"
        }}
      >
        Send
      </button>
    </div>
  );

  // --- CONTINUED BELOW DUE TO LENGTH ---  
  return (
    <div style={{ display: "flex", height: "100vh", fontFamily: "Segoe UI, sans-serif" }}>
      {/* Left Side: Canvas and Node Editor */}
      <div style={{ flex: 1, position: "relative" }}>
        <h2 style={{ textAlign: "center", padding: "10px 0", background: "#f1f5f9" }}>
          Your AI Chatbot Designer â€” Fast. Visual. Code-Free
        </h2>
        <div style={{ position: "absolute", top: 10, right: 10, zIndex: 10, display: "flex", gap: 8 }}>
          <button onClick={undo} style={{ background: "#facc15", border: "none", padding: 8, borderRadius: 4 }}>
            Undo
          </button>
          <button onClick={redo} style={{ background: "#4ade80", border: "none", padding: 8, borderRadius: 4 }}>
            Redo
          </button>
        </div>

        <ReactFlow
          nodes={nodes.map((node) => ({
            ...node,
            style: getNodeStyle(node.data?.type)
          }))}
          edges={allEdges}
          onNodesChange={onNodesChange}
          onEdgesChange={onEdgesChange}
          fitView
        >
          <MiniMap />
          <Controls />
          <Background />
        </ReactFlow>

        {/* Buttons Panel */}
        <div style={{ position: "absolute", bottom: 10, left: 10, display: "flex", gap: 10 }}>
          <button
            onClick={() => {
              const id = String(nodeCounter++);
              const newNode = {
                id,
                data: {
                  message: "New node",
                  type: "choice",
                  options: {},
                  capture: "",
                  next: ""
                },
                position: { x: 250, y: 50 * nodeCounter },
                type: "default"
              };
              const newNodes = [...nodes, newNode];
              setNodes(newNodes);
              pushToHistory(newNodes, manualEdges);
            }}
            style={{ background: "#60a5fa", color: "white", padding: "8px 12px", border: "none", borderRadius: 6 }}
          >
            Add Node
          </button>

          <button
            onClick={() => {
              if (!selectedNodeId) return alert("Select a node to delete.");
              const newNodes = nodes.filter((n) => n.id !== selectedNodeId);
              const newEdges = manualEdges.filter((e) => e.source !== selectedNodeId && e.target !== selectedNodeId);
              setNodes(newNodes);
              setEdges(newEdges);
              setSelectedNodeId(null);
              pushToHistory(newNodes, newEdges);
            }}
            style={{ background: "#ef4444", color: "white", padding: "8px 12px", border: "none", borderRadius: 6 }}
          >
            Delete Node
          </button>

          <button
            onClick={() => {
              const exported = getExportJSON();
              const blob = new Blob([JSON.stringify(exported, null, 2)], { type: "application/json" });
              const url = URL.createObjectURL(blob);
              const a = document.createElement("a");
              a.href = url;
              a.download = "conversation_flow.json";
              a.click();
            }}
            style={{ background: "#10b981", color: "white", padding: "8px 12px", border: "none", borderRadius: 6 }}
          >
            Export JSON
          </button>

          <button
            onClick={submitFlow}
            style={{ background: "#6366f1", color: "white", padding: "8px 12px", border: "none", borderRadius: 6 }}
          >
            Submit Flow
          </button>
        </div>
      </div>

      {/* Right Side: Chatbot Panel */}
      <div
        style={{
          width: "350px",
          borderLeft: "1px solid #ddd",
          background: "#f8fafc",
          display: "flex",
          flexDirection: "column",
          justifyContent: "space-between"
        }}
      >
        <div style={{ padding: 12, fontWeight: "bold", background: "#1e3a8a", color: "white" }}>
          ðŸ’¬ MindPeace Assistant
        </div>

        <div style={{ padding: 10, flex: 1, overflowY: "auto" }}>
          {chatHistory.map((msg, idx) => (
            <div
              key={idx}
              style={{
                marginBottom: 8,
                alignSelf: msg.sender === "user" ? "flex-end" : "flex-start",
                backgroundColor: msg.sender === "user" ? "#d1fae5" : "#e0f2fe",
                padding: 10,
                borderRadius: 12,
                maxWidth: "80%"
              }}
            >
              {msg.message}
            </div>
          ))}
        </div>

        {chatbotReady && (
          <div style={{ padding: 10, borderTop: "1px solid #ccc" }}>{renderChatInput()}</div>
        )}
      </div>
    </div>
  );
}
