import React, { useCallback, useState, useMemo } from "react";
import ReactFlow, {
  MiniMap,
  Controls,
  Background,
  useNodesState,
  useEdgesState,
  addEdge,
  MarkerType
} from "reactflow";
import "reactflow/dist/style.css";

const initialNodes = [
  {
    id: "1",
    data: {
      message: "Hi there, I’m MindPeace — your friendly mental health support assistant. Would you like to begin our conversation?",
      type: "choice",
      options: {},
      capture: "",
      next: ""
    },
    position: { x: 250, y: 0 },
    type: "default"
  }
];

const initialEdges = [];
let nodeCounter = 2;

export default function App() {
  const [nodes, setNodes, onNodesChange] = useNodesState(initialNodes);
  const [manualEdges, setEdges, onEdgesChange] = useEdgesState(initialEdges);
  const [selectedNodeId, setSelectedNodeId] = useState(null);
  const [selectedEdgeId, setSelectedEdgeId] = useState(null);

  const selectedNode = nodes.find((n) => n.id === selectedNodeId);
  const selectedEdge = manualEdges.find((e) => e.id === selectedEdgeId);

  const autoGeneratedEdges = useMemo(() => {
    const existingConnections = new Set(manualEdges.map((e) => `${e.source}->${e.target}`));
    const autoEdges = [];

    for (const node of nodes) {
      const target = node.data?.next;
      if (target && !existingConnections.has(`${node.id}->${target}`)) {
        autoEdges.push({
          id: `auto-${node.id}-${target}`,
          source: node.id,
          target,
          animated: false,
          type: "default",
          style: { strokeDasharray: "5,5", stroke: "#999" },
          markerEnd: { type: MarkerType.ArrowClosed },
          label: "next"
        });
      }
    }
    return autoEdges;
  }, [nodes, manualEdges]);

  const allEdges = [...manualEdges, ...autoGeneratedEdges];

  const onConnect = useCallback(
    (params) => {
      const label = prompt("Enter option key for this connection (e.g., yes, no, Stress):", "");
      if (!label) return;

      const edgeId = `e${params.source}-${params.target}`;
      setEdges((eds) =>
        addEdge(
          {
            ...params,
            id: edgeId,
            markerEnd: { type: MarkerType.ArrowClosed },
            label
          },
          eds
        )
      );

      setNodes((nds) =>
        nds.map((node) => {
          if (node.id === params.source) {
            const updated = { ...node };
            if (node.data.type === "choice" || node.data.type === "multi_choice") {
              updated.data.options = {
                ...updated.data.options,
                [label]: params.target
              };
            } else {
              updated.data.next = params.target;
            }
            return updated;
          }
          return node;
        })
      );
    },
    [setEdges, setNodes]
  );

  const addNode = () => {
    const newId = nodeCounter.toString();
    nodeCounter++;
    const newNode = {
      id: newId,
      data: {
        message: `This is node ${newId}`,
        type: "choice",
        options: {},
        capture: "",
        next: ""
      },
      position: { x: 200 + Math.random() * 300, y: 100 + Math.random() * 300 },
      type: "default"
    };
    setNodes((nds) => [...nds, newNode]);
  };

  const onNodeClick = (_, node) => {
    setSelectedNodeId(node.id);
    setSelectedEdgeId(null);
  };

  const onEdgeClick = (_, edge) => {
    setSelectedEdgeId(edge.id);
    setSelectedNodeId(null);
  };

  const updateNodeField = (field, value) => {
    setNodes((nds) =>
      nds.map((node) => {
        if (node.id === selectedNodeId) {
          return {
            ...node,
            data: {
              ...node.data,
              [field]: value
            }
          };
        }
        return node;
      })
    );
  };

  const updateEdgeLabel = (newLabel) => {
    if (!selectedEdge) return;

    const oldLabel = selectedEdge.label;
    const sourceId = selectedEdge.source;
    const targetId = selectedEdge.target;

    setEdges((eds) =>
      eds.map((edge) =>
        edge.id === selectedEdge.id ? { ...edge, label: newLabel } : edge
      )
    );

    setNodes((nds) =>
      nds.map((node) => {
        if (node.id === sourceId) {
          const updated = { ...node };
          if (updated.data.type === "choice" || updated.data.type === "multi_choice") {
            const newOptions = { ...updated.data.options };
            delete newOptions[oldLabel];
            newOptions[newLabel] = targetId;
            updated.data.options = newOptions;
          } else {
            updated.data.next = targetId;
          }
          return updated;
        }
        return node;
      })
    );
  };

  const exportJSON = () => {
    const output = {
      settings: {
        system_prompt:
          "You are MindPeace, a compassionate mental health assistant. Based on the user's shared info and emotional state, continue the conversation empathetically. Always respond warmly and supportively.",
        gpt_model: "gpt-3.5-turbo"
      },
      nodes: {}
    };

    nodes.forEach((node) => {
      const { id, data } = node;
      const { message, type, options, capture, next } = data;
      const nodeObj = { message, type };
      if (options && Object.keys(options).length > 0) nodeObj.options = options;
      if (capture) nodeObj.capture = capture;
      if (next) nodeObj.next = next;
      output.nodes[id] = nodeObj;
    });

    const blob = new Blob([JSON.stringify(output, null, 2)], {
      type: "application/json"
    });
    const url = URL.createObjectURL(blob);
    const link = document.createElement("a");
    link.href = url;
    link.download = "conversation_data.json";
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  const getNodeStyle = (type) => {
    switch (type) {
      case "choice":
        return { background: "#e0f2fe", border: "1px solid #38bdf8" };
      case "input":
        return { background: "#fef9c3", border: "1px solid #eab308" };
      case "multi_choice":
        return { background: "#ede9fe", border: "1px solid #8b5cf6" };
      case "gpt":
        return { background: "#f5d0fe", border: "1px solid #ec4899" };
      case "end":
        return { background: "#e2e8f0", border: "1px solid #64748b" };
      default:
        return {};
    }
  };

  return (
    <div style={{ width: "100vw", height: "100vh" }}>
      <button onClick={addNode} style={{ position: "absolute", top: 10, left: 10, zIndex: 10, background: "#2563eb", color: "white", padding: "8px 12px", borderRadius: 6, border: "none", cursor: "pointer" }}>
        Add Node
      </button>
      <button onClick={exportJSON} style={{ position: "absolute", top: 10, left: 120, zIndex: 10, background: "#2563eb", color: "white", padding: "8px 12px", borderRadius: 6, border: "none", cursor: "pointer" }}>
        Export JSON
      </button>

      {selectedNode && (
        <div style={{ position: "absolute", top: 50, left: 10, zIndex: 10, background: "#f0f4f8", padding: 10, border: "1px solid #cbd5e1", borderRadius: "8px", width: "300px" }}>
          <h4>Editing Node: {selectedNode.id}</h4>
          <div>
            <label>Message:</label>
            <textarea value={selectedNode.data.message} onChange={(e) => updateNodeField("message", e.target.value)} style={{ width: "100%", height: 60 }} />
          </div>
          <div>
            <label>Type:</label>
            <select value={selectedNode.data.type} onChange={(e) => updateNodeField("type", e.target.value)} style={{ width: "100%" }}>
              <option value="choice">choice</option>
              <option value="input">input</option>
              <option value="multi_choice">multi_choice</option>
              <option value="gpt">gpt</option>
              <option value="end">end</option>
            </select>
          </div>
          <div>
            <label>Capture Field:</label>
            <input type="text" value={selectedNode.data.capture} onChange={(e) => updateNodeField("capture", e.target.value)} style={{ width: "100%" }} />
          </div>
          <div>
            <label>Next Node ID:</label>
            <input type="text" value={selectedNode.data.next} onChange={(e) => updateNodeField("next", e.target.value)} style={{ width: "100%" }} />
          </div>
        </div>
      )}

      {selectedEdge && (
        <div style={{ position: "absolute", top: 50, left: 10, zIndex: 10, background: "#f0f4f8", padding: 10, border: "1px solid #cbd5e1", borderRadius: "8px", width: "300px" }}>
          <h4>Editing Edge</h4>
          <div>
            <label>Option Label:</label>
            <input type="text" value={selectedEdge.label} onChange={(e) => updateEdgeLabel(e.target.value)} style={{ width: "100%" }} />
          </div>
        </div>
      )}

      <ReactFlow
        nodes={nodes.map((n) => ({
          ...n,
          style: getNodeStyle(n.data.type),
          data: { ...n.data, label: n.data.message }
        }))}
        edges={allEdges}
        onNodesChange={onNodesChange}
        onEdgesChange={onEdgesChange}
        onConnect={onConnect}
        onNodeClick={onNodeClick}
        onEdgeClick={onEdgeClick}
        fitView
        edgeLabelMode="always"
      >
        <MiniMap />
        <Controls />
        <Background />
      </ReactFlow>
    </div>
  );
}
