import React, { useCallback, useState, useMemo, useRef } from "react";
import ReactFlow, {
  MiniMap,
  Controls,
  Background,
  useNodesState,
  useEdgesState,
  addEdge,
  MarkerType
} from "reactflow";
import "reactflow/dist/style.css";

const initialNodes = [
  {
    id: "1",
    data: {
      message:
        "Hi there, I’m MindPeace — your friendly mental health support assistant. Would you like to begin our conversation?",
      type: "choice",
      options: {},
      capture: "",
      next: ""
    },
    position: { x: 250, y: 0 },
    type: "default"
  }
];

const initialEdges = [];
let nodeCounter = 2;

export default function App() {
  const [nodes, setNodes, onNodesChange] = useNodesState(initialNodes);
  const [manualEdges, setEdges, onEdgesChange] = useEdgesState(initialEdges);
  const [selectedNodeId, setSelectedNodeId] = useState(null);
  const [selectedEdgeId, setSelectedEdgeId] = useState(null);
  const [chatbotReady, setChatbotReady] = useState(false);
  const [chatMessages, setChatMessages] = useState([]);
  const [userInput, setUserInput] = useState("");
  const [chatError, setChatError] = useState("");

  const history = useRef([{ nodes: initialNodes, edges: initialEdges }]);
  const historyIndex = useRef(0);

  const selectedNode = nodes.find((n) => n.id === selectedNodeId);
  const selectedEdge = manualEdges.find((e) => e.id === selectedEdgeId);

  const pushToHistory = (newNodes, newEdges) => {
    const currentState = { nodes: newNodes, edges: newEdges };
    const newHistory = history.current.slice(0, historyIndex.current + 1);
    newHistory.push(currentState);
    history.current = newHistory;
    historyIndex.current++;
  };

  const undo = () => {
    if (historyIndex.current > 0) {
      historyIndex.current--;
      const { nodes, edges } = history.current[historyIndex.current];
      setNodes(nodes);
      setEdges(edges);
    }
  };

  const redo = () => {
    if (historyIndex.current < history.current.length - 1) {
      historyIndex.current++;
      const { nodes, edges } = history.current[historyIndex.current];
      setNodes(nodes);
      setEdges(edges);
    }
  };

  const getNodeStyle = (type) => {
    const baseStyle = {
      borderRadius: 10,
      padding: 10,
      boxShadow: "2px 2px 8px #ccc",
      whiteSpace: "pre-wrap",
      wordWrap: "break-word",
      fontFamily: "Segoe UI, sans-serif",
      minWidth: 180,
      maxWidth: 300
    };
    switch (type) {
      case "choice":
        return { ...baseStyle, background: "#E0F2FE", border: "1px solid #38bdf8" };
      case "input":
        return { ...baseStyle, background: "#DCFCE7", border: "1px solid #22c55e" };
      case "multi_choice":
        return { ...baseStyle, background: "#FEF9C3", border: "1px solid #eab308" };
      case "gpt":
        return { ...baseStyle, background: "#F3E8FF", border: "1px solid #8b5cf6" };
      case "end":
        return { ...baseStyle, background: "#FEE2E2", border: "1px solid #f87171" };
      default:
        return { ...baseStyle, background: "#F3F4F6", border: "1px solid #d1d5db" };
    }
  };

  const autoGeneratedEdges = useMemo(() => {
    const existingConnections = new Set(manualEdges.map((e) => `${e.source}->${e.target}`));
    const autoEdges = [];
    for (const node of nodes) {
      const target = node.data?.next;
      if (target && !existingConnections.has(`${node.id}->${target}`)) {
        autoEdges.push({
          id: `auto-${node.id}-${target}`,
          source: node.id,
          target,
          animated: false,
          type: "default",
          style: { strokeDasharray: "5,5", stroke: "#999" },
          markerEnd: { type: MarkerType.ArrowClosed },
          label: "next"
        });
      }
    }
    return autoEdges;
  }, [nodes, manualEdges]);

  const allEdges = [...manualEdges, ...autoGeneratedEdges];

  const onConnect = useCallback(
    (params) => {
      const label = prompt("Enter option key for this connection (e.g., yes, no, Stress):", "");
      if (!label) return;
      const edgeId = `e${params.source}-${params.target}`;
      const updatedEdges = addEdge(
        {
          ...params,
          id: edgeId,
          markerEnd: { type: MarkerType.ArrowClosed },
          label
        },
        manualEdges
      );

      const updatedNodes = nodes.map((node) => {
        if (node.id === params.source) {
          const updated = { ...node };
          if (node.data.type === "choice" || node.data.type === "multi_choice") {
            updated.data.options = { ...updated.data.options, [label]: params.target };
          } else {
            updated.data.next = params.target;
          }
          return updated;
        }
        return node;
      });

      setEdges(updatedEdges);
      setNodes(updatedNodes);
      pushToHistory(updatedNodes, updatedEdges);
    },
    [nodes, manualEdges, setEdges, setNodes]
  );

  const addNode = () => {
    const newId = nodeCounter.toString();
    nodeCounter++;
    const position = selectedNodeId
      ? {
          x: nodes.find((n) => n.id === selectedNodeId).position.x + 220,
          y: nodes.find((n) => n.id === selectedNodeId).position.y + 50
        }
      : { x: Math.random() * 400, y: Math.random() * 400 };
    const newNode = {
      id: newId,
      data: {
        message: `This is node ${newId}`,
        type: "choice",
        options: {},
        capture: "",
        next: ""
      },
      position,
      type: "default"
    };
    const updatedNodes = [...nodes, newNode];
    setNodes(updatedNodes);
    pushToHistory(updatedNodes, manualEdges);
  };

  const deleteNode = () => {
    if (!selectedNodeId) return;
    const updatedNodes = nodes.filter((n) => n.id !== selectedNodeId);
    const updatedEdges = manualEdges.filter(
      (e) => e.source !== selectedNodeId && e.target !== selectedNodeId
    );
    setNodes(updatedNodes);
    setEdges(updatedEdges);
    setSelectedNodeId(null);
    pushToHistory(updatedNodes, updatedEdges);
  };

  const exportJSON = () => {
    const output = getExportJSON();
    const blob = new Blob([JSON.stringify(output, null, 2)], {
      type: "application/json"
    });
    const url = URL.createObjectURL(blob);
    const link = document.createElement("a");
    link.href = url;
    link.download = "conversation_data.json";
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  const getExportJSON = () => {
    const output = {
      settings: {
        system_prompt:
          "You are MindPeace, a compassionate mental health assistant. Based on the user's shared info and emotional state, continue the conversation empathetically. Always respond warmly and supportively.",
        gpt_model: "gpt-3.5-turbo"
      },
      nodes: {}
    };

    nodes.forEach((node) => {
      const { id, data } = node;
      const { message, type, options, capture, next } = data;
      const nodeObj = { message, type };
      if (options && Object.keys(options).length > 0) nodeObj.options = options;
      if (capture) nodeObj.capture = capture;
      if (next) nodeObj.next = next;
      output.nodes[id] = nodeObj;
    });

    return output;
  };

  const submitFlow = async () => {
    const payload = getExportJSON();
    try {
      const res = await fetch("http://localhost:5000/submit", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload)
      });

      if (res.ok) {
        setChatbotReady(true);
        alert("Flow submitted successfully. Chatbot is now active.");
      } else {
        alert("Submission failed. Please check the backend logs.");
      }
    } catch (error) {
      alert("Error connecting to backend. Make sure the Flask server is running.");
    }
  };

  const updateNodeField = (field, value) => {
    const updatedNodes = nodes.map((node) =>
      node.id === selectedNodeId
        ? { ...node, data: { ...node.data, [field]: value } }
        : node
    );
    setNodes(updatedNodes);
    pushToHistory(updatedNodes, manualEdges);
  };

  const updateEdgeLabel = (newLabel) => {
    if (!selectedEdge) return;
    const oldLabel = selectedEdge.label;
    const sourceId = selectedEdge.source;
    const targetId = selectedEdge.target;
    const updatedEdges = manualEdges.map((edge) =>
      edge.id === selectedEdge.id ? { ...edge, label: newLabel } : edge
    );
    const updatedNodes = nodes.map((node) => {
      if (node.id === sourceId) {
        const updated = { ...node };
        if (updated.data.type === "choice" || updated.data.type === "multi_choice") {
          const newOptions = { ...updated.data.options };
          delete newOptions[oldLabel];
          newOptions[newLabel] = targetId;
          updated.data.options = newOptions;
        } else {
          updated.data.next = targetId;
        }
        return updated;
      }
      return node;
    });
    setEdges(updatedEdges);
    setNodes(updatedNodes);
    pushToHistory(updatedNodes, updatedEdges);
  };

  const onNodeClick = (_, node) => {
    setSelectedNodeId(node.id);
    setSelectedEdgeId(null);
  };

  const onEdgeClick = (_, edge) => {
    setSelectedEdgeId(edge.id);
    setSelectedNodeId(null);
  };

  const sendMessage = async () => {
    if (!userInput.trim()) return;
    const summary = JSON.stringify(getExportJSON().nodes);
    const system_prompt = getExportJSON().settings.system_prompt;
    const gpt_model = getExportJSON().settings.gpt_model;

    const newMessages = [...chatMessages, { from: "user", text: userInput }];
    setChatMessages(newMessages);
    setUserInput("");

    try {
      const res = await fetch("http://localhost:5000/chat", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          message: userInput,
          summary,
          system_prompt,
          gpt_model
        })
      });

      const data = await res.json();
      if (data.reply) {
        setChatMessages([...newMessages, { from: "gpt", text: data.reply }]);
      } else {
        setChatError(data.error || "Unknown error");
      }
    } catch (err) {
      setChatError("Failed to connect to backend.");
    }
  };

  return (
    <div style={{ width: "100vw", height: "100vh", display: "flex" }}>
      <div style={{ flex: 1, position: "relative" }}>
        {/* Your buttons, flow, node editor code remains unchanged */}
        {/* ... */}
        <ReactFlow
          nodes={nodes.map((n) => ({
            ...n,
            data: { ...n.data, label: n.data.message },
            style: getNodeStyle(n.data.type)
          }))}
          edges={allEdges}
          onNodesChange={onNodesChange}
          onEdgesChange={onEdgesChange}
          onConnect={onConnect}
          onNodeClick={onNodeClick}
          onEdgeClick={onEdgeClick}
          fitView
          edgeLabelMode="always"
        >
          <MiniMap />
          <Controls />
          <Background />
        </ReactFlow>
      </div>

      <div style={{ width: "300px", borderLeft: "1px solid #ddd", display: "flex", flexDirection: "column" }}>
        <div style={{ flexGrow: 1, overflowY: "auto", padding: "10px" }}>
          <h3>MindPeace Chat</h3>
          {chatMessages.map((msg, idx) => (
            <div key={idx} style={{ margin: "5px 0", textAlign: msg.from === "user" ? "right" : "left" }}>
              <div
                style={{
                  display: "inline-block",
                  padding: "8px 12px",
                  borderRadius: "18px",
                  backgroundColor: msg.from === "user" ? "#dcfce7" : "#f3e8ff",
                  maxWidth: "90%"
                }}
              >
                {msg.text}
              </div>
            </div>
          ))}
          {!chatbotReady && <p>Please submit your flow to activate the chatbot.</p>}
          {chatError && <p style={{ color: "red" }}>{chatError}</p>}
        </div>

        {chatbotReady && (
          <div style={{ padding: "10px", borderTop: "1px solid #eee" }}>
            <input
              style={{ width: "100%", padding: "8px", borderRadius: "5px", border: "1px solid #ccc" }}
              type="text"
              value={userInput}
              onChange={(e) => setUserInput(e.target.value)}
              placeholder="Type your message..."
              onKeyDown={(e) => e.key === "Enter" && sendMessage()}
            />
            <button
              onClick={sendMessage}
              style={{
                marginTop: "8px",
                width: "100%",
                padding: "8px",
                background: "#38bdf8",
                color: "white",
                border: "none",
                borderRadius: "6px"
              }}
            >
              Send
            </button>
          </div>
        )}
      </div>
    </div>
  );
}
