import React, { useCallback, useState } from 'react';
import ReactFlow, {
  Background,
  Controls,
  MiniMap,
  addEdge,
  useEdgesState,
  useNodesState,
  MarkerType,
  Panel
} from 'reactflow';
import 'reactflow/dist/style.css';
import { v4 as uuidv4 } from 'uuid';

const initialSystemPrompt = `You are MindPeace, a compassionate mental health assistant. Based on the user's shared info and emotional state, continue the conversation empathetically. Always respond warmly and supportively.`;

const defaultNodeData = {
  message: 'New message...',
  nodeType: 'choice',
  options: { yes: '', no: '' },
  capture: '',
  next: '',
};

export default function App() {
  const [nodes, setNodes, onNodesChange] = useNodesState([]);
  const [edges, setEdges, onEdgesChange] = useEdgesState([]);
  const [selectedNode, setSelectedNode] = useState(null);
  const [systemPrompt, setSystemPrompt] = useState(initialSystemPrompt);
  const [gptModel, setGptModel] = useState('gpt-3.5-turbo');

  const addNewNode = () => {
    const newNode = {
      id: uuidv4(),
      type: 'default',
      position: { x: Math.random() * 400, y: Math.random() * 400 },
      data: { ...defaultNodeData },
    };
    setNodes((nds) => [...nds, newNode]);
  };

  const onConnect = useCallback(
    (params) => {
      setEdges((eds) => addEdge({ ...params, markerEnd: { type: MarkerType.ArrowClosed } }, eds));
    },
    [setEdges]
  );

  const handleFieldChange = (field, value) => {
    setNodes((nds) =>
      nds.map((node) => {
        if (node.id === selectedNode.id) {
          return {
            ...node,
            data: {
              ...node.data,
              [field]: value,
            },
          };
        }
        return node;
      })
    );
  };

  const handleOptionChange = (key, value) => {
    setNodes((nds) =>
      nds.map((node) => {
        if (node.id === selectedNode.id) {
          return {
            ...node,
            data: {
              ...node.data,
              options: {
                ...node.data.options,
                [key]: value,
              },
            },
          };
        }
        return node;
      })
    );
  };

  const exportConversationData = () => {
    const result = {
      settings: {
        system_prompt: systemPrompt,
        gpt_model: gptModel,
      },
      nodes: {},
    };
    nodes.forEach((node) => {
      const { id, data } = node;
      result.nodes[id] = {
        message: data.message,
        type: data.nodeType,
      };
      if (data.options) result.nodes[id].options = data.options;
      if (data.capture) result.nodes[id].capture = data.capture;
      if (data.next) result.nodes[id].next = data.next;
    });
    const blob = new Blob([JSON.stringify(result, null, 2)], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'conversation_data.json';
    a.click();
    URL.revokeObjectURL(url);
  };

  return (
    <div style={{ width: '100vw', height: '100vh' }}>
      <ReactFlow
        nodes={nodes}
        edges={edges}
        onNodesChange={onNodesChange}
        onEdgesChange={onEdgesChange}
        onConnect={onConnect}
        onNodeClick={(_, node) => setSelectedNode(node)}
        fitView
      >
        <Background />
        <MiniMap />
        <Controls />
        <Panel position="top-left">
          <button onClick={addNewNode}>Add Node</button>
          <button onClick={exportConversationData}>Export JSON</button>
        </Panel>
      </ReactFlow>

      {selectedNode && (
        <div style={{ position: 'absolute', right: 10, top: 10, width: 300, background: '#fff', padding: 10, border: '1px solid #ccc' }}>
          <h4>Edit Node {selectedNode.id}</h4>
          <label>Message:</label>
          <textarea
            value={selectedNode.data.message}
            onChange={(e) => handleFieldChange('message', e.target.value)}
            rows={4}
            style={{ width: '100%' }}
          />

          <label>Type:</label>
          <select
            value={selectedNode.data.nodeType}
            onChange={(e) => handleFieldChange('nodeType', e.target.value)}
            style={{ width: '100%', marginBottom: 10 }}
          >
            <option value="choice">Choice</option>
            <option value="multi_choice">Multi Choice</option>
            <option value="input">Input</option>
            <option value="gpt">GPT</option>
            <option value="end">End</option>
          </select>

          <label>Capture (if any):</label>
          <input
            type="text"
            value={selectedNode.data.capture || ''}
            onChange={(e) => handleFieldChange('capture', e.target.value)}
            style={{ width: '100%' }}
          />

          <label>Next (for input nodes):</label>
          <input
            type="text"
            value={selectedNode.data.next || ''}
            onChange={(e) => handleFieldChange('next', e.target.value)}
            style={{ width: '100%' }}
          />

          {(selectedNode.data.nodeType === 'choice' || selectedNode.data.nodeType === 'multi_choice') && (
            <div>
              <h5>Options</h5>
              {Object.entries(selectedNode.data.options || {}).map(([key, val], idx) => (
                <div key={idx}>
                  <label>{key} â†’ </label>
                  <input
                    type="text"
                    value={val}
                    onChange={(e) => handleOptionChange(key, e.target.value)}
                  />
                </div>
              ))}
            </div>
          )}
        </div>
      )}
    </div>
  );
}
