import React, { useCallback, useState, useMemo } from "react";
import ReactFlow, {
  MiniMap,
  Controls,
  Background,
  useNodesState,
  useEdgesState,
  addEdge,
  MarkerType
} from "reactflow";
import "reactflow/dist/style.css";

const initialNodes = [
  {
    id: "1",
    data: {
      message:
        "Hi there, I’m MindPeace — your friendly mental health support assistant. Would you like to begin our conversation?",
      type: "choice",
      options: {},
      capture: "",
      next: ""
    },
    position: { x: 250, y: 0 },
    type: "default"
  }
];

const initialEdges = [];
let nodeCounter = 2;

export default function App() {
  const [nodes, setNodes, onNodesChange] = useNodesState(initialNodes);
  const [edges, setEdges, onEdgesChange] = useEdgesState(initialEdges);
  const [selectedNodeId, setSelectedNodeId] = useState(null);
  const [selectedEdgeId, setSelectedEdgeId] = useState(null);

  const selectedNode = nodes.find((n) => n.id === selectedNodeId);
  const selectedEdge = edges.find((e) => e.id === selectedEdgeId);

  const autoEdges = useMemo(() => {
    const connected = new Set(edges.map((e) => `${e.source}->${e.target}`));
    const generated = [];

    for (const node of nodes) {
      const target = node.data?.next;
      if (target && !connected.has(`${node.id}->${target}`)) {
        generated.push({
          id: `auto-${node.id}-${target}`,
          source: node.id,
          target,
          type: "default",
          style: { strokeDasharray: "5,5", stroke: "#999" },
          markerEnd: { type: MarkerType.ArrowClosed },
          label: "next"
        });
      }
    }
    return generated;
  }, [nodes, edges]);

  const allEdges = [...edges, ...autoEdges];

  const onConnect = useCallback(
    (params) => {
      const label = prompt("Enter option key for this connection (e.g., yes, no, stress):", "");
      if (!label) return;

      const edgeId = `e${params.source}-${params.target}`;
      setEdges((eds) =>
        addEdge(
          {
            ...params,
            id: edgeId,
            label,
            markerEnd: { type: MarkerType.ArrowClosed }
          },
          eds
        )
      );

      setNodes((nds) =>
        nds.map((node) => {
          if (node.id === params.source) {
            const updated = { ...node };
            if (
              node.data.type === "choice" ||
              node.data.type === "multi_choice"
            ) {
              updated.data.options = {
                ...updated.data.options,
                [label]: params.target
              };
            } else {
              updated.data.next = params.target;
            }
            return updated;
          }
          return node;
        })
      );
    },
    [setEdges, setNodes]
  );

  const addNode = () => {
    const newId = nodeCounter.toString();
    nodeCounter++;

    let newPosition = { x: 100, y: 100 };

    if (selectedNodeId) {
      const selected = nodes.find((n) => n.id === selectedNodeId);
      if (selected) {
        newPosition = {
          x: selected.position.x + 50,
          y: selected.position.y + 120
        };
      }
    } else if (nodes.length > 0) {
      const lowest = nodes.reduce((a, b) =>
        a.position.y > b.position.y ? a : b
      );
      newPosition = {
        x: lowest.position.x + 50,
        y: lowest.position.y + 120
      };
    }

    const newNode = {
      id: newId,
      data: {
        message: `This is node ${newId}`,
        type: "choice",
        options: {},
        capture: "",
        next: ""
      },
      position: newPosition,
      type: "default"
    };
    setNodes((nds) => [...nds, newNode]);
  };

  const onNodeClick = (_, node) => {
    setSelectedNodeId(node.id);
    setSelectedEdgeId(null);
  };

  const onEdgeClick = (_, edge) => {
    setSelectedEdgeId(edge.id);
    setSelectedNodeId(null);
  };

  const updateNodeField = (field, value) => {
    setNodes((nds) =>
      nds.map((node) =>
        node.id === selectedNodeId
          ? {
              ...node,
              data: {
                ...node.data,
                [field]: value
              }
            }
          : node
      )
    );
  };

  const updateEdgeLabel = (newLabel) => {
    if (!selectedEdge) return;

    const oldLabel = selectedEdge.label;
    const sourceId = selectedEdge.source;
    const targetId = selectedEdge.target;

    setEdges((eds) =>
      eds.map((e) =>
        e.id === selectedEdge.id ? { ...e, label: newLabel } : e
      )
    );

    setNodes((nds) =>
      nds.map((node) => {
        if (node.id === sourceId) {
          const updated = { ...node };
          if (
            updated.data.type === "choice" ||
            updated.data.type === "multi_choice"
          ) {
            const newOptions = { ...updated.data.options };
            delete newOptions[oldLabel];
            newOptions[newLabel] = targetId;
            updated.data.options = newOptions;
          }
          return updated;
        }
        return node;
      })
    );
  };

  const exportJSON = () => {
    const output = {
      settings: {
        system_prompt:
          "You are MindPeace, a compassionate mental health assistant. Based on the user's shared info and emotional state, continue the conversation empathetically. Always respond warmly and supportively.",
        gpt_model: "gpt-3.5-turbo"
      },
      nodes: {}
    };

    nodes.forEach((node) => {
      const { id, data } = node;
      const nodeObj = {
        message: data.message,
        type: data.type
      };
      if (data.options && Object.keys(data.options).length > 0)
        nodeObj.options = data.options;
      if (data.capture) nodeObj.capture = data.capture;
      if (data.next) nodeObj.next = data.next;
      output.nodes[id] = nodeObj;
    });

    const blob = new Blob([JSON.stringify(output, null, 2)], {
      type: "application/json"
    });
    const url = URL.createObjectURL(blob);
    const link = document.createElement("a");
    link.href = url;
    link.download = "conversation_data.json";
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  return (
    <div className="w-screen h-screen font-sans bg-gray-50">
      <button
        onClick={addNode}
        className="absolute top-4 left-4 z-10 bg-blue-600 text-white px-4 py-2 rounded shadow hover:bg-blue-700"
      >
        ➕ Add Node
      </button>
      <button
        onClick={exportJSON}
        className="absolute top-4 left-36 z-10 bg-green-600 text-white px-4 py-2 rounded shadow hover:bg-green-700"
      >
        ⬇️ Export JSON
      </button>

      {selectedNode && (
        <div className="absolute top-20 left-4 z-10 bg-white p-4 w-80 rounded-xl shadow border border-gray-300 space-y-3">
          <h3 className="text-lg font-semibold text-gray-800">
            🛠️ Editing Node {selectedNode.id}
          </h3>

          <label className="text-sm text-gray-600">Message</label>
          <textarea
            value={selectedNode.data.message}
            onChange={(e) => updateNodeField("message", e.target.value)}
            className="w-full p-2 border rounded"
            rows={3}
          />

          <label className="text-sm text-gray-600">Type</label>
          <select
            value={selectedNode.data.type}
            onChange={(e) => updateNodeField("type", e.target.value)}
            className="w-full p-2 border rounded"
          >
            <option value="choice">choice</option>
            <option value="input">input</option>
            <option value="multi_choice">multi_choice</option>
            <option value="gpt">gpt</option>
            <option value="end">end</option>
          </select>

          <label className="text-sm text-gray-600">Capture Field</label>
          <input
            type="text"
            value={selectedNode.data.capture}
            onChange={(e) => updateNodeField("capture", e.target.value)}
            className="w-full p-2 border rounded"
          />

          <label className="text-sm text-gray-600">Next Node ID</label>
          <input
            type="text"
            value={selectedNode.data.next}
            onChange={(e) => updateNodeField("next", e.target.value)}
            className="w-full p-2 border rounded"
          />
        </div>
      )}

      {selectedEdge && (
        <div className="absolute top-20 left-4 z-10 bg-white p-4 w-80 rounded-xl shadow border border-gray-300 space-y-3">
          <h3 className="text-lg font-semibold text-gray-800">
            ✏️ Editing Edge
          </h3>
          <label className="text-sm text-gray-600">Option Label</label>
          <input
            type="text"
            value={selectedEdge.label}
            onChange={(e) => updateEdgeLabel(e.target.value)}
            className="w-full p-2 border rounded"
          />
        </div>
      )}

      <ReactFlow
        nodes={nodes.map((n) => ({
          ...n,
          data: { ...n.data, label: n.data.message }
        }))}
        edges={allEdges}
        onNodesChange={onNodesChange}
        onEdgesChange={onEdgesChange}
        onConnect={onConnect}
        onNodeClick={onNodeClick}
        onEdgeClick={onEdgeClick}
        fitView
        edgeLabelMode="always"
      >
        <MiniMap />
        <Controls />
        <Background />
      </ReactFlow>
    </div>
  );
}
