import React, { useCallback, useEffect, useRef, useState } from "react";
import ReactFlow, {
  Background,
  Controls,
  MiniMap,
  addEdge,
  applyEdgeChanges,
  applyNodeChanges,
  useEdgesState,
  useNodesState,
} from "reactflow";
import "reactflow/dist/style.css";
import axios from "axios";
import PromptSettingsPanel from "./PromptSettingsPanel";

const nodeTypes = {
  default: ({ data }) => (
    <div
      style={{
        padding: 10,
        borderRadius: 8,
        boxShadow: "0 2px 6px rgba(0,0,0,0.2)",
        background: data.type === "gpt"
          ? "#fef9c3"
          : data.type === "choice"
          ? "#cffafe"
          : data.type === "multi_choice"
          ? "#ede9fe"
          : "#f1f5f9",
        minWidth: 180,
        maxWidth: 250,
        wordWrap: "break-word",
      }}
    >
      <div style={{ fontSize: 12, fontWeight: "bold", marginBottom: 4 }}>{data.label}</div>
      <div style={{ fontSize: 14 }}>{data.message}</div>
    </div>
  ),
};

let id = 0;
const getId = () => `node_${id++}`;

function App() {
  const [nodes, setNodes, onNodesChange] = useNodesState([]);
  const [edges, setEdges, onEdgesChange] = useEdgesState([]);
  const [selectedNodeId, setSelectedNodeId] = useState(null);
  const [selectedEdgeId, setSelectedEdgeId] = useState(null);
  const [history, setHistory] = useState([]);
  const [redoStack, setRedoStack] = useState([]);
  const [settings, setSettings] = useState({
    system_prompt: "You are a helpful assistant.",
    gpt_model: "gpt-3.5-turbo",
  });

  const [chatHistory, setChatHistory] = useState([]);
  const [activeChatbot, setActiveChatbot] = useState(false);
  const [inputValue, setInputValue] = useState("");
  const [currentNodeId, setCurrentNodeId] = useState(null);
  const [multiSelections, setMultiSelections] = useState([]);
  const chatEndRef = useRef(null);

  const scrollToBottom = () => {
    if (chatEndRef.current) {
      chatEndRef.current.scrollIntoView({ behavior: "smooth" });
    }
  };

  useEffect(() => {
    scrollToBottom();
  }, [chatHistory]);
  const pushToHistory = () => {
    setHistory((prev) => [...prev, { nodes, edges }]);
    setRedoStack([]);
  };

  const addNode = () => {
    const newNode = {
      id: getId(),
      data: { label: "New Node", message: "Edit me", type: "default" },
      position: { x: Math.random() * 250, y: Math.random() * 250 },
      type: "default",
    };
    pushToHistory();
    setNodes((nds) => [...nds, newNode]);
  };

  const deleteNode = () => {
    if (selectedNodeId) {
      pushToHistory();
      setNodes((nds) => nds.filter((n) => n.id !== selectedNodeId));
      setEdges((eds) => eds.filter((e) => e.source !== selectedNodeId && e.target !== selectedNodeId));
      setSelectedNodeId(null);
    }
  };

  const exportToJson = () => {
    const exportData = {
      settings,
      nodes: nodes.map((n) => ({ id: n.id, data: n.data, position: n.position })),
      edges,
    };
    const blob = new Blob([JSON.stringify(exportData, null, 2)], { type: "application/json" });
    const a = document.createElement("a");
    a.href = URL.createObjectURL(blob);
    a.download = "chatbot_flow.json";
    a.click();
  };

  const importFromJson = (e) => {
    const fileReader = new FileReader();
    fileReader.onload = (event) => {
      try {
        const json = JSON.parse(event.target.result);
        setNodes(json.nodes || []);
        setEdges(json.edges || []);
        setSettings(json.settings || settings);
      } catch (err) {
        alert("Invalid JSON");
      }
    };
    fileReader.readAsText(e.target.files[0]);
  };

  const undo = () => {
    if (history.length > 0) {
      const prev = history[history.length - 1];
      setRedoStack((r) => [...r, { nodes, edges }]);
      setNodes(prev.nodes);
      setEdges(prev.edges);
      setHistory((h) => h.slice(0, -1));
    }
  };

  const redo = () => {
    if (redoStack.length > 0) {
      const next = redoStack[redoStack.length - 1];
      setHistory((h) => [...h, { nodes, edges }]);
      setNodes(next.nodes);
      setEdges(next.edges);
      setRedoStack((r) => r.slice(0, -1));
    }
  };

  const handleSendMessage = async () => {
    if (!inputValue.trim()) return;
    const newMessage = { role: "user", content: inputValue };
    setChatHistory((prev) => [...prev, newMessage]);
    setInputValue("");

    try {
      const response = await axios.post("http://localhost:5000/chat", {
        message: inputValue,
        history: chatHistory,
      });
      const reply = { role: "assistant", content: response.data.reply };
      setChatHistory((prev) => [...prev, reply]);
    } catch (error) {
      console.error("Error sending message:", error);
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const handleExportChat = () => {
    const textContent = chatHistory
      .map((msg) => `${msg.role === "user" ? "You" : "Bot"}: ${msg.content}`)
      .join("\n\n");

    const blob = new Blob([textContent], { type: "text/plain" });
    const a = document.createElement("a");
    a.href = URL.createObjectURL(blob);
    a.download = "chat_history.txt";
    a.click();
  };

  const submitFlow = async () => {
    try {
      await axios.post("http://localhost:5000/submit_flow", {
        settings,
        nodes,
        edges,
      });
      setChatHistory([]);
      setActiveChatbot(true);
      setCurrentNodeId("start");
    } catch (error) {
      console.error("Error submitting flow:", error);
    }
  };
  return (
    <div style={{ display: "flex", height: "100vh", fontFamily: "Arial, sans-serif" }}>
      {/* React Flow + Controls */}
      <div style={{ flex: 1, position: "relative" }}>
        <ReactFlow
          nodes={nodes}
          edges={edges}
          nodeTypes={nodeTypes}
          onNodesChange={onNodesChange}
          onEdgesChange={onEdgesChange}
          onNodeClick={(e, node) => setSelectedNodeId(node.id)}
          onEdgeClick={(e, edge) => setSelectedEdgeId(edge.id)}
          fitView
        >
          <MiniMap />
          <Controls />
          <Background />
        </ReactFlow>

        {/* Buttons (Top-Left) */}
        <div style={{ position: "absolute", top: 20, left: 20, display: "flex", flexDirection: "column", gap: 8 }}>
          <button onClick={addNode} style={{ background: "#2563eb", color: "white", padding: "6px 12px", border: "none", borderRadius: 4 }}>
            Add Node
          </button>
          <button onClick={deleteNode} style={{ background: "#dc2626", color: "white", padding: "6px 12px", border: "none", borderRadius: 4 }}>
            Delete Node
          </button>
          <button onClick={exportToJson} style={{ background: "#16a34a", color: "white", padding: "6px 12px", border: "none", borderRadius: 4 }}>
            Export JSON
          </button>
          <button onClick={undo} style={{ background: "#a16207", color: "white", padding: "6px 12px", border: "none", borderRadius: 4 }}>
            Undo
          </button>
          <button onClick={redo} style={{ background: "#0e7490", color: "white", padding: "6px 12px", border: "none", borderRadius: 4 }}>
            Redo
          </button>
          <label style={{ background: "#7c3aed", color: "white", padding: "6px 12px", borderRadius: 4, cursor: "pointer" }}>
            Import JSON
            <input type="file" accept=".json" onChange={importFromJson} style={{ display: "none" }} />
          </label>
          <button onClick={submitFlow} style={{ background: "#0284c7", color: "white", padding: "6px 12px", border: "none", borderRadius: 4 }}>
            Submit Flow
          </button>
        </div>
      </div>

      {/* Right Panel */}
      <div style={{ width: "320px", padding: 20, borderLeft: "1px solid #ddd", display: "flex", flexDirection: "column", position: "relative" }}>
        {/* Export Chat - Top Right */}
        <button
          onClick={handleExportChat}
          style={{
            position: "absolute",
            top: 10,
            right: 10,
            background: "orange",
            border: "none",
            padding: "5px 10px",
            borderRadius: 4,
            fontWeight: "bold",
            fontSize: "12px",
          }}
        >
          Export Chat
        </button>

        {/* Prompt Settings */}
        <PromptSettingsPanel settings={settings} setSettings={setSettings} />

        {/* Chatbox UI */}
        <div style={{ flex: 1, overflowY: "auto", marginTop: 10, marginBottom: 10, background: "#f8fafc", padding: 10, borderRadius: 8 }}>
          {chatHistory.map((msg, idx) => (
            <div
              key={idx}
              style={{
                textAlign: msg.role === "user" ? "right" : "left",
                marginBottom: 8,
              }}
            >
              <span
                style={{
                  display: "inline-block",
                  background: msg.role === "user" ? "#e0f2fe" : "#e2e8f0",
                  padding: "6px 12px",
                  borderRadius: 12,
                  maxWidth: "80%",
                  wordWrap: "break-word",
                }}
              >
                {msg.content}
              </span>
            </div>
          ))}
          <div ref={chatEndRef} />
        </div>

        {/* Input Box */}
        <div style={{ display: "flex", gap: 4 }}>
          <input
            type="text"
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
            onKeyDown={handleKeyDown}
            placeholder="Type your message..."
            style={{
              flex: 1,
              padding: "8px 12px",
              borderRadius: 20,
              border: "1px solid #ccc",
              fontSize: 14,
            }}
          />
          <button
            onClick={handleSendMessage}
            style={{
              background: "#6366f1",
              color: "white",
              border: "none",
              borderRadius: 20,
              padding: "8px 16px",
              fontWeight: "bold",
            }}
          >
            Send
          </button>
        </div>
      </div>
    </div>
  );
}

export default App;
