import React, { useState, useMemo, useRef } from "react";
import ReactFlow, {
  MiniMap,
  Controls,
  Background,
  useNodesState,
  useEdgesState,
  addEdge,
  MarkerType
} from "reactflow";
import "reactflow/dist/style.css";

const initialNodes = [
  {
    id: "1",
    data: {
      message:
        "Hi there, I’m MindPeace — your friendly mental health support assistant. Would you like to begin our conversation?",
      type: "choice",
      options: {},
      capture: "",
      next: ""
    },
    position: { x: 250, y: 0 },
    type: "default"
  }
];

const initialEdges = [];
let nodeCounter = 2;

export default function App() {
  const [nodes, setNodes, onNodesChange] = useNodesState(initialNodes);
  const [manualEdges, setEdges, onEdgesChange] = useEdgesState(initialEdges);
  const [selectedNodeId, setSelectedNodeId] = useState(null);
  const [selectedEdgeId, setSelectedEdgeId] = useState(null);
  const [chatbotReady, setChatbotReady] = useState(false);
  const [submittedFlow, setSubmittedFlow] = useState(null);
  const [currentNodeId, setCurrentNodeId] = useState(null);
  const [gptMode, setGptMode] = useState(false);
  const [chatHistory, setChatHistory] = useState([]);
  const [userInputs, setUserInputs] = useState({});
  const [inputValue, setInputValue] = useState("");
  const [multiChoiceSelection, setMultiChoiceSelection] = useState([]);

  // NEW: Prompt settings (editable via Prompt Editor)
  const [promptSettings, setPromptSettings] = useState({
    system_prompt:
      "You are MindPeace, a compassionate mental health assistant. Based on the user's shared info and emotional state, continue the conversation empathetically.",
    gpt_model: "gpt-3.5-turbo"
  });

  const history = useRef([{ nodes: initialNodes, edges: initialEdges }]);
  const historyIndex = useRef(0);

  const selectedNode = nodes.find((n) => n.id === selectedNodeId);
  const selectedEdge = manualEdges.find((e) => e.id === selectedEdgeId);

  const pushToHistory = (newNodes, newEdges) => {
    const currentState = { nodes: newNodes, edges: newEdges };
    const newHistory = history.current.slice(0, historyIndex.current + 1);
    newHistory.push(currentState);
    history.current = newHistory;
    historyIndex.current++;
  };

  const undo = () => {
    if (historyIndex.current > 0) {
      historyIndex.current--;
      const { nodes, edges } = history.current[historyIndex.current];
      setNodes(nodes);
      setEdges(edges);
    }
  };

  const redo = () => {
    if (historyIndex.current < history.current.length - 1) {
      historyIndex.current++;
      const { nodes, edges } = history.current[historyIndex.current];
      setNodes(nodes);
      setEdges(edges);
    }
  };

  const getNodeStyle = (type) => {
    const baseStyle = {
      borderRadius: 10,
      padding: 10,
      boxShadow: "2px 2px 8px #ccc",
      whiteSpace: "pre-wrap",
      wordWrap: "break-word",
      fontFamily: "Segoe UI, sans-serif",
      minWidth: 180,
      maxWidth: 300
    };
    switch (type) {
      case "choice":
        return { ...baseStyle, background: "#0284c7", border: "1px solid #0369a1", color: "white" };
      case "input":
        return { ...baseStyle, background: "#DCFCE7", border: "1px solid #22c55e" };
      case "multi_choice":
        return { ...baseStyle, background: "#facc15", border: "1px solid #eab308" };
      case "gpt":
        return { ...baseStyle, background: "#F3E8FF", border: "1px solid #8b5cf6" };
      case "end":
        return { ...baseStyle, background: "#FEE2E2", border: "1px solid #f87171" };
      default:
        return { ...baseStyle, background: "#F3F4F6", border: "1px solid #d1d5db" };
    }
  };

  const autoGeneratedEdges = useMemo(() => {
    const existingConnections = new Set(manualEdges.map((e) => `${e.source}->${e.target}`));
    const autoEdges = [];
    for (const node of nodes) {
      const target = node.data?.next;
      if (target && !existingConnections.has(`${node.id}->${target}`)) {
        autoEdges.push({
          id: `auto-${node.id}-${target}`,
          source: node.id,
          target,
          animated: false,
          type: "default",
          style: { strokeDasharray: "5,5", stroke: "#999" },
          markerEnd: { type: MarkerType.ArrowClosed },
          label: "next"
        });
      }
    }
    return autoEdges;
  }, [nodes, manualEdges]);

  const allEdges = [...manualEdges, ...autoGeneratedEdges];

  const getExportJSON = () => {
    const output = {
      settings: {
        system_prompt: promptSettings.system_prompt,
        gpt_model: promptSettings.gpt_model
      },
      nodes: {}
    };
    nodes.forEach((node) => {
      const { id, data } = node;
      const { message, type, options, capture, next } = data;
      const nodeObj = { message, type };
      if (options && Object.keys(options).length > 0) nodeObj.options = options;
      if (capture) nodeObj.capture = capture;
      if (next) nodeObj.next = next;
      output.nodes[id] = nodeObj;
    });
    return output;
  };
  const submitFlow = async () => {
    const payload = getExportJSON();
    try {
      const res = await fetch("http://localhost:5000/submit", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload)
      });
      if (res.ok) {
        const firstMsg = payload.nodes["1"]?.message || "Let's begin.";
        setSubmittedFlow(payload);
        setChatbotReady(true);
        setChatHistory([{ sender: "bot", message: firstMsg }]);
        setUserInputs({});
        setInputValue("");
        setCurrentNodeId("1");
        setGptMode(false);
        alert("Flow submitted successfully.");
      } else alert("Submission failed.");
    } catch (err) {
      alert("Backend error.");
    }
  };

  const handleUserResponse = async (response) => {
    if (!submittedFlow) return;

    const updatedHistory = [...chatHistory, { sender: "user", message: response }];
    setChatHistory(updatedHistory);

    const formattedHistory = updatedHistory.map((entry) => ({
      role: entry.sender === "user" ? "user" : "assistant",
      content: entry.message
    }));

    if (gptMode) {
      try {
        const res = await fetch("http://localhost:5000/chat", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({
            chat_history: formattedHistory,
            system_prompt: submittedFlow.settings.system_prompt,
            gpt_model: submittedFlow.settings.gpt_model,
            user_inputs: userInputs
          })
        });
        const data = await res.json();
        const gptReply = data.reply || "No response from GPT.";
        setChatHistory((prev) => [...prev, { sender: "bot", message: gptReply }]);
      } catch {
        setChatHistory((prev) => [...prev, { sender: "bot", message: "Error contacting GPT." }]);
      }
      return;
    }

    const currentNode = submittedFlow.nodes[currentNodeId];
    const newInputs = { ...userInputs };
    if (currentNode.capture) newInputs[currentNode.capture] = response;

    let nextId = currentNode.next || currentNode.options?.[response];
    const nextNode = submittedFlow.nodes?.[nextId];

    if (!nextNode) {
      setUserInputs(newInputs);
      setChatHistory([...updatedHistory, { sender: "bot", message: "Conversation complete." }]);
      setCurrentNodeId(null);
      return;
    }

    if (nextNode.type === "gpt") {
      const message = nextNode.message || "Let's continue.";
      try {
        const res = await fetch("http://localhost:5000/chat", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({
            chat_history: [...formattedHistory, { role: "user", content: response }],
            system_prompt: submittedFlow.settings.system_prompt,
            gpt_model: submittedFlow.settings.gpt_model,
            user_inputs: newInputs
          })
        });
        const data = await res.json();
        const gptReply = data.reply || "No response from GPT.";
        setChatHistory([
          ...updatedHistory,
          { sender: "bot", message },
          { sender: "bot", message: gptReply }
        ]);
        setCurrentNodeId(null);
        setGptMode(true);
        setUserInputs(newInputs);
      } catch {
        setChatHistory([
          ...updatedHistory,
          { sender: "bot", message },
          { sender: "bot", message: "Error contacting GPT." }
        ]);
        setCurrentNodeId(null);
        setGptMode(true);
        setUserInputs(newInputs);
      }
    } else {
      setUserInputs(newInputs);
      setChatHistory([...updatedHistory, { sender: "bot", message: nextNode.message }]);
      setCurrentNodeId(nextId);
    }
  };

  const renderChatInput = () => {
    if (!submittedFlow || (!currentNodeId && !gptMode)) return null;

    // GPT Mode
    if (gptMode) {
      return (
        <div style={{ display: "flex", gap: "6px" }}>
          <input
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
            onKeyDown={(e) => {
              if (e.key === "Enter") {
                handleUserResponse(inputValue);
                setInputValue("");
              }
            }}
            style={{
              flex: 1,
              padding: "8px",
              borderRadius: 6,
              border: "1px solid #ccc",
              height: "40px"
            }}
          />
          <button
            onClick={() => {
              handleUserResponse(inputValue);
              setInputValue("");
            }}
            style={{
              background: "#38bdf8",
              color: "white",
              borderRadius: 6,
              padding: "0 16px",
              border: "none",
              height: "40px",
              whiteSpace: "nowrap"
            }}
          >
            Send
          </button>
        </div>
      );
    }

    const node = submittedFlow.nodes[currentNodeId];

    if (node.type === "choice") {
      const opts = Object.keys(node.options || {});
      return (
        <div style={{ display: "flex", flexDirection: "column", gap: 8 }}>
          {opts.map((opt) => (
            <button
              key={opt}
              onClick={() => handleUserResponse(opt)}
              style={{
                padding: "8px",
                width: "100%",
                borderRadius: 6,
                border: "1px solid #0369a1",
                background: "#0284c7",
                color: "white"
              }}
            >
              {opt}
            </button>
          ))}
        </div>
      );
    }

    if (node.type === "multi_choice") {
      return (
        <div>
          {Object.keys(node.options || {}).map((opt) => (
            <div key={opt} style={{ background: "#facc15", padding: "4px 8px", borderRadius: "4px", margin: "4px 0" }}>
              <label>
                <input
                  type="checkbox"
                  checked={multiChoiceSelection.includes(opt)}
                  onChange={() =>
                    setMultiChoiceSelection((prev) =>
                      prev.includes(opt)
                        ? prev.filter((o) => o !== opt)
                        : [...prev, opt]
                    )
                  }
                />{" "}
                {opt}
              </label>
            </div>
          ))}
          <button
            onClick={() => {
              if (multiChoiceSelection.length > 0) {
                handleUserResponse(multiChoiceSelection.join(", "));
                setMultiChoiceSelection([]);
                setInputValue("");
              }
            }}
            style={{
              marginTop: 10,
              background: "#22c55e",
              color: "white",
              padding: 8,
              borderRadius: 6,
              border: "none",
              width: "100%"
            }}
          >
            Submit Selection
          </button>
        </div>
      );
    }

    if (node.type === "input") {
      return (
        <div style={{ display: "flex", gap: "6px" }}>
          <input
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
            onKeyDown={(e) => {
              if (e.key === "Enter") {
                handleUserResponse(inputValue);
                setInputValue("");
              }
            }}
            style={{
              flex: 1,
              padding: "8px",
              borderRadius: 6,
              border: "1px solid #ccc",
              height: "40px"
            }}
          />
          <button
            onClick={() => {
              handleUserResponse(inputValue);
              setInputValue("");
            }}
            style={{
              background: "#38bdf8",
              color: "white",
              borderRadius: 6,
              padding: "0 16px",
              border: "none",
              height: "40px",
              whiteSpace: "nowrap"
            }}
          >
            Submit
          </button>
        </div>
      );
    }

    return null;
  };

  // Clean up node/edge mappings when edges are removed
  const handleEdgesChange = (changes) => {
    const removedIds = new Set(
      changes.filter((c) => c.type === "remove" && c.id).map((c) => c.id)
    );
    const removedEdges = manualEdges.filter((e) => removedIds.has(e.id));

    onEdgesChange(changes);

    if (!removedEdges.length) return;

    const updatedNodes = nodes.map((n) => {
      let data = { ...n.data };
      let mutated = false;

      for (const e of removedEdges) {
        if (e.source !== n.id) continue;

        if ((data.type === "choice" || data.type === "multi_choice") && data.options) {
          if (e.label && data.options[e.label] === e.target) {
            const opts = { ...data.options };
            delete opts[e.label];
            data.options = opts;
            mutated = true;
          } else {
            const opts = { ...data.options };
            let changed = false;
            for (const [k, v] of Object.entries(opts)) {
              if (v === e.target) {
                delete opts[k];
                changed = true;
              }
            }
            if (changed) {
              data.options = opts;
              mutated = true;
            }
          }
        } else if (data.next === e.target) {
          data.next = "";
          mutated = true;
        }
      }

      return mutated ? { ...n, data } : n;
    });

    setNodes(updatedNodes);
    const remainingEdges = manualEdges.filter((e) => !removedIds.has(e.id));
    pushToHistory(updatedNodes, remainingEdges);
  };
  return (
    <div style={{ width: "100vw", height: "100vh", display: "flex" }}>
      {/* Left Panel */}
      <div style={{ flex: 1, position: "relative" }}>
        <div style={{
          textAlign: "center",
          padding: "12px",
          background: "#f1f5f9",
          boxShadow: "0px 2px 6px rgba(0,0,0,0.1)",
          borderRadius: "8px",
          fontSize: "18px",
          fontWeight: "bold",
          fontFamily: "Segoe UI, sans-serif",
          width: "fit-content",
          margin: "10px auto",
          zIndex: 20,
          position: "relative"
        }}>
          Your AI Chatbot Designer — Fast. Visual. Code-Free
        </div>

        {/* Top Left Buttons */}
        <div style={{ position: "absolute", top: 60, left: 10, zIndex: 10, display: "flex", gap: "10px" }}>
          <button style={{ background: "#38bdf8", color: "white", borderRadius: 6, padding: "6px 12px", border: "none" }}
            onClick={() => {
              const newId = nodeCounter.toString();
              nodeCounter++;
              const position = selectedNodeId
                ? {
                    x: nodes.find((n) => n.id === selectedNodeId).position.x + 220,
                    y: nodes.find((n) => n.id === selectedNodeId).position.y + 50
                  }
                : { x: Math.random() * 400, y: Math.random() * 400 };
              const newNode = {
                id: newId,
                data: {
                  message: `This is node ${newId}`,
                  type: "choice",
                  options: {},
                  capture: "",
                  next: ""
                },
                position,
                type: "default"
              };
              const updatedNodes = [...nodes, newNode];
              setNodes(updatedNodes);
              pushToHistory(updatedNodes, manualEdges);
            }}>Add Node</button>

          <button style={{ background: "#22c55e", color: "white", borderRadius: 6, padding: "6px 12px", border: "none" }}
            onClick={() => {
              const blob = new Blob([JSON.stringify(getExportJSON(), null, 2)], { type: "application/json" });
              const url = URL.createObjectURL(blob);
              const link = document.createElement("a");
              link.href = url;
              link.download = "conversation_data.json";
              document.body.appendChild(link);
              link.click();
              document.body.removeChild(link);
            }}>Export JSON</button>

          <button style={{ background: "#ef4444", color: "white", borderRadius: 6, padding: "6px 12px", border: "none" }}
            onClick={() => {
              if (!selectedNodeId) return;

              const deletedId = selectedNodeId;

              // Remove the node and its edges
              const updatedNodesBase = nodes.filter((n) => n.id !== deletedId);
              const updatedEdges = manualEdges.filter(
                (e) => e.source !== deletedId && e.target !== deletedId
              );

              // Scrub references to the deleted node from remaining nodes
              const updatedNodes = updatedNodesBase.map((n) => {
                const data = { ...n.data };
                let mutated = false;

                if (data.next === deletedId) {
                  data.next = "";
                  mutated = true;
                }

                if (data.options && Object.keys(data.options).length) {
                  const opts = { ...data.options };
                  let changed = false;
                  for (const [k, v] of Object.entries(opts)) {
                    if (v === deletedId) {
                      delete opts[k];
                      changed = true;
                    }
                  }
                  if (changed) {
                    data.options = opts;
                    mutated = true;
                  }
                }

                return mutated ? { ...n, data } : n;
              });

              setNodes(updatedNodes);
              setEdges(updatedEdges);
              setSelectedNodeId(null);
              pushToHistory(updatedNodes, updatedEdges);
            }}>Delete Node</button>

          <button style={{ background: "#10b981", color: "white", borderRadius: 6, padding: "6px 12px", border: "none" }}
            onClick={submitFlow}>Submit Flow</button>
        </div>

        {/* Top Right Buttons */}
        <div style={{ position: "absolute", top: 60, right: 10, zIndex: 10, display: "flex", gap: "10px" }}>
          <button style={{ background: "#a855f7", color: "white", borderRadius: 6, padding: "6px 12px", border: "none" }} onClick={undo}>Undo</button>
          <button style={{ background: "#f59e0b", color: "white", borderRadius: 6, padding: "6px 12px", border: "none" }} onClick={redo}>Redo</button>
        </div>

        {/* Node Editor */}
        {selectedNode && (
          <div style={{
            position: "absolute",
            top: 110,
            left: 10,
            zIndex: 10,
            background: "#f8fafc",
            padding: 12,
            border: "1px solid #d1d5db",
            borderRadius: "10px",
            width: 300,
            boxShadow: "0 2px 6px rgba(0, 0, 0, 0.1)"
          }}>
            <h4>Editing Node: {selectedNode.id}</h4>
            <label>Message:</label>
            <textarea value={selectedNode.data.message}
              onChange={(e) => {
                const updatedNodes = nodes.map((node) =>
                  node.id === selectedNodeId
                    ? { ...node, data: { ...node.data, message: e.target.value } }
                    : node
                );
                setNodes(updatedNodes);
                pushToHistory(updatedNodes, manualEdges);
              }} style={{ width: "100%", height: 60 }} />
            <label>Type:</label>
            <select value={selectedNode.data.type}
              onChange={(e) => {
                const newType = e.target.value;
                const updatedNodes = nodes.map((node) => {
                  if (node.id !== selectedNodeId) return node;
                  const nd = { ...node, data: { ...node.data, type: newType } };
                  if (newType !== "choice" && newType !== "multi_choice") {
                    nd.data.options = {};
                  }
                  return nd;
                });
                setNodes(updatedNodes);
                pushToHistory(updatedNodes, manualEdges);
              }} style={{ width: "100%" }}>
              <option value="choice">choice</option>
              <option value="input">input</option>
              <option value="multi_choice">multi_choice</option>
              <option value="gpt">gpt</option>
              <option value="end">end</option>
            </select>
            <label>Capture Field:</label>
            <input type="text" value={selectedNode.data.capture}
              onChange={(e) => {
                const updatedNodes = nodes.map((node) =>
                  node.id === selectedNodeId
                    ? { ...node, data: { ...node.data, capture: e.target.value } }
                    : node
                );
                setNodes(updatedNodes);
                pushToHistory(updatedNodes, manualEdges);
              }} style={{ width: "100%" }} />
            <label>Next Node ID:</label>
            <input type="text" value={selectedNode.data.next}
              onChange={(e) => {
                const updatedNodes = nodes.map((node) =>
                  node.id === selectedNodeId
                    ? { ...node, data: { ...node.data, next: e.target.value } }
                    : node
                );
                setNodes(updatedNodes);
                pushToHistory(updatedNodes, manualEdges);
              }} style={{ width: "100%" }} />
          </div>
        )}

        {/* NEW: Prompt Editor (same size, sits below Node Editor, different color) */}
        <div style={{
          position: "absolute",
          top: selectedNode ? 110 + 330 : 110, // roughly below the node editor
          left: 10,
          zIndex: 9,
          background: "#fff7ed",               // warm parchment color
          padding: 12,
          border: "1px solid #f59e0b",
          borderRadius: "10px",
          width: 300,
          boxShadow: "0 2px 6px rgba(0, 0, 0, 0.08)"
        }}>
          <h4>Prompt Editor</h4>
          <label>System Prompt:</label>
          <textarea
            value={promptSettings.system_prompt}
            onChange={(e) =>
              setPromptSettings((p) => ({ ...p, system_prompt: e.target.value }))
            }
            style={{ width: "100%", height: 120 }}
          />
          <label style={{ marginTop: 8, display: "block" }}>Model:</label>
          <select
            value={promptSettings.gpt_model}
            onChange={(e) =>
              setPromptSettings((p) => ({ ...p, gpt_model: e.target.value }))
            }
            style={{ width: "100%" }}
          >
            <option value="gpt-3.5-turbo">gpt-3.5-turbo</option>
            <option value="gpt-4o-mini">gpt-4o-mini</option>
            <option value="gpt-4o">gpt-4o</option>
          </select>
          <div style={{ marginTop: 10, display: "flex", gap: 8 }}>
            <button
              onClick={() => {
                // no-op: state already updated live; keep button for UX clarity
                alert("Prompt settings are now active. Submit Flow to apply.");
              }}
              style={{ background: "#10b981", color: "white", border: "none", borderRadius: 6, padding: "6px 12px" }}
            >
              Save
            </button>
            <button
              onClick={() =>
                setPromptSettings({
                  system_prompt:
                    "You are MindPeace, a compassionate mental health assistant. Based on the user's shared info and emotional state, continue the conversation empathetically.",
                  gpt_model: "gpt-3.5-turbo"
                })
              }
              style={{ background: "#e11d48", color: "white", border: "none", borderRadius: 6, padding: "6px 12px" }}
            >
              Reset
            </button>
          </div>
          <div style={{ fontSize: 12, marginTop: 6, color: "#7c2d12" }}>
            Changes will be included in Export JSON and used after “Submit Flow”.
          </div>
        </div>

        {/* Edge Editor */}
        {selectedEdge && (
          <div style={{
            position: "absolute",
            top: 110,
            left: 10,
            zIndex: 10,
            background: "#f9fafb",
            padding: 12,
            border: "1px solid #ccc",
            borderRadius: "8px",
            width: "300px",
            boxShadow: "0 2px 6px rgba(0, 0, 0, 0.1)"
          }}>
            <h4>Editing Edge</h4>
            <label>Option Label:</label>
            <input type="text" value={selectedEdge.label}
              onChange={(e) => {
                const newLabel = e.target.value;
                const updatedEdges = manualEdges.map((edge) =>
                  edge.id === selectedEdge.id ? { ...edge, label: newLabel } : edge
                );
                const sourceId = selectedEdge.source;
                const targetId = selectedEdge.target;
                const updatedNodes = nodes.map((node) => {
                  if (node.id === sourceId) {
                    const updated = { ...node };
                    if (updated.data.type === "choice" || updated.data.type === "multi_choice") {
                      const newOptions = { ...updated.data.options };
                      delete newOptions[selectedEdge.label];
                      newOptions[newLabel] = targetId;
                      updated.data.options = newOptions;
                    } else {
                      updated.data.next = targetId;
                    }
                    return updated;
                  }
                  return node;
                });
                setEdges(updatedEdges);
                setNodes(updatedNodes);
                pushToHistory(updatedNodes, updatedEdges);
              }} style={{ width: "100%" }} />
          </div>
        )}

        {/* React Flow Canvas */}
        <ReactFlow
          nodes={nodes.map((n) => ({
            ...n,
            data: { ...n.data, label: n.data.message },
            style: getNodeStyle(n.data.type)
          }))}
          edges={allEdges}
          onNodesChange={onNodesChange}
          onEdgesChange={handleEdgesChange}
          onConnect={(params) => {
            const label = prompt("Enter option key for this connection:", "");
            if (!label) return;
            const edgeId = `e${params.source}-${params.target}`;
            const updatedEdges = addEdge({ ...params, id: edgeId, markerEnd: { type: MarkerType.ArrowClosed }, label }, manualEdges);
            const updatedNodes = nodes.map((node) => {
              if (node.id === params.source) {
                const updated = { ...node };
                if (updated.data.type === "choice" || updated.data.type === "multi_choice") {
                  updated.data.options = { ...updated.data.options, [label]: params.target };
                } else {
                  updated.data.next = params.target;
                }
                return updated;
              }
              return node;
            });
            setEdges(updatedEdges);
            setNodes(updatedNodes);
            pushToHistory(updatedNodes, updatedEdges);
          }}
          onNodeClick={(_, node) => {
            setSelectedNodeId(node.id);
            setSelectedEdgeId(null);
          }}
          onEdgeClick={(_, edge) => {
            setSelectedEdgeId(edge.id);
            setSelectedNodeId(null);
          }}
          fitView
          edgeLabelMode="always"
        >
          <MiniMap />
          <Controls />
          <Background />
        </ReactFlow>
      </div>

      {/* Right Chat Panel */}
      <div style={{
        width: "320px",
        padding: 20,
        borderLeft: "1px solid #ddd",
        display: "flex",
        flexDirection: "column",
        position: "relative",
        height: "100vh",
        boxSizing: "border-box"
      }}>
        {/* Header */}
        <div style={{
          fontWeight: "bold",
          marginBottom: 10,
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
          flexShrink: 0
        }}>
          <span>MindPeace Chat</span>
          {chatbotReady && (
            <button
              onClick={() => {
                const textContent = chatHistory
                  .map((entry) => `${entry.sender === "user" ? "You" : "MindPeace"}: ${entry.message}`)
                  .join("\n\n");
                const blob = new Blob([textContent], { type: "text/plain" });
                const url = URL.createObjectURL(blob);
                const link = document.createElement("a");
                link.href = url;
                link.download = "chat_history.txt";
                document.body.appendChild(link);
                link.click();
                document.body.removeChild(link);
              }}
              style={{
                background: "#6366f1",
                color: "white",
                border: "none",
                borderRadius: "6px",
                padding: "4px 8px",
                fontSize: "12px",
                cursor: "pointer"
              }}
            >
              Export
            </button>
          )}
        </div>

        {/* Scrollable Chat Messages */}
        <div style={{
          flex: 1,
          overflowY: "auto",
          marginBottom: 10,
          paddingRight: 4
        }}>
          {chatbotReady ? (
            <div style={{ display: "flex", flexDirection: "column", gap: 10 }}>
              {chatHistory.map((entry, index) => (
                <div key={index} style={{
                  alignSelf: entry.sender === "user" ? "flex-end" : "flex-start",
                  background: entry.sender === "user" ? "#e0f2fe" : "#f3f4f6",
                  padding: 8,
                  borderRadius: 6,
                  maxWidth: "90%"
                }}>
                  {entry.message}
                </div>
              ))}
            </div>
          ) : (
            <div>Please submit your flow to activate the chatbot.</div>
          )}
        </div>

        {/* Fixed Input Box (scrollable for multiple buttons) */}
        {chatbotReady && (
          <div style={{ flexShrink: 0, maxHeight: 220, overflowY: "auto" }}>
            {renderChatInput()}
          </div>
        )}
      </div>
    </div>
  );
}
