import React, { useCallback, useState } from "react";
import ReactFlow, {
  MiniMap,
  Controls,
  Background,
  useNodesState,
  useEdgesState,
  addEdge,
  MarkerType
} from "reactflow";
import "reactflow/dist/style.css";

const initialNodes = [
  {
    id: "1",
    data: {
      message:
        "Hi there, I’m MindPeace — your friendly mental health support assistant. Would you like to begin our conversation?",
      type: "choice",
      options: {},
      capture: "",
      next: ""
    },
    position: { x: 250, y: 0 },
    type: "default"
  }
];

const initialEdges = [];
let nodeCounter = 2;

export default function App() {
  const [nodes, setNodes, onNodesChange] = useNodesState(initialNodes);
  const [edges, setEdges, onEdgesChange] = useEdgesState(initialEdges);
  const [selectedNodeId, setSelectedNodeId] = useState(null);

  const selectedNode = nodes.find((n) => n.id === selectedNodeId);

  const onConnect = useCallback(
    (params) => {
      const label = prompt(
        "Enter option key for this connection (e.g., yes, no, Stress):",
        ""
      );
      if (!label) return;

      setEdges((eds) =>
        addEdge(
          {
            ...params,
            markerEnd: { type: MarkerType.ArrowClosed },
            label
          },
          eds
        )
      );

      // Update options or next
      setNodes((nds) =>
        nds.map((node) => {
          if (node.id === params.source) {
            const updated = { ...node };
            if (
              node.data.type === "choice" ||
              node.data.type === "multi_choice"
            ) {
              updated.data.options = {
                ...updated.data.options,
                [label]: params.target
              };
            } else {
              updated.data.next = params.target;
            }
            return updated;
          }
          return node;
        })
      );
    },
    [setEdges, setNodes]
  );

  const addNode = () => {
    const newId = nodeCounter.toString();
    nodeCounter++;
    const newNode = {
      id: newId,
      data: {
        message: `This is node ${newId}`,
        type: "choice",
        options: {},
        capture: "",
        next: ""
      },
      position: { x: Math.random() * 400, y: Math.random() * 400 },
      type: "default"
    };
    setNodes((nds) => [...nds, newNode]);
  };

  const onNodeClick = (_, node) => {
    setSelectedNodeId(node.id);
  };

  const updateNodeField = (field, value) => {
    setNodes((nds) =>
      nds.map((node) => {
        if (node.id === selectedNodeId) {
          return {
            ...node,
            data: {
              ...node.data,
              [field]: value
            }
          };
        }
        return node;
      })
    );
  };

  const exportJSON = () => {
    const output = {
      settings: {
        system_prompt:
          "You are MindPeace, a compassionate mental health assistant. Based on the user's shared info and emotional state, continue the conversation empathetically. Always respond warmly and supportively.",
        gpt_model: "gpt-3.5-turbo"
      },
      nodes: {}
    };

    const visualEdges = [...edges];

    // Add dummy edges for `next` fields that are not visually connected
    nodes.forEach((node) => {
      const { id, data } = node;
      if (data.next) {
        const alreadyConnected = visualEdges.some(
          (e) => e.source === id && e.target === data.next
        );
        if (!alreadyConnected) {
          visualEdges.push({
            id: `dummy-${id}-${data.next}`,
            source: id,
            target: data.next,
            label: "next",
            style: { stroke: "#aaa", strokeDasharray: "5,5" },
            markerEnd: { type: MarkerType.ArrowClosed }
          });
        }
      }
    });

    // Convert nodes to JSON
    nodes.forEach((node) => {
      const { id, data } = node;
      const { message, type, options, capture, next } = data;
      const nodeObj = { message, type };
      if (options && Object.keys(options).length > 0) nodeObj.options = options;
      if (capture) nodeObj.capture = capture;
      if (next) nodeObj.next = next;
      output.nodes[id] = nodeObj;
    });

    const blob = new Blob([JSON.stringify(output, null, 2)], {
      type: "application/json"
    });
    const url = URL.createObjectURL(blob);
    const link = document.createElement("a");
    link.href = url;
    link.download = "conversation_data.json";
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  return (
    <div style={{ width: "100vw", height: "100vh" }}>
      <button onClick={addNode} style={{ position: "absolute", top: 10, left: 10, zIndex: 10 }}>
        Add Node
      </button>
      <button onClick={exportJSON} style={{ position: "absolute", top: 10, left: 100, zIndex: 10 }}>
        Export JSON
      </button>

      {selectedNode && (
        <div
          style={{
            position: "absolute",
            top: 50,
            left: 10,
            zIndex: 10,
            background: "white",
            padding: 10,
            border: "1px solid #ccc",
            borderRadius: "8px",
            width: "300px"
          }}
        >
          <h4>Editing Node ID: {selectedNode.id}</h4>
          <div>
            <label>Message:</label>
            <textarea
              value={selectedNode.data.message}
              onChange={(e) => updateNodeField("message", e.target.value)}
              style={{ width: "100%", height: 60 }}
            />
          </div>
          <div>
            <label>Type:</label>
            <select
              value={selectedNode.data.type}
              onChange={(e) => updateNodeField("type", e.target.value)}
              style={{ width: "100%" }}
            >
              <option value="choice">choice</option>
              <option value="multi_choice">multi_choice</option>
              <option value="input">input</option>
              <option value="gpt">gpt</option>
              <option value="end">end</option>
            </select>
          </div>
          <div>
            <label>Capture Field:</label>
            <input
              type="text"
              value={selectedNode.data.capture}
              onChange={(e) => updateNodeField("capture", e.target.value)}
              style={{ width: "100%" }}
            />
          </div>
          <div>
            <label>Next Node ID:</label>
            <input
              type="text"
              value={selectedNode.data.next}
              onChange={(e) => updateNodeField("next", e.target.value)}
              style={{ width: "100%" }}
            />
          </div>
        </div>
      )}

      <ReactFlow
        nodes={nodes.map((n) => ({
          ...n,
          data: { ...n.data, label: `${n.id}. ${n.data.message}` }
        }))}
        edges={edges}
        onNodesChange={onNodesChange}
        onEdgesChange={onEdgesChange}
        onConnect={onConnect}
        onNodeClick={onNodeClick}
        fitView
        edgeLabelMode="always"
      >
        <MiniMap />
        <Controls />
        <Background />
      </ReactFlow>
    </div>
  );
}
