import React, { useCallback, useEffect, useRef, useState } from "react";
import ReactFlow, {
  Background,
  Controls,
  MiniMap,
  addEdge,
  applyEdgeChanges,
  applyNodeChanges,
  useEdgesState,
  useNodesState,
} from "reactflow";
import "reactflow/dist/style.css";
import axios from "axios";
import PromptSettingsPanel from "./PromptSettingsPanel";

const nodeTypes = {
  default: ({ data }) => (
    <div
      style={{
        padding: 10,
        borderRadius: 8,
        boxShadow: "0 2px 6px rgba(0,0,0,0.2)",
        background: data.type === "gpt" ? "#fef9c3" :
                    data.type === "choice" ? "#cffafe" :
                    data.type === "multi_choice" ? "#ede9fe" :
                    "#f1f5f9",
        minWidth: 180,
        maxWidth: 250,
        wordWrap: "break-word"
      }}
    >
      <div style={{ fontSize: 12, fontWeight: "bold", marginBottom: 4 }}>{data.label}</div>
      <div style={{ fontSize: 14 }}>{data.message}</div>
    </div>
  ),
};

let id = 0;
const getId = () => `node_${id++}`;

function App() {
  const [nodes, setNodes, onNodesChange] = useNodesState([]);
  const [edges, setEdges, onEdgesChange] = useEdgesState([]);
  const [selectedNodeId, setSelectedNodeId] = useState(null);
  const [selectedEdgeId, setSelectedEdgeId] = useState(null);
  const [settings, setSettings] = useState({
    system_prompt: "You are a helpful assistant.",
    gpt_model: "gpt-3.5-turbo",
  });
  const [chatHistory, setChatHistory] = useState([]);
  const [activeChatbot, setActiveChatbot] = useState(false);
  const [inputValue, setInputValue] = useState("");
  const [currentNodeId, setCurrentNodeId] = useState(null);
  const chatEndRef = useRef(null);
  const scrollToBottom = () => {
    if (chatEndRef.current) {
      chatEndRef.current.scrollIntoView({ behavior: "smooth" });
    }
  };

  const handleSendMessage = async () => {
    if (!inputValue.trim()) return;

    const newMessage = { role: "user", content: inputValue };
    setChatHistory((prev) => [...prev, newMessage]);
    setInputValue("");

    try {
      const response = await axios.post("http://localhost:5000/chat", {
        message: inputValue,
        history: chatHistory,
      });
      const reply = { role: "assistant", content: response.data.reply };
      setChatHistory((prev) => [...prev, reply]);
    } catch (error) {
      console.error("Error sending message:", error);
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const handleExportChat = () => {
    const textContent = chatHistory
      .map((msg) => `${msg.role === "user" ? "You" : "Bot"}: ${msg.content}`)
      .join("\n\n");

    const blob = new Blob([textContent], { type: "text/plain" });
    const a = document.createElement("a");
    a.href = URL.createObjectURL(blob);
    a.download = "chat_history.txt";
    a.click();
  };

  const submitFlow = async () => {
    try {
      await axios.post("http://localhost:5000/submit_flow", {
        settings,
        nodes,
        edges,
      });
      setChatHistory([]);
      setActiveChatbot(true);
      setCurrentNodeId("start");
    } catch (error) {
      console.error("Error submitting flow:", error);
    }
  };

  useEffect(() => {
    scrollToBottom();
  }, [chatHistory]);
  return (
    <div style={{ display: "flex", height: "100vh", overflow: "hidden" }}>
      <div style={{ flex: 1, position: "relative" }}>
        <ReactFlow
          nodes={nodes}
          edges={edges}
          onNodesChange={onNodesChange}
          onEdgesChange={onEdgesChange}
          nodeTypes={nodeTypes}
          fitView
        >
          <Background />
          <Controls />
          <MiniMap />
        </ReactFlow>

        <div style={{ position: "absolute", top: 10, left: 10, zIndex: 10 }}>
          <button onClick={submitFlow} style={{ backgroundColor: "#0284c7", color: "#fff", padding: "8px 12px", border: "none", borderRadius: 4, marginRight: 10 }}>
            Submit Flow
          </button>
        </div>
      </div>

      {/* Right Panel */}
      <div
        style={{
          width: "320px",
          padding: 20,
          borderLeft: "1px solid #ddd",
          display: "flex",
          flexDirection: "column",
          position: "relative",
        }}
      >
        {/* Prompt Settings Panel */}
        <PromptSettingsPanel settings={settings} setSettings={setSettings} />

        {/* Chatbot Panel */}
        <div
          style={{
            flex: 1,
            overflowY: "auto",
            border: "1px solid #ddd",
            padding: 10,
            borderRadius: 6,
            marginBottom: 10,
            background: "#f9fafb",
          }}
        >
          {chatHistory.map((msg, idx) => (
            <div
              key={idx}
              style={{
                marginBottom: 8,
                alignSelf: msg.role === "user" ? "flex-end" : "flex-start",
                backgroundColor: msg.role === "user" ? "#d1fae5" : "#e0e7ff",
                padding: "8px 12px",
                borderRadius: 12,
                maxWidth: "85%",
                fontSize: "14px",
              }}
            >
              {msg.content}
            </div>
          ))}
          <div ref={chatEndRef} />
        </div>

        <div style={{ display: "flex", alignItems: "center", gap: 6 }}>
          <input
            type="text"
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
            onKeyDown={handleKeyDown}
            placeholder="Type your message..."
            style={{
              flex: 1,
              padding: 8,
              fontSize: 14,
              borderRadius: 6,
              border: "1px solid #ccc",
            }}
          />
          <button
            onClick={handleSendMessage}
            style={{
              padding: "8px 12px",
              fontSize: 14,
              backgroundColor: "#4f46e5",
              color: "#fff",
              border: "none",
              borderRadius: 6,
              height: "100%",
            }}
          >
            Send
          </button>
        </div>

        <button
          onClick={handleExportChat}
          style={{
            position: "absolute",
            top: 10,
            right: 10,
            fontSize: "12px",
            padding: "4px 8px",
            backgroundColor: "#f97316",
            color: "#fff",
            border: "none",
            borderRadius: 4,
          }}
        >
          Export Chat
        </button>
      </div>
    </div>
  );
}

export default App;
