import React from "react";

const PromptSettingsPanel = ({ settings, setSettings }) => {
  const handlePromptChange = (e) => {
    setSettings((prev) => ({
      ...prev,
      system_prompt: e.target.value,
    }));
  };

  const handleModelChange = (e) => {
    setSettings((prev) => ({
      ...prev,
      gpt_model: e.target.value,
    }));
  };

  return (
    <div style={{ marginBottom: 20 }}>
      <h3 style={{ fontSize: "16px", marginBottom: 10 }}>ðŸ§  Prompt Settings</h3>

      <label style={{ fontWeight: "bold", display: "block", marginBottom: 4 }}>
        System Prompt:
      </label>
      <textarea
        value={settings.system_prompt}
        onChange={handlePromptChange}
        rows={6}
        style={{
          width: "100%",
          fontSize: "14px",
          padding: "8px",
          border: "1px solid #ccc",
          borderRadius: "6px",
          resize: "vertical",
          marginBottom: 10,
        }}
        placeholder="e.g., You are a compassionate mental wellness assistant..."
      />

      <label style={{ fontWeight: "bold", display: "block", marginBottom: 4 }}>
        GPT Model:
      </label>
      <select
        value={settings.gpt_model}
        onChange={handleModelChange}
        style={{
          width: "100%",
          fontSize: "14px",
          padding: "6px",
          border: "1px solid #ccc",
          borderRadius: "6px",
        }}
      >
        <option value="gpt-3.5-turbo">gpt-3.5-turbo</option>
        <option value="gpt-4">gpt-4</option>
      </select>
    </div>
  );
};

export default PromptSettingsPanel;
