import React, { useState, useMemo, useRef } from "react";
import ReactFlow, {
  MiniMap,
  Controls,
  Background,
  useNodesState,
  useEdgesState,
  addEdge,
  MarkerType
} from "reactflow";
import "reactflow/dist/style.css";

const initialNodes = [
  {
    id: "1",
    data: {
      message:
        "Hi there, Iâ€™m MindPeace â€” your friendly mental health support assistant. Would you like to begin our conversation?",
      type: "choice",
      options: {},
      capture: "",
      next: ""
    },
    position: { x: 250, y: 0 },
    type: "default"
  }
];

const initialEdges = [];
let nodeCounter = 2;

export default function App() {
  const [nodes, setNodes, onNodesChange] = useNodesState(initialNodes);
  const [manualEdges, setEdges, onEdgesChange] = useEdgesState(initialEdges);
  const [selectedNodeId, setSelectedNodeId] = useState(null);
  const [selectedEdgeId, setSelectedEdgeId] = useState(null);
  const [chatbotReady, setChatbotReady] = useState(false);
  const [submittedFlow, setSubmittedFlow] = useState(null);
  const [currentNodeId, setCurrentNodeId] = useState(null);
  const [gptMode, setGptMode] = useState(false);
  const [chatHistory, setChatHistory] = useState([]);
  const [userInputs, setUserInputs] = useState({});
  const [inputValue, setInputValue] = useState("");
  const [multiChoiceSelection, setMultiChoiceSelection] = useState([]);
  const [systemPrompt, setSystemPrompt] = useState(
    "You are MindPeace, a compassionate mental health assistant. Based on the user's shared info and emotional state, continue the conversation empathetically."
  );
  const [gptModel, setGptModel] = useState("gpt-3.5-turbo");

  const history = useRef([{ nodes: initialNodes, edges: initialEdges }]);
  const historyIndex = useRef(0);

  const selectedNode = nodes.find((n) => n.id === selectedNodeId);
  const selectedEdge = manualEdges.find((e) => e.id === selectedEdgeId);

  const pushToHistory = (newNodes, newEdges) => {
    const currentState = { nodes: newNodes, edges: newEdges };
    const newHistory = history.current.slice(0, historyIndex.current + 1);
    newHistory.push(currentState);
    history.current = newHistory;
    historyIndex.current++;
  };

  const undo = () => {
    if (historyIndex.current > 0) {
      historyIndex.current--;
      const { nodes, edges } = history.current[historyIndex.current];
      setNodes(nodes);
      setEdges(edges);
    }
  };

  const redo = () => {
    if (historyIndex.current < history.current.length - 1) {
      historyIndex.current++;
      const { nodes, edges } = history.current[historyIndex.current];
      setNodes(nodes);
      setEdges(edges);
    }
  };

  const getNodeStyle = (type) => {
    const baseStyle = {
      borderRadius: 10,
      padding: 10,
      boxShadow: "2px 2px 8px #ccc",
      whiteSpace: "pre-wrap",
      wordWrap: "break-word",
      fontFamily: "Segoe UI, sans-serif",
      minWidth: 180,
      maxWidth: 300
    };
    switch (type) {
      case "choice":
        return { ...baseStyle, background: "#0284c7", border: "1px solid #0369a1", color: "white" };
      case "input":
        return { ...baseStyle, background: "#DCFCE7", border: "1px solid #22c55e" };
      case "multi_choice":
        return { ...baseStyle, background: "#facc15", border: "1px solid #eab308" };
      case "gpt":
        return { ...baseStyle, background: "#F3E8FF", border: "1px solid #8b5cf6" };
      case "end":
        return { ...baseStyle, background: "#FEE2E2", border: "1px solid #f87171" };
      default:
        return { ...baseStyle, background: "#F3F4F6", border: "1px solid #d1d5db" };
    }
  };

  const autoGeneratedEdges = useMemo(() => {
    const existingConnections = new Set(manualEdges.map((e) => `${e.source}->${e.target}`));
    const autoEdges = [];
    for (const node of nodes) {
      const target = node.data?.next;
      if (target && !existingConnections.has(`${node.id}->${target}`)) {
        autoEdges.push({
          id: `auto-${node.id}-${target}`,
          source: node.id,
          target,
          animated: false,
          type: "default",
          style: { strokeDasharray: "5,5", stroke: "#999" },
          markerEnd: { type: MarkerType.ArrowClosed },
          label: "next"
        });
      }
    }
    return autoEdges;
  }, [nodes, manualEdges]);

  const allEdges = [...manualEdges, ...autoGeneratedEdges];

  const getExportJSON = () => {
    const output = {
      settings: {
        system_prompt: systemPrompt,
        gpt_model: gptModel
      },
      nodes: {}
    };
    nodes.forEach((node) => {
      const { id, data } = node;
      const { message, type, options, capture, next } = data;
      const nodeObj = { message, type };
      if (options && Object.keys(options).length > 0) nodeObj.options = options;
      if (capture) nodeObj.capture = capture;
      if (next) nodeObj.next = next;
      output.nodes[id] = nodeObj;
    });
    return output;
  };
  const exportToJson = () => {
    const dataStr = "data:text/json;charset=utf-8," + encodeURIComponent(JSON.stringify(getExportJSON(), null, 2));
    const downloadAnchorNode = document.createElement("a");
    downloadAnchorNode.setAttribute("href", dataStr);
    downloadAnchorNode.setAttribute("download", "conversation_data.json");
    document.body.appendChild(downloadAnchorNode);
    downloadAnchorNode.click();
    downloadAnchorNode.remove();
  };

  const importJson = (event) => {
    const fileReader = new FileReader();
    fileReader.onload = (e) => {
      const json = JSON.parse(e.target.result);
      if (json.settings) {
        setSystemPrompt(json.settings.system_prompt || "");
        setGptModel(json.settings.gpt_model || "gpt-3.5-turbo");
      }
      const importedNodes = Object.entries(json.nodes || {}).map(([id, nodeData], index) => ({
        id,
        type: "default",
        position: { x: 100 + index * 50, y: 100 + index * 50 },
        data: {
          message: nodeData.message,
          type: nodeData.type,
          options: nodeData.options || {},
          capture: nodeData.capture || "",
          next: nodeData.next || ""
        }
      }));
      setNodes(importedNodes);
      setEdges([]);
    };
    fileReader.readAsText(event.target.files[0]);
  };

  const addNode = () => {
    const newNode = {
      id: `${nodeCounter++}`,
      type: "default",
      position: {
        x: Math.random() * 250 + 50,
        y: Math.random() * 250 + 50
      },
      data: {
        message: "New message",
        type: "text",
        options: {},
        capture: "",
        next: ""
      }
    };
    const updatedNodes = [...nodes, newNode];
    setNodes(updatedNodes);
    pushToHistory(updatedNodes, manualEdges);
  };

  const deleteNode = () => {
    if (!selectedNodeId) return;
    const updatedNodes = nodes.filter((n) => n.id !== selectedNodeId);
    const updatedEdges = manualEdges.filter(
      (e) => e.source !== selectedNodeId && e.target !== selectedNodeId
    );
    setNodes(updatedNodes);
    setEdges(updatedEdges);
    setSelectedNodeId(null);
    pushToHistory(updatedNodes, updatedEdges);
  };

  const updateNodeField = (field, value) => {
    const updated = nodes.map((node) => {
      if (node.id === selectedNodeId) {
        return {
          ...node,
          data: {
            ...node.data,
            [field]: value
          }
        };
      }
      return node;
    });
    setNodes(updated);
  };

  const updateEdgeLabel = (value) => {
    const updated = manualEdges.map((edge) =>
      edge.id === selectedEdgeId ? { ...edge, label: value } : edge
    );
    setEdges(updated);
  };

  const onConnect = (params) => {
    const newEdge = {
      ...params,
      id: `edge-${params.source}-${params.target}`,
      markerEnd: { type: MarkerType.ArrowClosed },
      style: { strokeWidth: 2 }
    };
    const updatedEdges = [...manualEdges, newEdge];
    setEdges(updatedEdges);
    pushToHistory(nodes, updatedEdges);
  };

  const submitFlow = () => {
    const flowData = getExportJSON();
    setSubmittedFlow(flowData);
    setChatbotReady(true);
    setChatHistory([]);
    setUserInputs({});
    setCurrentNodeId("1");
    setGptMode(false);
    setInputValue("");
    setMultiChoiceSelection([]);
  };
  const handleUserMessage = async () => {
    if (!inputValue.trim()) return;

    const newChat = [...chatHistory, { sender: "user", text: inputValue }];
    setChatHistory(newChat);
    setInputValue("");

    const systemPromptToSend = submittedFlow?.settings?.system_prompt || systemPrompt;
    const gptModelToSend = submittedFlow?.settings?.gpt_model || gptModel;

    try {
      const response = await fetch("http://localhost:5000/chat", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          messages: newChat,
          summary: JSON.stringify(userInputs),
          system_prompt: systemPromptToSend,
          gpt_model: gptModelToSend
        })
      });
      const data = await response.json();
      const reply = data.reply;
      setChatHistory([...newChat, { sender: "bot", text: reply }]);
    } catch (error) {
      console.error("GPT error:", error);
    }
  };

  const exportChat = () => {
    const content = chatHistory.map(msg => `${msg.sender === "user" ? "You" : "Bot"}: ${msg.text}`).join("\n\n");
    const blob = new Blob([content], { type: "text/plain" });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = "chat_history.txt";
    a.click();
    URL.revokeObjectURL(url);
  };

  return (
    <div style={{ display: "flex", height: "100vh", fontFamily: "Segoe UI, sans-serif" }}>
      <div style={{ flex: 1, display: "flex", flexDirection: "column" }}>
        <div style={{ padding: 10 }}>
          <button onClick={addNode} style={{ backgroundColor: "#0284c7", color: "white", marginRight: 5 }}>Add Node</button>
          <button onClick={exportToJson} style={{ backgroundColor: "green", color: "white", marginRight: 5 }}>Export JSON</button>
          <button onClick={deleteNode} style={{ backgroundColor: "red", color: "white", marginRight: 5 }}>Delete Node</button>
          <label style={{ marginRight: 5 }}>
            <input type="file" accept=".json" onChange={importJson} style={{ display: "none" }} id="fileInput" />
            <span style={{ backgroundColor: "orange", color: "white", padding: "5px 10px", borderRadius: 4, cursor: "pointer" }} onClick={() => document.getElementById("fileInput").click()}>Import JSON</span>
          </label>
          <button onClick={submitFlow} style={{ backgroundColor: "#0ea5e9", color: "white", marginRight: 5 }}>Submit Flow</button>
          <button onClick={undo} style={{ backgroundColor: "#f59e0b", color: "white", marginRight: 5 }}>Undo</button>
          <button onClick={redo} style={{ backgroundColor: "#8b5cf6", color: "white" }}>Redo</button>
        </div>

        <div style={{ flex: 1 }}>
          <ReactFlow
            nodes={nodes.map(n => ({ ...n, style: getNodeStyle(n.data.type) }))}
            edges={allEdges}
            onNodesChange={onNodesChange}
            onEdgesChange={onEdgesChange}
            onConnect={onConnect}
            onNodeClick={(_, node) => setSelectedNodeId(node.id)}
            onEdgeClick={(_, edge) => setSelectedEdgeId(edge.id)}
            fitView
          >
            <Background />
            <Controls />
            <MiniMap />
          </ReactFlow>
        </div>
      </div>

      <div style={{ width: "320px", padding: 20, borderLeft: "1px solid #ddd", display: "flex", flexDirection: "column", position: "relative" }}>
        {/* Prompt Settings Panel */}
        <div style={{ marginBottom: 10 }}>
          <h4>ðŸ§  Prompt Settings</h4>
          <div style={{ fontSize: "0.9rem", marginBottom: 4 }}>System Prompt:</div>
          <textarea
            value={systemPrompt}
            onChange={(e) => setSystemPrompt(e.target.value)}
            rows={4}
            style={{ width: "100%", marginBottom: 8 }}
          />
          <div style={{ fontSize: "0.9rem", marginBottom: 4 }}>GPT Model:</div>
          <select
            value={gptModel}
            onChange={(e) => setGptModel(e.target.value)}
            style={{ width: "100%", marginBottom: 8 }}
          >
            <option value="gpt-3.5-turbo">gpt-3.5-turbo</option>
            <option value="gpt-4">gpt-4</option>
          </select>
        </div>

        {/* Chat History */}
        <div style={{ flex: 1, overflowY: "auto", background: "#f9f9f9", padding: 10, border: "1px solid #ddd", borderRadius: 4, marginBottom: 10 }}>
          {chatHistory.map((msg, index) => (
            <div key={index} style={{ textAlign: msg.sender === "user" ? "right" : "left", margin: "4px 0" }}>
              <span
                style={{
                  display: "inline-block",
                  padding: "6px 10px",
                  borderRadius: 12,
                  background: msg.sender === "user" ? "#e0f2fe" : "#ede9fe",
                  color: "#111",
                  maxWidth: "90%",
                  wordWrap: "break-word"
                }}
              >
                {msg.text}
              </span>
            </div>
          ))}
        </div>

        {/* Input + Send */}
        <div style={{ display: "flex" }}>
          <input
            type="text"
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
            onKeyDown={(e) => e.key === "Enter" && handleUserMessage()}
            placeholder="Type your message..."
            style={{ flex: 1, padding: "8px", borderRadius: "6px 0 0 6px", border: "1px solid #ccc", fontSize: "14px" }}
          />
          <button
            onClick={handleUserMessage}
            style={{ backgroundColor: "#6366f1", color: "white", padding: "8px 14px", borderRadius: "0 6px 6px 0", border: "none", cursor: "pointer" }}
          >
            Send
          </button>
        </div>

        {/* Export Chat Button */}
        <button
          onClick={exportChat}
          style={{
            position: "absolute",
            top: 10,
            right: 10,
            padding: "4px 8px",
            backgroundColor: "orange",
            color: "white",
            border: "none",
            borderRadius: 4,
            fontSize: "12px"
          }}
        >
          Export Chat
        </button>
      </div>
    </div>
  );
}
