import React, { useCallback, useState } from "react";
import ReactFlow, {
  MiniMap,
  Controls,
  Background,
  useNodesState,
  useEdgesState,
  addEdge,
  MarkerType
} from "reactflow";
import "reactflow/dist/style.css";

const initialNodes = [
  {
    id: "1",
    data: {
      message: "Hi there, I’m MindPeace — your friendly mental health support assistant. Would you like to begin our conversation?",
      type: "choice",
      options: { yes: "2", no: "99" },
      capture: "",
      next: ""
    },
    position: { x: 250, y: 0 },
    type: "default"
  }
];

const initialEdges = [];
let nodeCounter = 2;

export default function App() {
  const [nodes, setNodes, onNodesChange] = useNodesState(initialNodes);
  const [edges, setEdges, onEdgesChange] = useEdgesState(initialEdges);
  const [selectedNodeId, setSelectedNodeId] = useState(null);
  const [optionKey, setOptionKey] = useState("");
  const [optionValue, setOptionValue] = useState("");

  const selectedNode = nodes.find((n) => n.id === selectedNodeId);

  const onConnect = useCallback(
    (params) =>
      setEdges((eds) =>
        addEdge(
          {
            ...params,
            markerEnd: { type: MarkerType.ArrowClosed }
          },
          eds
        )
      ),
    [setEdges]
  );

  const addNode = () => {
    const newId = nodeCounter.toString();
    nodeCounter++;
    const newNode = {
      id: newId,
      data: {
        message: "",
        type: "choice",
        options: {},
        capture: "",
        next: ""
      },
      position: { x: Math.random() * 400, y: Math.random() * 400 },
      type: "default"
    };
    setNodes((nds) => [...nds, newNode]);
  };

  const onNodeClick = (_, node) => {
    setSelectedNodeId(node.id);
  };

  const updateNodeField = (field, value) => {
    setNodes((nds) =>
      nds.map((node) => {
        if (node.id === selectedNodeId) {
          return {
            ...node,
            data: {
              ...node.data,
              [field]: value
            }
          };
        }
        return node;
      })
    );
  };

  const updateOptions = () => {
    if (!optionKey || !optionValue) return;
    setNodes((nds) =>
      nds.map((node) => {
        if (node.id === selectedNodeId) {
          return {
            ...node,
            data: {
              ...node.data,
              options: {
                ...node.data.options,
                [optionKey]: optionValue
              }
            }
          };
        }
        return node;
      })
    );
    setOptionKey("");
    setOptionValue("");
  };

  const exportJSON = () => {
    const output = {
      settings: {
        system_prompt:
          "You are MindPeace, a compassionate mental health assistant. Based on the user's shared info and emotional state, continue the conversation empathetically. Always respond warmly and supportively.",
        gpt_model: "gpt-3.5-turbo"
      },
      nodes: {}
    };

    nodes.forEach((node) => {
      const { id, data } = node;
      const { message, type, options, capture, next } = data;
      const nodeObj = { message, type };
      if (options && Object.keys(options).length > 0) nodeObj.options = options;
      if (capture) nodeObj.capture = capture;
      if (next) nodeObj.next = next;
      output.nodes[id] = nodeObj;
    });

    const blob = new Blob([JSON.stringify(output, null, 2)], { type: "application/json" });
    const url = URL.createObjectURL(blob);
    const link = document.createElement("a");
    link.href = url;
    link.download = "conversation_data.json";
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  return (
    <div style={{ width: "100vw", height: "100vh" }}>
      <button onClick={addNode} style={{ position: "absolute", top: 10, left: 10, zIndex: 10 }}>
        Add Node
      </button>
      <button onClick={exportJSON} style={{ position: "absolute", top: 10, left: 100, zIndex: 10 }}>
        Export JSON
      </button>

      {selectedNode && (
        <div
          style={{
            position: "absolute",
            top: 50,
            left: 10,
            zIndex: 10,
            background: "white",
            padding: 10,
            border: "1px solid #ccc",
            borderRadius: "8px",
            width: "300px"
          }}
        >
          <h4>Node ID: {selectedNode.id}</h4>
          <div>
            <label>Message:</label>
            <textarea
              value={selectedNode.data.message}
              onChange={(e) => updateNodeField("message", e.target.value)}
              style={{ width: "100%", height: 60 }}
            />
          </div>
          <div>
            <label>Type:</label>
            <select
              value={selectedNode.data.type}
              onChange={(e) => updateNodeField("type", e.target.value)}
            >
              <option value="choice">choice</option>
              <option value="input">input</option>
              <option value="multi_choice">multi_choice</option>
              <option value="gpt">gpt</option>
              <option value="end">end</option>
            </select>
          </div>
          <div>
            <label>Capture Field:</label>
            <input
              type="text"
              value={selectedNode.data.capture}
              onChange={(e) => updateNodeField("capture", e.target.value)}
              style={{ width: "100%" }}
            />
          </div>
          <div>
            <label>Next Node ID:</label>
            <input
              type="text"
              value={selectedNode.data.next}
              onChange={(e) => updateNodeField("next", e.target.value)}
              style={{ width: "100%" }}
            />
          </div>
          <div>
            <label>Prefilled Options:</label>
            <button onClick={() => setOptionKey("yes")}>Yes</button>
            <button onClick={() => setOptionKey("no")}>No</button>
            <input
              type="text"
              placeholder="Next Node ID"
              value={optionValue}
              onChange={(e) => setOptionValue(e.target.value)}
              style={{ width: "100%", marginTop: "5px" }}
            />
            <button onClick={updateOptions} style={{ marginTop: "5px" }}>
              Add Option
            </button>
          </div>
        </div>
      )}

      <ReactFlow
        nodes={nodes}
        edges={edges}
        onNodesChange={onNodesChange}
        onEdgesChange={onEdgesChange}
        onConnect={onConnect}
        onNodeClick={onNodeClick}
        fitView
      >
        <MiniMap />
        <Controls />
        <Background />
      </ReactFlow>
    </div>
  );
}
