import React, { useCallback, useState } from 'react';
import ReactFlow, {
  ReactFlowProvider,
  addEdge,
  MiniMap,
  Controls,
  Background,
  useNodesState,
  useEdgesState,
} from 'reactflow';
import 'reactflow/dist/style.css';
import { nanoid } from 'nanoid';

const App = () => {
  const [nodes, setNodes, onNodesChange] = useNodesState([]);
  const [edges, setEdges, onEdgesChange] = useEdgesState([]);
  const [selectedNodeId, setSelectedNodeId] = useState(null);
  const [label, setLabel] = useState('');
  const [capture, setCapture] = useState('');
  const [next, setNext] = useState('');
  const [optionsJson, setOptionsJson] = useState('');

  const onConnect = useCallback(
    (params) =>
      setEdges((eds) =>
        addEdge({ ...params, label: 'Option', type: 'default' }, eds)
      ),
    [setEdges]
  );

  const onNodeClick = useCallback((_, node) => {
    setSelectedNodeId(node.id);
    const n = nodes.find((n) => n.id === node.id);
    setLabel(n.data.label || '');
    setCapture(n.data.capture || '');
    setNext(n.data.next || '');
    setOptionsJson(JSON.stringify(n.data.options || {}, null, 2));
  }, [nodes]);

  const handleNodeUpdate = () => {
    setNodes((nds) =>
      nds.map((node) =>
        node.id === selectedNodeId
          ? {
              ...node,
              data: {
                ...node.data,
                label,
                capture,
                next,
                options: JSON.parse(optionsJson || '{}'),
              },
            }
          : node
      )
    );
  };

  const addNewNode = () => {
    const newId = nanoid(4);
    const newNode = {
      id: newId,
      type: 'default',
      data: { label: `New Node ${newId}` },
      position: {
        x: Math.random() * 400,
        y: Math.random() * 400,
      },
    };
    setNodes((nds) => [...nds, newNode]);
  };

  const exportToJson = () => {
    const exported = {
      nodes: {},
    };

    nodes.forEach((node) => {
      const nodeData = {
        message: node.data.label || '',
      };
      if (node.data.capture) nodeData.capture = node.data.capture;
      if (node.data.next) nodeData.next = node.data.next;
      if (node.data.options && Object.keys(node.data.options).length > 0) {
        nodeData.options = node.data.options;
      }
      exported.nodes[node.id] = nodeData;
    });

    const blob = new Blob([JSON.stringify(exported, null, 2)], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'conversation_data.json';
    a.click();
    URL.revokeObjectURL(url);
  };

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (e) => {
      try {
        const json = JSON.parse(e.target.result);
        if (!json.nodes) return alert('Invalid file format');

        const rawNodes = json.nodes;

        // 1. Build nodes
        const importedNodes = Object.entries(rawNodes).map(([id, node], index) => ({
          id,
          type: 'default',
          data: {
            label: node.message || '',
            capture: node.capture || '',
            next: node.next || '',
            options: node.options || {},
          },
          position: {
            x: (index % 4) * 250,
            y: Math.floor(index / 4) * 150,
          }
        }));

        // 2. Auto-create edges
        const generatedEdges = [];

        Object.entries(rawNodes).forEach(([sourceId, node]) => {
          // Edges from "options"
          if (node.options) {
            Object.entries(node.options).forEach(([key, targetId]) => {
              generatedEdges.push({
                id: `${sourceId}-${targetId}-${key}`,
                source: sourceId,
                target: targetId,
                label: key,
                type: 'default'
              });
            });
          }

          // Edge from "next"
          if (node.next) {
            generatedEdges.push({
              id: `${sourceId}-${node.next}`,
              source: sourceId,
              target: node.next,
              type: 'default'
            });
          }
        });

        setNodes(importedNodes);
        setEdges(generatedEdges);
        setSelectedNodeId(null);
        setLabel('');
        setCapture('');
        setNext('');
        setOptionsJson('');
      } catch (err) {
        alert('Failed to parse file.');
      }
    };
    reader.readAsText(file);
  };

  return (
    <ReactFlowProvider>
      <div style={{ display: 'flex', height: '100vh' }}>
        <div style={{ width: '75%', height: '100%' }}>
          <ReactFlow
            nodes={nodes}
            edges={edges}
            onNodesChange={onNodesChange}
            onEdgesChange={onEdgesChange}
            onConnect={onConnect}
            onNodeClick={onNodeClick}
            fitView
          >
            <MiniMap />
            <Controls />
            <Background />
          </ReactFlow>
        </div>
        <div style={{ width: '25%', padding: 10, background: '#f9f9f9' }}>
          <h3>Node Editor</h3>
          <button onClick={addNewNode}>âž• Add Node</button>
          <br /><br />
          <label>Message:</label><br />
          <textarea rows={3} value={label} onChange={(e) => setLabel(e.target.value)} style={{ width: '100%' }} />
          <br />
          <label>Capture Field (optional):</label><br />
          <input value={capture} onChange={(e) => setCapture(e.target.value)} style={{ width: '100%' }} />
          <br />
          <label>Next Node ID (optional):</label><br />
          <input value={next} onChange={(e) => setNext(e.target.value)} style={{ width: '100%' }} />
          <br />
          <label>Options (JSON):</label><br />
          <textarea rows={5} value={optionsJson} onChange={(e) => setOptionsJson(e.target.value)} style={{ width: '100%' }} />
          <br />
          <button onClick={handleNodeUpdate}>ðŸ’¾ Update Node</button>
          <br /><br />
          <button onClick={exportToJson}>ðŸ“¤ Export JSON</button>
          <br /><br />
          <input type="file" accept=".json" onChange={handleFileUpload} />
          <small><i>Upload conversation_data.json</i></small>
        </div>
      </div>
    </ReactFlowProvider>
  );
};

export default App;
