import React, { useState, useCallback, useEffect, useRef } from "react";
import ReactFlow, {
  MiniMap,
  Controls,
  Background,
  applyNodeChanges,
  applyEdgeChanges,
  addEdge
} from "reactflow";
import "reactflow/dist/style.css";
import "./styles.css";

const initialNodes = [];
const initialEdges = [];

export default function App() {
  const [nodes, setNodes] = useState(initialNodes);
  const [edges, setEdges] = useState(initialEdges);
  const [selectedNodeId, setSelectedNodeId] = useState(null);
  const [selectedEdgeId, setSelectedEdgeId] = useState(null);
  const [userInputs, setUserInputs] = useState({});
  const [submittedFlow, setSubmittedFlow] = useState(null);
  const [chatStarted, setChatStarted] = useState(false);
  const [chatHistory, setChatHistory] = useState([]);
  const [currentNodeId, setCurrentNodeId] = useState(null);
  const [chatInput, setChatInput] = useState("");
  const [multiChoiceSelections, setMultiChoiceSelections] = useState([]);
  const [showMultiSubmit, setShowMultiSubmit] = useState(false);
  const inputRef = useRef(null);

  const nodeTypes = {
    start: "#cceeff",
    choice: "#ffffcc",
    multi_choice: "#ffcc99",
    gpt: "#e0ccff",
    message: "#f8d7da"
  };

  const getNodeById = (id) => nodes.find((n) => n.id === id);

  const handleNodeClick = (_event, node) => setSelectedNodeId(node.id);
  const handleEdgeClick = (_event, edge) => setSelectedEdgeId(edge.id);

  const handleNodesChange = (changes) => setNodes((nds) => applyNodeChanges(changes, nds));
  const handleEdgesChange = (changes) => setEdges((eds) => applyEdgeChanges(changes, eds));

  const handleConnect = (params) =>
    setEdges((eds) => addEdge({ ...params, label: "next" }, eds));

  const getOutgoingEdges = (nodeId) =>
    edges.filter((e) => e.source === nodeId);

  const getNextNodeId = (edgeList, label) => {
    const match = edgeList.find((e) => e.label === label || e.label === "next");
    return match?.target || null;
  };

  const getNodeStyle = (type) => ({
    padding: 10,
    borderRadius: 10,
    boxShadow: "2px 2px 10px rgba(0,0,0,0.2)",
    background: nodeTypes[type] || "#ffffff",
    border: "1px solid #ccc",
    width: 180,
    textAlign: "center",
    whiteSpace: "pre-wrap"
  });

  const handleNodeFieldChange = (field, value) => {
    setNodes((prev) =>
      prev.map((node) =>
        node.id === selectedNodeId
          ? { ...node, data: { ...node.data, [field]: value } }
          : node
      )
    );
  };

  const handleEdgeLabelChange = (value) => {
    setEdges((prev) =>
      prev.map((edge) =>
        edge.id === selectedEdgeId ? { ...edge, label: value } : edge
      )
    );
  };

  const addNode = () => {
    const id = (nodes.length + 1).toString();
    const newNode = {
      id,
      type: "default",
      position: { x: 100 + nodes.length * 20, y: 100 + nodes.length * 20 },
      data: { label: `This is node ${id}`, type: "message", message: "" }
    };
    setNodes((nds) => [...nds, newNode]);
  };

  const deleteNode = () => {
    if (!selectedNodeId) return;
    setEdges((eds) => eds.filter((e) => e.source !== selectedNodeId && e.target !== selectedNodeId));
    setNodes((nds) => nds.filter((n) => n.id !== selectedNodeId));
    setSelectedNodeId(null);
  };

  const exportFlow = () => {
    const settings = {
      system_prompt: "You are a helpful assistant.",
      gpt_model: "gpt-3.5-turbo"
    };
    const exportData = {
      settings,
      nodes: nodes.map((n) => ({
        id: n.id,
        message: n.data.message,
        type: n.data.type,
        capture: n.data.capture || "",
        next: getOutgoingEdges(n.id)
          .filter((e) => e.label === "next")
          .map((e) => e.target)[0] || null,
        options: getOutgoingEdges(n.id)
          .filter((e) => e.label !== "next")
          .map((e) => ({ label: e.label, next_id: e.target }))
      }))
    };
    const blob = new Blob([JSON.stringify(exportData, null, 2)], { type: "application/json" });
    const a = document.createElement("a");
    a.href = URL.createObjectURL(blob);
    a.download = "conversation_data.json";
    a.click();
  };

  const submitFlow = async () => {
    const settings = {
      system_prompt: "You are a helpful assistant.",
      gpt_model: "gpt-3.5-turbo"
    };
    const exportData = {
      settings,
      nodes: nodes.map((n) => ({
        id: n.id,
        message: n.data.message,
        type: n.data.type,
        capture: n.data.capture || "",
        next: getOutgoingEdges(n.id)
          .filter((e) => e.label === "next")
          .map((e) => e.target)[0] || null,
        options: getOutgoingEdges(n.id)
          .filter((e) => e.label !== "next")
          .map((e) => ({ label: e.label, next_id: e.target }))
      }))
    };
    setSubmittedFlow(exportData);
    setChatStarted(true);
    setCurrentNodeId("1");
    setChatHistory([]);
    setUserInputs({});
    setChatInput("");
    setMultiChoiceSelections([]);
    setShowMultiSubmit(false);

    await fetch("http://localhost:5000/submit", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(exportData)
    });
  };

  const handleUserInput = async () => {
    const currentNode = submittedFlow.nodes.find((n) => n.id === currentNodeId);
    if (!currentNode) return;

    const updatedHistory = [...chatHistory, { role: "user", content: chatInput }];
    setChatHistory(updatedHistory);

    const res = await fetch("http://localhost:5000/chat", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        chat_history: updatedHistory,
        user_inputs: userInputs,
        system_prompt: submittedFlow.settings.system_prompt,
        gpt_model: submittedFlow.settings.gpt_model
      })
    });

    const data = await res.json();
    setChatHistory((prev) => [...prev, { role: "assistant", content: data.reply }]);
    setChatInput("");
  };

  const proceedToNextNode = async (response, selectedOptionLabel = null) => {
    const currentNode = submittedFlow.nodes.find((n) => n.id === currentNodeId);
    const newInputs = { ...userInputs };

    if (currentNode.capture) newInputs[currentNode.capture] = response;

    const formattedHistory = [
      ...chatHistory,
      { role: "assistant", content: currentNode.message },
      { role: "user", content: response }
    ];
    setChatHistory(formattedHistory);
    setUserInputs(newInputs);

    if (currentNode.type === "gpt") {
      const res = await fetch("http://localhost:5000/chat", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          chat_history: formattedHistory,
          user_inputs: newInputs,
          system_prompt: submittedFlow.settings.system_prompt,
          gpt_model: submittedFlow.settings.gpt_model
        })
      });
      const data = await res.json();
      setChatHistory((prev) => [...prev, { role: "assistant", content: data.reply }]);
      setChatInput("");
    }

    const nextNodeId =
      currentNode.next ||
      currentNode.options?.find((opt) => opt.label === selectedOptionLabel)?.next_id ||
      null;

    setCurrentNodeId(nextNodeId);
    setMultiChoiceSelections([]);
    setShowMultiSubmit(false);
  };

  const renderChatInput = () => {
    if (!chatStarted || !currentNodeId) return null;

    const currentNode = submittedFlow.nodes.find((n) => n.id === currentNodeId);
    if (!currentNode) return null;

    if (currentNode.type === "choice") {
      return currentNode.options.map((opt) => (
        <button
          key={opt.label}
          onClick={() => proceedToNextNode(opt.label, opt.label)}
          style={{ marginRight: "10px" }}
        >
          {opt.label}
        </button>
      ));
    }

    if (currentNode.type === "multi_choice") {
      const handleCheckboxChange = (label) => {
        setMultiChoiceSelections((prev) =>
          prev.includes(label) ? prev.filter((l) => l !== label) : [...prev, label]
        );
      };

      return (
        <div>
          {currentNode.options.map((opt) => (
            <div key={opt.label}>
              <label>
                <input
                  type="checkbox"
                  checked={multiChoiceSelections.includes(opt.label)}
                  onChange={() => handleCheckboxChange(opt.label)}
                />
                {opt.label}
              </label>
            </div>
          ))}
          <button
            style={{ marginTop: "10px", background: "green", color: "white" }}
            onClick={() => proceedToNextNode(multiChoiceSelections.join(", "), "next")}
          >
            Submit Selection
          </button>
        </div>
      );
    }

    if (currentNode.type === "gpt") {
      return (
        <div style={{ display: "flex", marginTop: 10 }}>
          <input
            ref={inputRef}
            type="text"
            value={chatInput}
            onChange={(e) => setChatInput(e.target.value)}
            style={{ flex: 1, marginRight: 10 }}
          />
          <button onClick={handleUserInput}>Send</button>
        </div>
      );
    }

    return (
      <button onClick={() => proceedToNextNode("")}>Continue</button>
    );
  };

  return (
    <div style={{ display: "flex", height: "100vh", width: "100vw" }}>
      {/* Left Designer Panel */}
      <div style={{ flex: 1, position: "relative" }}>
        <h2>Your AI Chatbot Designer â€” Fast. Visual. Code-Free</h2>
        <button onClick={addNode} style={{ background: "deepskyblue" }}>Add Node</button>
        <button onClick={exportFlow} style={{ background: "limegreen" }}>Export JSON</button>
        <button onClick={deleteNode} style={{ background: "red", color: "white" }}>Delete Node</button>
        <button onClick={submitFlow} style={{ background: "mediumseagreen" }}>Submit Flow</button>

        <div style={{ position: "absolute", top: 100, left: 10, zIndex: 10 }}>
          {selectedNodeId && (
            <div>
              <h4>Editing Node: {selectedNodeId}</h4>
              <label>Message:</label>
              <textarea
                rows={3}
                value={getNodeById(selectedNodeId)?.data.message || ""}
                onChange={(e) => handleNodeFieldChange("message", e.target.value)}
              />
              <br />
              <label>Type:</label>
              <select
                value={getNodeById(selectedNodeId)?.data.type || "message"}
                onChange={(e) => handleNodeFieldChange("type", e.target.value)}
              >
                <option value="message">message</option>
                <option value="choice">choice</option>
                <option value="multi_choice">multi_choice</option>
                <option value="gpt">gpt</option>
              </select>
              <br />
              <label>Capture Field:</label>
              <input
                value={getNodeById(selectedNodeId)?.data.capture || ""}
                onChange={(e) => handleNodeFieldChange("capture", e.target.value)}
              />
            </div>
          )}

          {selectedEdgeId && (
            <div>
              <h4>Editing Edge: {selectedEdgeId}</h4>
              <label>Label:</label>
              <input
                value={edges.find((e) => e.id === selectedEdgeId)?.label || ""}
                onChange={(e) => handleEdgeLabelChange(e.target.value)}
              />
            </div>
          )}
        </div>

        <ReactFlow
          nodes={nodes.map((n) => ({ ...n, style: getNodeStyle(n.data.type), data: { ...n.data, label: n.data.message } }))}
          edges={edges}
          onNodesChange={handleNodesChange}
          onEdgesChange={handleEdgesChange}
          onConnect={handleConnect}
          onNodeClick={handleNodeClick}
          onEdgeClick={handleEdgeClick}
          fitView
        >
          <Background />
          <Controls />
        </ReactFlow>
      </div>

      {/* Right Chat Panel */}
      <div style={{ width: "30%", padding: "10px", borderLeft: "1px solid #ccc", overflowY: "auto" }}>
        <h4>MindPeace Chat</h4>
        {chatHistory.map((msg, i) => (
          <div
            key={i}
            style={{
              background: msg.role === "user" ? "#cce5ff" : "#f1f1f1",
              padding: "8px",
              borderRadius: "10px",
              margin: "5px 0",
              alignSelf: msg.role === "user" ? "flex-end" : "flex-start",
              maxWidth: "90%"
            }}
          >
            {msg.content}
          </div>
        ))}
        {renderChatInput()}
      </div>
    </div>
  );
}
