import React, { useCallback, useState } from 'react';
import ReactFlow, {
  addEdge,
  Background,
  Controls,
  MiniMap,
  useEdgesState,
  useNodesState
} from 'reactflow';
import 'reactflow/dist/style.css';
import CustomEdge from './CustomEdge'; // ✅ NEW: import custom edge

// Initial dummy data
const initialNodes = [
  {
    id: '1',
    position: { x: 250, y: 5 },
    data: {
      label: 'Start Node',
      message: 'Hello! Do you want to continue? (Yes/No)',
      capture: '',
      next: '',
      end: false
    },
    type: 'default'
  },
  {
    id: '2',
    position: { x: 100, y: 100 },
    data: {
      label: 'Second Node',
      message: 'Would you like to share your name?',
      capture: 'name',
      next: '',
      end: false
    },
    type: 'default'
  }
];

const initialEdges = [
  { id: 'e1-2', source: '1', target: '2', data: { label: 'yes' } }
];

function App() {
  const [nodes, setNodes, onNodesChange] = useNodesState(initialNodes);
  const [edges, setEdges, onEdgesChange] = useEdgesState(initialEdges);
  const [selectedNode, setSelectedNode] = useState(null);
  const [selectedEdge, setSelectedEdge] = useState(null);
  const [systemPrompt, setSystemPrompt] = useState(
    "You are MindPeace, a friendly, compassionate mental health support assistant. Your role is to listen, validate, and gently guide the user to talk about their feelings, stress, anxiety, or mental well-being. You must refuse to answer any question unrelated to mental health. If asked about such unrelated topics (like sports, weather, celebrities, trivia, general knowledge), politely explain that you are here only to help them with their mental health and invite them to share how they feel. If the user asks for links or resources specifically about mental health topics (like relaxation exercises, meditation, breathing techniques), you must share at least 2 specific example YouTube links in your reply. Do not just suggest search terms. Always address the user by their first name only in normal conversation. If the user asks about their name or full name, respond politely with their full stored name. If the user asks about any other personal information (like email, age), do NOT reveal it. Instead, explain you must protect their privacy due to GDPR. You also remember user's shared problems. If the user asks 'what problems did I share with you?', list all their problems. IMPORTANT: Each time you reply, check if the user's latest message includes NEW mental health issues. If so, you MUST also output a JSON with a 'new_problems' list of any newly detected issues in the format: {'new_problems': [{'type': 'Panic Attacks', 'details': 'seeing ex girlfriend triggers panic'}]} Also include your normal human-friendly reply in a 'reply' field. Always return valid JSON with 'reply' and optional 'new_problems'."
  ); // State to store the system prompt

  // ✅ Edge type mapping
  const edgeTypes = {
    custom: CustomEdge
  };

  // Handle new connections with default label
  const onConnect = useCallback(
    (params) =>
      setEdges((eds) =>
        addEdge(
          { ...params, data: { label: 'Option' }, type: 'custom' },
          eds
        )
      ),
    [setEdges]
  );

  // Node click selects node
  const onNodeClick = useCallback((event, node) => {
    setSelectedNode(node);
    setSelectedEdge(null);
  }, []);

  // Edge click selects edge
  const onEdgeClick = useCallback((event, edge) => {
    event.stopPropagation();
    setSelectedEdge(edge);
    setSelectedNode(null);
  }, []);

  // Handle node field editing
  const handleFieldChange = (field, value) => {
    if (!selectedNode) return;

    setNodes((nds) =>
      nds.map((node) =>
        node.id === selectedNode.id
          ? {
              ...node,
              data: {
                ...node.data,
                [field]: field === 'end' ? value : value.trim(),
                label:
                  field === 'message'
                    ? value.trim().slice(0, 20) + (value.length > 20 ? '...' : '')
                    : node.data.label
              }
            }
          : node
      )
    );

    setSelectedNode((prev) => ({
      ...prev,
      data: {
        ...prev.data,
        [field]: field === 'end' ? value : value.trim(),
        label:
          field === 'message'
            ? value.trim().slice(0, 20) + (value.length > 20 ? '...' : '')
            : prev.data.label
      }
    }));
  };

  // Handle edge label editing
  const handleEdgeLabelChange = (value) => {
    if (!selectedEdge) return;

    setEdges((eds) =>
      eds.map((edge) =>
        edge.id === selectedEdge.id
          ? { ...edge, data: { ...edge.data, label: value.trim() } }
          : edge
      )
    );

    setSelectedEdge((prev) => ({
      ...prev,
      data: {
        ...prev.data,
        label: value.trim()
      }
    }));
  };

  // Add new node
  const addNewNode = () => {
    const newId = (nodes.length + 1).toString();
    const newNode = {
      id: newId,
      position: { x: 50, y: 50 },
      data: {
        label: 'New Node',
        message: '',
        capture: '',
        next: '',
        end: false
      },
      type: 'default'
    };
    setNodes((nds) => nds.concat(newNode));
  };

  // Delete selected node and its edges
  const deleteSelectedNode = () => {
    if (!selectedNode) return;
    const nodeIdToDelete = selectedNode.id;

    setNodes((nds) => nds.filter((node) => node.id !== nodeIdToDelete));
    setEdges((eds) =>
      eds.filter((edge) => edge.source !== nodeIdToDelete && edge.target !== nodeIdToDelete)
    );
    setSelectedNode(null);
  };

  // Export to conversation_tree.json
  const exportJSON = () => {
    const conversationTree = {
      settings: {
        default_gpt_model: "gpt-3.5-turbo",
        default_system_prompt: systemPrompt, // Use the editable system prompt here
        context_includes: ["history", "user_state", "problems"]
      },
      variables: {
        name: "",
        first_name: "",
        email: "",
        age: "",
        problem_type: "",
        problem_details: "",
        intensity: "",
        problems: []
      },
      nodes: {}
    };

    const optionsMap = {};

    edges.forEach((edge) => {
      if (!optionsMap[edge.source]) {
        optionsMap[edge.source] = {};
      }
      optionsMap[edge.source][edge.data?.label || 'Option'] = edge.target;
    });

    nodes.forEach((node) => {
      const { message, capture, next, end } = node.data;
      const nodeData = {
        message: message || ''
      };

      if (optionsMap[node.id]) {
        nodeData.options = optionsMap[node.id];
      }

      if (capture) nodeData.capture = capture;
      if (next) nodeData.next = next;
      if (end) nodeData.end = true;

      // Adding more attributes as needed
      if (node.data.follow_up_prompt) {
        nodeData.follow_up_prompt = node.data.follow_up_prompt;
      }
      if (node.data.follow_up_capture) {
        nodeData.follow_up_capture = node.data.follow_up_capture;
      }

      conversationTree.nodes[node.id] = nodeData;
    });

    const json = JSON.stringify(conversationTree, null, 2);
    const blob = new Blob([json], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'conversation_tree.json';
    a.click();
    URL.revokeObjectURL(url);
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', height: '100vh', width: '100%' }}>
      <div style={{ padding: '10px', background: '#eee', borderBottom: '1px solid #ccc' }}>
        <button
          onClick={addNewNode}
          style={{
            marginRight: '10px',
            padding: '8px 16px',
            backgroundColor: '#2196F3',
            color: 'white',
            border: 'none',
            borderRadius: '4px',
            cursor: 'pointer'
          }}
        >
          Add Node
        </button>

        <button
          onClick={deleteSelectedNode}
          style={{
            padding: '8px 16px',
            backgroundColor: '#f44336',
            color: 'white',
            border: 'none',
            borderRadius: '4px',
            cursor: 'pointer'
          }}
        >
          Delete Selected Node
        </button>

        {/* Editable System Prompt Field */}
        <div style={{ marginTop: '20px' }}>
          <label><strong>Edit System Prompt</strong></label>
          <textarea
            value={systemPrompt}
            onChange={(e) => setSystemPrompt(e.target.value)} // Update state when changed
            rows={6} // Adjusted height for readability
            style={{ width: '100%', marginBottom: '10px' }}
          />
        </div>
      </div>

      <div style={{ flex: 1, display: 'flex' }}>
        <div style={{ flex: 1 }}>
          <ReactFlow
            nodes={nodes}
            edges={edges}
            onNodesChange={onNodesChange}
            onEdgesChange={onEdgesChange}
            onConnect={onConnect}
            onNodeClick={onNodeClick}
            onEdgeClick={onEdgeClick}
            fitView
            edgeTypes={edgeTypes}
            defaultEdgeOptions={{ type: 'custom' }}
          >
            <Background />
            <Controls />
            <MiniMap />
          </ReactFlow>
        </div>

        {(selectedNode || selectedEdge) && (
          <div
            style={{
              width: '300px',
              padding: '15px',
              background: '#f4f4f4',
              borderLeft: '1px solid #ccc',
              overflowY: 'auto'
            }}
          >
            {selectedNode && (
              <>
                <h3>Node Editor</h3>
                <p><strong>ID:</strong> {selectedNode.id}</p>

                <label>Message:</label>
                <textarea
                  value={selectedNode.data.message}
                  onChange={(e) => handleFieldChange('message', e.target.value)}
                  rows={4}
                  style={{ width: '100%', marginBottom: '10px' }}
                />

                <label>Capture Field:</label>
                <input
                  type="text"
                  value={selectedNode.data.capture}
                  onChange={(e) => handleFieldChange('capture', e.target.value)}
                  style={{ width: '100%', marginBottom: '10px' }}
                />

                <label>Next Node:</label>
                <input
                  type="text"
                  value={selectedNode.data.next}
                  onChange={(e) => handleFieldChange('next', e.target.value)}
                  style={{ width: '100%', marginBottom: '10px' }}
                />

                <label style={{ display: 'block', marginTop: '10px' }}>
                  <input
                    type="checkbox"
                    checked={selectedNode.data.end}
                    onChange={(e) => handleFieldChange('end', e.target.checked)}
                  />{' '}
                  End Node
                </label>
              </>
            )}

            {selectedEdge && (
              <>
                <h3>Edge Editor</h3>
                <p><strong>ID:</strong> {selectedEdge.id}</p>

                <label>Label:</label>
                <input
                  type="text"
                  value={selectedEdge.data?.label || ''}
                  onChange={(e) => handleEdgeLabelChange(e.target.value)}
                  style={{ width: '100%', marginBottom: '10px' }}
                />
              </>
            )}
          </div>
        )}

        <button
          onClick={exportJSON}
          style={{
            position: 'fixed',
            bottom: '20px',
            right: '20px',
            padding: '10px 20px',
            backgroundColor: '#4CAF50',
            color: 'white',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer'
          }}
        >
          Export to JSON
        </button>
      </div>
    </div>
  );
}

export default App;
