import React, { useCallback, useState } from 'react';
import ReactFlow, {
  ReactFlowProvider,
  addEdge,
  MiniMap,
  Controls,
  Background,
  useNodesState,
  useEdgesState,
  MarkerType,
} from 'reactflow';
import 'reactflow/dist/style.css';
import { nanoid } from 'nanoid';

const App = () => {
  const [nodes, setNodes, onNodesChange] = useNodesState([]);
  const [edges, setEdges, onEdgesChange] = useEdgesState([]);
  const [selectedNodeId, setSelectedNodeId] = useState(null);
  const [label, setLabel] = useState('');
  const [capture, setCapture] = useState('');
  const [next, setNext] = useState('');
  const [expectedInputs, setExpectedInputs] = useState('');
  const [inputMapping, setInputMapping] = useState('');
  const [followUpPrompt, setFollowUpPrompt] = useState('');
  const [followUpCapture, setFollowUpCapture] = useState('');
  const [gpt, setGpt] = useState(false);
  const [end, setEnd] = useState(false);

  const onConnect = useCallback(
    (params) => {
      const label = prompt('Label for this edge (e.g., "yes", "no")');
      setEdges((eds) =>
        addEdge(
          {
            ...params,
            label: label || '',
            type: 'default',
            markerEnd: {
              type: MarkerType.ArrowClosed,
            },
          },
          eds
        )
      );
    },
    [setEdges]
  );

  const onNodeClick = useCallback((_, node) => {
    setSelectedNodeId(node.id);
    const n = nodes.find((n) => n.id === node.id);
    setLabel(n.data.label || '');
    setCapture(n.data.capture || '');
    setNext(n.data.next || '');
    setExpectedInputs((n.data.expected_inputs || []).join(', '));
    setInputMapping(JSON.stringify(n.data.input_mapping || {}, null, 2));
    setFollowUpPrompt(n.data.follow_up_prompt || '');
    setFollowUpCapture(n.data.follow_up_capture || '');
    setGpt(n.data.gpt?.enabled || false);
    setEnd(n.data.end || false);
  }, [nodes]);

  const handleNodeUpdate = () => {
    const inputs = expectedInputs.split(',').map((i) => i.trim()).filter(Boolean);
    let inputMap = {};
    try {
      inputMap = JSON.parse(inputMapping || '{}');
    } catch {}

    setNodes((nds) =>
      nds.map((node) =>
        node.id === selectedNodeId
          ? {
              ...node,
              data: {
                ...node.data,
                label,
                capture,
                next,
                expected_inputs: inputs,
                input_mapping: inputMap,
                follow_up_prompt: followUpPrompt,
                follow_up_capture: followUpCapture,
                gpt: gpt ? { enabled: true } : null,
                end,
              },
            }
          : node
      )
    );
  };

  const addNewNode = () => {
    const newId = nanoid(4);
    const newNode = {
      id: newId,
      type: 'default',
      data: {
        label: `New Node ${newId}`,
      },
      position: {
        x: Math.random() * 400,
        y: Math.random() * 400,
      }
    };
    setNodes((nds) => [...nds, newNode]);
  };

  const exportToJson = () => {
    const exported = {
      nodes: {},
    };

    nodes.forEach((node) => {
      const nodeData = {
        message: node.data.label || '',
      };
      if (node.data.capture) nodeData.capture = node.data.capture;
      if (node.data.next) nodeData.next = node.data.next;
      if (node.data.expected_inputs) nodeData.expected_inputs = node.data.expected_inputs;
      if (node.data.input_mapping) nodeData.input_mapping = node.data.input_mapping;
      if (node.data.follow_up_prompt) nodeData.follow_up_prompt = node.data.follow_up_prompt;
      if (node.data.follow_up_capture) nodeData.follow_up_capture = node.data.follow_up_capture;
      if (node.data.gpt) nodeData.gpt = node.data.gpt;
      if (node.data.end) nodeData.end = node.data.end;

      // derive options from edges
      const options = {};
      edges.forEach((edge) => {
        if (edge.source === node.id && edge.label) {
          options[edge.label] = edge.target;
        }
      });
      if (Object.keys(options).length > 0) {
        nodeData.options = options;
      }

      exported.nodes[node.id] = nodeData;
    });

    const blob = new Blob([JSON.stringify(exported, null, 2)], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'conversation_data.json';
    a.click();
    URL.revokeObjectURL(url);
  };

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (e) => {
      try {
        const json = JSON.parse(e.target.result);
        if (!json.nodes) return alert('Invalid file format');

        const rawNodes = json.nodes;

        const importedNodes = Object.entries(rawNodes).map(([id, node], index) => ({
          id,
          type: 'default',
          data: {
            label: node.message || '',
            capture: node.capture || '',
            next: node.next || '',
            expected_inputs: node.expected_inputs || [],
            input_mapping: node.input_mapping || {},
            follow_up_prompt: node.follow_up_prompt || '',
            follow_up_capture: node.follow_up_capture || '',
            gpt: node.gpt || null,
            end: node.end || false,
          },
          position: {
            x: (index % 4) * 250,
            y: Math.floor(index / 4) * 150,
          }
        }));

        const generatedEdges = [];
        Object.entries(rawNodes).forEach(([sourceId, node]) => {
          if (node.options) {
            Object.entries(node.options).forEach(([label, targetId]) => {
              generatedEdges.push({
                id: `${sourceId}-${targetId}-${label}`,
                source: sourceId,
                target: targetId,
                label,
                type: 'default',
                markerEnd: {
                  type: MarkerType.ArrowClosed,
                }
              });
            });
          }
          if (node.next) {
            generatedEdges.push({
              id: `${sourceId}-${node.next}`,
              source: sourceId,
              target: node.next,
              type: 'default',
              markerEnd: {
                type: MarkerType.ArrowClosed,
              }
            });
          }
        });

        setNodes(importedNodes);
        setEdges(generatedEdges);
        setSelectedNodeId(null);
      } catch (err) {
        alert('Failed to parse file.');
      }
    };
    reader.readAsText(file);
  };

  const nodeTypes = {
    default: ({ id, data }) => (
      <div style={{ padding: 10, border: '2px solid #333', borderRadius: 8, background: '#fff', minWidth: 150 }}>
        <strong>ID: {id}</strong>
        <div>{data.label}</div>
      </div>
    )
  };

  return (
    <ReactFlowProvider>
      <div style={{ display: 'flex', height: '100vh' }}>
        <div style={{ width: '75%', height: '100%' }}>
          <ReactFlow
            nodes={nodes}
            edges={edges}
            onNodesChange={onNodesChange}
            onEdgesChange={onEdgesChange}
            onConnect={onConnect}
            onNodeClick={onNodeClick}
            nodeTypes={nodeTypes}
            fitView
          >
            <MiniMap />
            <Controls />
            <Background />
          </ReactFlow>
        </div>
        <div style={{ width: '25%', padding: 10, background: '#f9f9f9' }}>
          <h3>Node Editor</h3>
          <button onClick={addNewNode}>âž• Add Node</button><br /><br />
          <label>Message:</label><br />
          <textarea rows={3} value={label} onChange={(e) => setLabel(e.target.value)} style={{ width: '100%' }} /><br />
          <label>Capture Field:</label><br />
          <input value={capture} onChange={(e) => setCapture(e.target.value)} style={{ width: '100%' }} /><br />
          <label>Next Node ID:</label><br />
          <input value={next} onChange={(e) => setNext(e.target.value)} style={{ width: '100%' }} /><br />
          <label>Expected Inputs (comma separated):</label><br />
          <input value={expectedInputs} onChange={(e) => setExpectedInputs(e.target.value)} style={{ width: '100%' }} /><br />
          <label>Input Mapping (JSON):</label><br />
          <textarea rows={3} value={inputMapping} onChange={(e) => setInputMapping(e.target.value)} style={{ width: '100%' }} /><br />
          <label>Follow-up Prompt:</label><br />
          <input value={followUpPrompt} onChange={(e) => setFollowUpPrompt(e.target.value)} style={{ width: '100%' }} /><br />
          <label>Follow-up Capture:</label><br />
          <input value={followUpCapture} onChange={(e) => setFollowUpCapture(e.target.value)} style={{ width: '100%' }} /><br />
          <label><input type="checkbox" checked={gpt} onChange={() => setGpt(!gpt)} /> GPT Enabled</label><br />
          <label><input type="checkbox" checked={end} onChange={() => setEnd(!end)} /> End Node</label><br /><br />
          <button onClick={handleNodeUpdate}>ðŸ’¾ Update Node</button><br /><br />
          <button onClick={exportToJson}>ðŸ“¤ Export JSON</button><br /><br />
          <input type="file" accept=".json" onChange={handleFileUpload} />
        </div>
      </div>
    </ReactFlowProvider>
  );
};

export default App;
