// PromptSettings.js
import React from "react";

export default function PromptSettings({ promptSettings, setPromptSettings }) {
  const handlePromptChange = (e) => {
    setPromptSettings((prev) => ({
      ...prev,
      system_prompt: e.target.value
    }));
  };

  const handleModelChange = (e) => {
    setPromptSettings((prev) => ({
      ...prev,
      gpt_model: e.target.value
    }));
  };

  return (
    <div
      style={{
        marginTop: 12,
        background: "#e0e7ff",
        padding: 12,
        border: "1px solid #c7d2fe",
        borderRadius: "10px",
        width: 300,
        boxShadow: "0 2px 6px rgba(0, 0, 0, 0.1)"
      }}
    >
      <h4>Prompt Settings</h4>

      <label style={{ fontWeight: "bold", display: "block", marginBottom: 4 }}>
        System Prompt:
      </label>
      <textarea
        value={promptSettings.system_prompt}
        onChange={handlePromptChange}
        style={{ width: "100%", height: 60, marginBottom: 8 }}
      />

      <label style={{ fontWeight: "bold", display: "block", marginBottom: 4 }}>
        GPT Model:
      </label>
      <select
        value={promptSettings.gpt_model}
        onChange={handleModelChange}
        style={{ width: "100%" }}
      >
        <option value="gpt-3.5-turbo">gpt-3.5-turbo</option>
        <option value="gpt-4">gpt-4</option>
      </select>
    </div>
  );
}
