import React, { useCallback, useState } from 'react';
import ReactFlow, {
  addEdge,
  Background,
  Controls,
  MiniMap,
  useEdgesState,
  useNodesState
} from 'reactflow';
import 'reactflow/dist/style.css';

// Initial conversation data (nodes and edges)
const initialNodes = [
  {
    id: '1',
    position: { x: 250, y: 5 },
    data: {
      label: 'Start Node',
      message: "Hello! I'm MindPeace. Do you want to continue? (Yes/No)",
      options: { yes: '2', no: '99' },
    },
    type: 'default',
  },
  {
    id: '2',
    position: { x: 100, y: 100 },
    data: {
      label: 'Name Node',
      message: 'Would you like to share your name? (Yes/No)',
      options: { yes: '3', no: '5' },
    },
    type: 'default',
  },
  {
    id: '3',
    position: { x: 400, y: 100 },
    data: {
      label: 'Capture Name',
      message: 'Please tell me your name:',
      capture: 'name',
      next: '4',
    },
    type: 'default',
  },
  {
    id: '4',
    position: { x: 250, y: 200 },
    data: {
      label: 'Email Node',
      message: "Thanks {first_name}! Would you like to share your email? (Yes/No)",
      options: { continue: '5' },
    },
    type: 'default',
  },
  {
    id: '5',
    position: { x: 100, y: 300 },
    data: {
      label: 'Email Node',
      message: "Would you like to share your email? (Yes/No)",
      options: { yes: '6', no: '7' },
    },
    type: 'default',
  },
  {
    id: '6',
    position: { x: 400, y: 300 },
    data: {
      label: 'Capture Email',
      message: 'Please type your email:',
      capture: 'email',
      next: '7',
    },
    type: 'default',
  },
  {
    id: '7',
    position: { x: 250, y: 400 },
    data: {
      label: 'Age Node',
      message: 'Would you like to share your age? (Yes/No)',
      options: { yes: '8', no: '10' },
    },
    type: 'default',
  },
  {
    id: '8',
    position: { x: 400, y: 500 },
    data: {
      label: 'Capture Age',
      message: 'Please tell me your age:',
      capture: 'age',
      next: '10',
    },
    type: 'default',
  },
  {
    id: '10',
    position: { x: 250, y: 600 },
    data: {
      label: 'Problem Type Node',
      message: "What brings you here today? (1. Stress 2. Anxiety 3. Panic Attacks 4. Overthinking 5. Other)",
      capture: 'problem_type',
      input_mapping: {
        '1': 'Stress',
        '2': 'Anxiety',
        '3': 'Panic Attacks',
        '4': 'Overthinking',
        '5': 'Other',
      },
      follow_up_prompt: 'Can you describe what\'s been causing your {problem_type}?',
      follow_up_capture: 'problem_details',
      next: '11',
    },
    type: 'default',
  },
  {
    id: '11',
    position: { x: 400, y: 700 },
    data: {
      label: 'Intensity Node',
      message: 'On a scale of 1 to 10, how intense are your feelings?',
      capture: 'intensity',
    },
    type: 'default',
  },
  {
    id: '99',
    position: { x: 250, y: 800 },
    data: {
      label: 'End Node',
      message: 'Thank you for reaching out. Remember, help is always available. Take care!',
      end: true,
    },
    type: 'default',
  },
];

const initialEdges = [
  { id: 'e1-2', source: '1', target: '2', data: { label: 'yes' } },
  { id: 'e2-3', source: '2', target: '3', data: { label: 'yes' } },
  { id: 'e3-4', source: '3', target: '4', data: { label: 'continue' } },
  { id: 'e4-5', source: '4', target: '5', data: { label: 'continue' } },
  { id: 'e2-5', source: '2', target: '5', data: { label: 'no' } },
  { id: 'e5-6', source: '5', target: '6', data: { label: 'yes' } },
  { id: 'e6-7', source: '6', target: '7', data: { label: 'continue' } },
  { id: 'e7-8', source: '7', target: '8', data: { label: 'yes' } },
  { id: 'e8-10', source: '8', target: '10', data: { label: 'continue' } },
  { id: 'e7-10', source: '7', target: '10', data: { label: 'no' } },
  { id: 'e10-11', source: '10', target: '11', data: { label: 'continue' } },
  { id: 'e11-99', source: '11', target: '99', data: { label: 'end' } },
];

// App Component
function App() {
  const [nodes, setNodes, onNodesChange] = useNodesState(initialNodes);
  const [edges, setEdges, onEdgesChange] = useEdgesState(initialEdges);
  const [selectedNode, setSelectedNode] = useState(null);
  const [selectedEdge, setSelectedEdge] = useState(null);
  const [systemPrompt, setSystemPrompt] = useState(
    `You are MindPeace, a friendly, compassionate mental health support assistant. 
    Your role is to listen, validate, and gently guide the user to talk about their feelings, stress, anxiety, or mental well-being. 
    You must refuse to answer any question unrelated to mental health. If asked about such unrelated topics (like sports, weather, celebrities, trivia, general knowledge), politely explain that you are here only to help them with their mental health and invite them to share how they feel. 
    If the user asks for links or resources specifically about mental health topics (like relaxation exercises, meditation, breathing techniques), you must share at least 2 specific example YouTube links in your reply. Do not just suggest search terms. 
    Always address the user by their first name only in normal conversation. 
    If the user asks for their name or full name, respond politely with their full stored name. 
    If the user asks about any other personal information (like email, age), do NOT reveal it. Instead, explain you must protect their privacy due to GDPR. 
    You also remember user's shared problems. If the user asks 'what problems did I share with you?', list all their problems. 
    IMPORTANT: Each time you reply, check if the user's latest message includes NEW mental health issues. 
    If so, you MUST also output a JSON with a 'new_problems' list of any newly detected issues in the format: 
    {'new_problems': [{'type': 'Panic Attacks', 'details': 'seeing ex girlfriend triggers panic'}]} 
    Also include your normal human-friendly reply in a 'reply' field. 
    Always return valid JSON with 'reply' and optional 'new_problems'.`
  );

  // Handle new connections with default label
  const onConnect = useCallback(
    (params) =>
      setEdges((eds) =>
        addEdge({ ...params, data: { label: 'Option' } }, eds)
      ),
    [setEdges]
  );

  // Node click selects node
  const onNodeClick = useCallback((event, node) => {
    setSelectedNode(node);
    setSelectedEdge(null);
  }, []);

  // Edge click selects edge
  const onEdgeClick = useCallback((event, edge) => {
    event.stopPropagation();
    setSelectedEdge(edge);
    setSelectedNode(null);
  }, []);

  // Handle node field editing
  const handleFieldChange = (field, value) => {
    if (!selectedNode) return;

    setNodes((nds) =>
      nds.map((node) =>
        node.id === selectedNode.id
          ? {
              ...node,
              data: {
                ...node.data,
                [field]: field === 'end' ? value : value.trim(),
                label:
                  field === 'message'
                    ? value.trim().slice(0, 20) + (value.length > 20 ? '...' : '')
                    : node.data.label,
              },
            }
          : node
      )
    );

    setSelectedNode((prev) => ({
      ...prev,
      data: {
        ...prev.data,
        [field]: field === 'end' ? value : value.trim(),
        label:
          field === 'message'
            ? value.trim().slice(0, 20) + (value.length > 20 ? '...' : '')
            : prev.data.label,
      },
    }));
  };

  // Handle edge label editing
  const handleEdgeLabelChange = (value) => {
    if (!selectedEdge) return;

    setEdges((eds) =>
      eds.map((edge) =>
        edge.id === selectedEdge.id
          ? { ...edge, data: { ...edge.data, label: value.trim() } }
          : edge
      )
    );

    setSelectedEdge((prev) => ({
      ...prev,
      data: {
        ...prev.data,
        label: value.trim(),
      },
    }));
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', height: '100vh', width: '100%' }}>
      <div style={{ padding: '10px', background: '#eee', borderBottom: '1px solid #ccc' }}>
        <button
          onClick={addNewNode}
          style={{
            marginRight: '10px',
            padding: '8px 16px',
            backgroundColor: '#2196F3',
            color: 'white',
            border: 'none',
            borderRadius: '4px',
            cursor: 'pointer',
          }}
        >
          Add Node
        </button>

        <button
          onClick={deleteSelectedNode}
          style={{
            padding: '8px 16px',
            backgroundColor: '#f44336',
            color: 'white',
            border: 'none',
            borderRadius: '4px',
            cursor: 'pointer',
          }}
        >
          Delete Selected Node
        </button>

        {/* Editable System Prompt Field */}
        <div style={{ marginTop: '20px' }}>
          <label>
            <strong>Edit System Prompt</strong>
          </label>
          <textarea
            value={systemPrompt}
            onChange={(e) => setSystemPrompt(e.target.value)} // Update state when changed
            rows={6}
            style={{ width: '100%', marginBottom: '10px' }}
          />
        </div>
      </div>

      <div style={{ flex: 1, display: 'flex' }}>
        <div style={{ flex: 1 }}>
          <ReactFlow
            nodes={nodes}
            edges={edges}
            onNodesChange={onNodesChange}
            onEdgesChange={onEdgesChange}
            onConnect={onConnect}
            onNodeClick={onNodeClick}
            onEdgeClick={onEdgeClick}
            fitView
          >
            <Background />
            <Controls />
            <MiniMap />
          </ReactFlow>
        </div>

        {(selectedNode || selectedEdge) && (
          <div
            style={{
              width: '300px',
              padding: '15px',
              background: '#f4f4f4',
              borderLeft: '1px solid #ccc',
              overflowY: 'auto',
            }}
          >
            {selectedNode && (
              <>
                <h3>Node Editor</h3>
                <p>
                  <strong>ID:</strong> {selectedNode.id}
                </p>

                <label>Message:</label>
                <textarea
                  value={selectedNode.data.message}
                  onChange={(e) => handleFieldChange('message', e.target.value)}
                  rows={4}
                  style={{ width: '100%', marginBottom: '10px' }}
                />

                <label>Capture Field:</label>
                <input
                  type="text"
                  value={selectedNode.data.capture}
                  onChange={(e) => handleFieldChange('capture', e.target.value)}
                  style={{ width: '100%', marginBottom: '10px' }}
                />

                <label>Next Node:</label>
                <input
                  type="text"
                  value={selectedNode.data.next}
                  onChange={(e) => handleFieldChange('next', e.target.value)}
                  style={{ width: '100%', marginBottom: '10px' }}
                />

                <label style={{ display: 'block', marginTop: '10px' }}>
                  <input
                    type="checkbox"
                    checked={selectedNode.data.end}
                    onChange={(e) => handleFieldChange('end', e.target.checked)}
                  />{' '}
                  End Node
                </label>
              </>
            )}

            {selectedEdge && (
              <>
                <h3>Edge Editor</h3>
                <p>
                  <strong>ID:</strong> {selectedEdge.id}
                </p>

                <label>Label:</label>
                <input
                  type="text"
                  value={selectedEdge.data?.label || ''}
                  onChange={(e) => handleEdgeLabelChange(e.target.value)}
                  style={{ width: '100%', marginBottom: '10px' }}
                />
              </>
            )}
          </div>
        )}

        <button
          onClick={() => {
            const blob = new Blob([JSON.stringify({ nodes, edges }, null, 2)], {
              type: 'application/json',
            });
            const url = URL.createObjectURL(blob);
            const a = document.createElement('a');
            a.href = url;
            a.download = 'conversation_tree.json';
            a.click();
            URL.revokeObjectURL(url);
          }}
          style={{
            position: 'fixed',
            bottom: '20px',
            right: '20px',
            padding: '10px 20px',
            backgroundColor: '#4CAF50',
            color: 'white',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer',
          }}
        >
          Export to JSON
        </button>
      </div>
    </div>
  );
}

export default App;
