import React, { useState, useCallback } from 'react';
import ReactFlow, { addEdge, removeElements, MiniMap, Controls, Background } from 'react-flow-renderer';
import { v4 as uuidv4 } from 'uuid';

const initialNodes = [
  {
    id: '1',
    data: { label: 'Hello! I\'m MindPeace. Do you want to continue? (Yes/No)' },
    position: { x: 250, y: 50 },
    type: 'default',
    style: { width: 200, height: 80 },
  },
  {
    id: '2',
    data: { label: 'Would you like to share your name? (Yes/No)' },
    position: { x: 250, y: 200 },
    type: 'default',
    style: { width: 200, height: 80 },
  },
  {
    id: '3',
    data: { label: 'Please tell me your name:' },
    position: { x: 250, y: 350 },
    type: 'default',
    style: { width: 200, height: 80 },
  },
];

const initialEdges = [
  { id: 'e1-2', source: '1', target: '2', animated: true },
  { id: 'e2-3', source: '2', target: '3', animated: true },
];

const App = () => {
  const [nodes, setNodes] = useState(initialNodes);
  const [edges, setEdges] = useState(initialEdges);
  const [selectedNode, setSelectedNode] = useState(null);
  const [message, setMessage] = useState('');
  const [options, setOptions] = useState('');

  const onConnect = useCallback((params) => setEdges((eds) => addEdge(params, eds)), []);
  const onNodesChange = useCallback((changes) => setNodes((nds) => applyNodeChanges(changes, nds)), []);
  const onEdgesChange = useCallback((changes) => setEdges((eds) => applyEdgeChanges(changes, eds)), []);

  const handleAddNode = () => {
    const newNode = {
      id: uuidv4(),
      data: { label: 'New Node' },
      position: { x: 250, y: 100 },
      type: 'default',
      style: { width: 200, height: 80 },
    };
    setNodes((nds) => nds.concat(newNode));
  };

  const handleDeleteNode = () => {
    if (selectedNode) {
      setNodes((nds) => nds.filter((node) => node.id !== selectedNode));
      setEdges((eds) => eds.filter((edge) => edge.source !== selectedNode && edge.target !== selectedNode));
      setSelectedNode(null);
    }
  };

  const handleEditNode = () => {
    if (selectedNode) {
      setNodes((nds) =>
        nds.map((node) =>
          node.id === selectedNode
            ? { ...node, data: { ...node.data, label: message, options } }
            : node
        )
      );
    }
  };

  const handleSelectNode = (nodeId) => {
    setSelectedNode(nodeId);
    const selectedNode = nodes.find((node) => node.id === nodeId);
    setMessage(selectedNode.data.label);
    setOptions(selectedNode.data.options || '');
  };

  return (
    <div style={{ height: '100vh' }}>
      <div style={{ display: 'flex', flexDirection: 'column', padding: '10px' }}>
        <button onClick={handleAddNode}>Add Node</button>
        <button onClick={handleDeleteNode}>Delete Node</button>
        <input
          type="text"
          placeholder="Edit Message"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
        />
        <input
          type="text"
          placeholder="Edit Options (comma-separated)"
          value={options}
          onChange={(e) => setOptions(e.target.value)}
        />
        <button onClick={handleEditNode}>Update Node</button>
      </div>

      <ReactFlow
        nodes={nodes}
        edges={edges}
        onConnect={onConnect}
        onNodesChange={onNodesChange}
        onEdgesChange={onEdgesChange}
        nodeTypes={{ default: 'default' }}
        onElementClick={(event, element) => handleSelectNode(element.id)}
        style={{ width: '100%', height: '100%' }}
      >
        <MiniMap />
        <Controls />
        <Background />
      </ReactFlow>
    </div>
  );
};

export default App;
