import React, { useCallback, useState } from 'react';
import ReactFlow, {
  addEdge,
  Background,
  Controls,
  MiniMap,
  useEdgesState,
  useNodesState
} from 'reactflow';
import 'reactflow/dist/style.css';

function App() {
  const [nodes, setNodes, onNodesChange] = useNodesState([]);
  const [edges, setEdges, onEdgesChange] = useEdgesState([]);
  const [selectedNode, setSelectedNode] = useState(null);
  const [selectedEdge, setSelectedEdge] = useState(null);
  const [systemPrompt, setSystemPrompt] = useState(
    `You are MindPeace, a friendly, compassionate mental health support assistant...`
  );

  const onConnect = useCallback(
    (params) =>
      setEdges((eds) =>
        addEdge({ ...params, data: { label: 'Option' }, type: 'default' }, eds)
      ),
    [setEdges]
  );

  const onNodeClick = useCallback((event, node) => {
    setSelectedNode(node);
    setSelectedEdge(null);
  }, []);

  const onEdgeClick = useCallback((event, edge) => {
    event.stopPropagation();
    setSelectedEdge(edge);
    setSelectedNode(null);
  }, []);

  const handleFieldChange = (field, value) => {
    if (!selectedNode) return;

    let updatedValue = value;

    if (field === 'expected_inputs') {
      updatedValue = value.split(',').map((v) => v.trim());
    }

    if (field === 'input_mapping') {
      try {
        updatedValue = JSON.parse(value);
      } catch (err) {
        alert('Invalid JSON in input_mapping');
        return;
      }
    }

    if (field === 'gpt') {
      updatedValue = { enabled: value };
    }

    setNodes((nds) =>
      nds.map((node) =>
        node.id === selectedNode.id
          ? {
              ...node,
              data: {
                ...node.data,
                [field]: updatedValue,
                label:
                  field === 'message'
                    ? updatedValue.trim().slice(0, 20) +
                      (updatedValue.length > 20 ? '...' : '')
                    : node.data.label
              }
            }
          : node
      )
    );

    setSelectedNode((prev) => ({
      ...prev,
      data: {
        ...prev.data,
        [field]: updatedValue,
        label:
          field === 'message'
            ? updatedValue.trim().slice(0, 20) +
              (updatedValue.length > 20 ? '...' : '')
            : prev.data.label
      }
    }));
  };

  const handleEdgeLabelChange = (value) => {
    if (!selectedEdge) return;

    setEdges((eds) =>
      eds.map((edge) =>
        edge.id === selectedEdge.id
          ? { ...edge, data: { ...edge.data, label: value.trim() } }
          : edge
      )
    );

    setSelectedEdge((prev) => ({
      ...prev,
      data: {
        ...prev.data,
        label: value.trim()
      }
    }));
  };

  const addNewNode = () => {
    const newId = (nodes.length + 1).toString();
    const newNode = {
      id: newId,
      position: { x: 100, y: 100 },
      data: {
        label: 'New Node',
        message: '',
        capture: '',
        next: '',
        end: false,
        expected_inputs: [],
        input_mapping: {},
        follow_up_prompt: '',
        follow_up_capture: '',
        gpt: { enabled: false }
      },
      type: 'default'
    };
    setNodes((nds) => nds.concat(newNode));
  };

  const deleteSelectedNode = () => {
    if (!selectedNode) return;
    const nodeIdToDelete = selectedNode.id;

    setNodes((nds) => nds.filter((node) => node.id !== nodeIdToDelete));
    setEdges((eds) =>
      eds.filter((edge) => edge.source !== nodeIdToDelete && edge.target !== nodeIdToDelete)
    );
    setSelectedNode(null);
  };

  const exportJSON = () => {
    const conversationTree = {
      settings: {
        default_gpt_model: "gpt-3.5-turbo",
        default_system_prompt: systemPrompt,
        context_includes: ["history", "user_state", "problems"]
      },
      variables: {
        name: "",
        first_name: "",
        email: "",
        age: "",
        problem_type: "",
        problem_details: "",
        intensity: "",
        problems: []
      },
      nodes: {}
    };

    const optionsMap = {};
    edges.forEach((edge) => {
      if (!optionsMap[edge.source]) {
        optionsMap[edge.source] = {};
      }
      optionsMap[edge.source][edge.data?.label || 'Option'] = edge.target;
    });

    nodes.forEach((node) => {
      const data = node.data;
      const nodeData = { message: data.message || '' };

      if (optionsMap[node.id]) nodeData.options = optionsMap[node.id];
      if (data.capture) nodeData.capture = data.capture;
      if (data.next) nodeData.next = data.next;
      if (data.end) nodeData.end = true;
      if (data.expected_inputs?.length) nodeData.expected_inputs = data.expected_inputs;
      if (Object.keys(data.input_mapping || {}).length)
        nodeData.input_mapping = data.input_mapping;
      if (data.follow_up_prompt) nodeData.follow_up_prompt = data.follow_up_prompt;
      if (data.follow_up_capture) nodeData.follow_up_capture = data.follow_up_capture;
      if (data.gpt?.enabled) nodeData.gpt = { enabled: true };

      conversationTree.nodes[node.id] = nodeData;
    });

    const json = JSON.stringify(conversationTree, null, 2);
    const blob = new Blob([json], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'conversation_tree.json';
    a.click();
    URL.revokeObjectURL(url);
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', height: '100vh' }}>
      <div style={{ padding: '10px', background: '#eee' }}>
        <button onClick={addNewNode} style={{ marginRight: 10 }}>Add Node</button>
        <button onClick={deleteSelectedNode}>Delete Node</button>
        <div style={{ marginTop: 10 }}>
          <label><strong>System Prompt</strong></label>
          <textarea
            value={systemPrompt}
            onChange={(e) => setSystemPrompt(e.target.value)}
            rows={6}
            style={{ width: '100%' }}
          />
        </div>
      </div>

      <div style={{ flex: 1, display: 'flex' }}>
        <div style={{ flex: 1 }}>
          <ReactFlow
            nodes={nodes}
            edges={edges}
            onNodesChange={onNodesChange}
            onEdgesChange={onEdgesChange}
            onConnect={onConnect}
            onNodeClick={onNodeClick}
            onEdgeClick={onEdgeClick}
            fitView
          >
            <Background />
            <Controls />
            <MiniMap />
          </ReactFlow>
        </div>

        {(selectedNode || selectedEdge) && (
          <div style={{ width: 300, padding: 10, background: '#f4f4f4' }}>
            {selectedNode && (
              <>
                <h3>Node Editor</h3>
                <label>Message:</label>
                <textarea
                  value={selectedNode.data.message}
                  onChange={(e) => handleFieldChange('message', e.target.value)}
                  rows={3}
                  style={{ width: '100%' }}
                />

                <label>Capture:</label>
                <input
                  type="text"
                  value={selectedNode.data.capture}
                  onChange={(e) => handleFieldChange('capture', e.target.value)}
                  style={{ width: '100%' }}
                />

                <label>Next:</label>
                <input
                  type="text"
                  value={selectedNode.data.next}
                  onChange={(e) => handleFieldChange('next', e.target.value)}
                  style={{ width: '100%' }}
                />

                <label>Expected Inputs (comma):</label>
                <input
                  type="text"
                  value={selectedNode.data.expected_inputs?.join(', ') || ''}
                  onChange={(e) => handleFieldChange('expected_inputs', e.target.value)}
                  style={{ width: '100%' }}
                />

                <label>Input Mapping (JSON):</label>
                <textarea
                  value={JSON.stringify(selectedNode.data.input_mapping || {}, null, 2)}
                  onChange={(e) => handleFieldChange('input_mapping', e.target.value)}
                  rows={2}
                  style={{ width: '100%' }}
                />

                <label>Follow-up Prompt:</label>
                <input
                  type="text"
                  value={selectedNode.data.follow_up_prompt || ''}
                  onChange={(e) => handleFieldChange('follow_up_prompt', e.target.value)}
                  style={{ width: '100%' }}
                />

                <label>Follow-up Capture:</label>
                <input
                  type="text"
                  value={selectedNode.data.follow_up_capture || ''}
                  onChange={(e) => handleFieldChange('follow_up_capture', e.target.value)}
                  style={{ width: '100%' }}
                />

                <label>
                  <input
                    type="checkbox"
                    checked={selectedNode.data.gpt?.enabled || false}
                    onChange={(e) => handleFieldChange('gpt', e.target.checked)}
                  /> GPT Enabled
                </label>

                <label>
                  <input
                    type="checkbox"
                    checked={selectedNode.data.end || false}
                    onChange={(e) => handleFieldChange('end', e.target.checked)}
                  /> End Node
                </label>
              </>
            )}

            {selectedEdge && (
              <>
                <h3>Edge Editor</h3>
                <label>Label:</label>
                <input
                  type="text"
                  value={selectedEdge.data?.label || ''}
                  onChange={(e) => handleEdgeLabelChange(e.target.value)}
                  style={{ width: '100%' }}
                />
              </>
            )}
          </div>
        )}
      </div>

      <button
        onClick={exportJSON}
        style={{
          position: 'fixed',
          bottom: 20,
          right: 20,
          padding: '10px 20px',
          backgroundColor: '#4CAF50',
          color: 'white',
          border: 'none',
          borderRadius: '5px',
          cursor: 'pointer'
        }}
      >
        Export to JSON
      </button>
    </div>
  );
}

export default App;
