// üß† All imports remain the same...
import React, { useCallback, useState } from 'react';
import ReactFlow, {
  addEdge,
  Background,
  Controls,
  MiniMap,
  useEdgesState,
  useNodesState
} from 'reactflow';
import 'reactflow/dist/style.css';

function App() {
  const [nodes, setNodes, onNodesChange] = useNodesState([]);
  const [edges, setEdges, onEdgesChange] = useEdgesState([]);
  const [selectedNode, setSelectedNode] = useState(null);
  const [selectedEdge, setSelectedEdge] = useState(null);
  const [systemPrompt, setSystemPrompt] = useState('');

  const onConnect = useCallback((params) => {
    setEdges((eds) =>
      addEdge({ ...params, data: { label: 'Option' }, type: 'default' }, eds)
    );
  }, [setEdges]);

  const onNodeClick = useCallback((_, node) => {
    setSelectedNode(node);
    setSelectedEdge(null);
  }, []);

  const onEdgeClick = useCallback((event, edge) => {
    event.stopPropagation();
    setSelectedEdge(edge);
    setSelectedNode(null);
  }, []);

  const handleFieldChange = (field, value) => {
    if (!selectedNode) return;

    let updatedValue = value;

    if (field === 'expected_inputs') {
      updatedValue = value.split(',').map((v) => v.trim());
    }

    if (field === 'input_mapping') {
      try {
        updatedValue = JSON.parse(value);
      } catch {
        alert('Invalid JSON in input_mapping');
        return;
      }
    }

    if (field === 'gpt') {
      updatedValue = { enabled: value };
    }

    setNodes((nds) =>
      nds.map((node) =>
        node.id === selectedNode.id
          ? {
              ...node,
              data: {
                ...node.data,
                [field]: updatedValue,
                label:
                  field === 'message'
                    ? updatedValue.trim().slice(0, 20) + (updatedValue.length > 20 ? '...' : '')
                    : node.data.label
              }
            }
          : node
      )
    );

    setSelectedNode((prev) => ({
      ...prev,
      data: {
        ...prev.data,
        [field]: updatedValue,
        label:
          field === 'message'
            ? updatedValue.trim().slice(0, 20) + (updatedValue.length > 20 ? '...' : '')
            : prev.data.label
      }
    }));
  };

  const handleEdgeLabelChange = (value) => {
    if (!selectedEdge) return;

    setEdges((eds) =>
      eds.map((edge) =>
        edge.id === selectedEdge.id
          ? { ...edge, data: { ...edge.data, label: value.trim() } }
          : edge
      )
    );

    setSelectedEdge((prev) => ({
      ...prev,
      data: {
        ...prev.data,
        label: value.trim()
      }
    }));
  };

  const addNewNode = () => {
    const newId = (nodes.length + 1).toString();
    const newNode = {
      id: newId,
      position: { x: 100, y: 100 },
      data: {
        label: 'New Node',
        message: '',
        capture: '',
        next: '',
        end: false,
        expected_inputs: [],
        input_mapping: {},
        follow_up_prompt: '',
        follow_up_capture: '',
        gpt: { enabled: false }
      },
      type: 'default'
    };
    setNodes((nds) => nds.concat(newNode));
  };

  const deleteSelectedNode = () => {
    if (!selectedNode) return;
    const nodeIdToDelete = selectedNode.id;

    setNodes((nds) => nds.filter((node) => node.id !== nodeIdToDelete));
    setEdges((eds) =>
      eds.filter((edge) => edge.source !== nodeIdToDelete && edge.target !== nodeIdToDelete)
    );
    setSelectedNode(null);
  };

  const exportJSON = () => {
    const conversationTree = {
      settings: {
        default_gpt_model: 'gpt-3.5-turbo',
        default_system_prompt: systemPrompt,
        context_includes: ['history', 'user_state', 'problems']
      },
      variables: {
        name: '',
        first_name: '',
        email: '',
        age: '',
        problem_type: '',
        problem_details: '',
        intensity: '',
        problems: []
      },
      nodes: {}
    };

    const optionsMap = {};
    edges.forEach((edge) => {
      if (!optionsMap[edge.source]) optionsMap[edge.source] = {};
      optionsMap[edge.source][edge.data?.label || 'Option'] = edge.target;
    });

    nodes.forEach((node) => {
      const data = node.data;
      const nodeData = { message: data.message || '' };

      if (optionsMap[node.id]) nodeData.options = optionsMap[node.id];
      if (data.capture) nodeData.capture = data.capture;
      if (data.next) nodeData.next = data.next;
      if (data.end) nodeData.end = true;
      if (data.expected_inputs?.length) nodeData.expected_inputs = data.expected_inputs;
      if (Object.keys(data.input_mapping || {}).length)
        nodeData.input_mapping = data.input_mapping;
      if (data.follow_up_prompt) nodeData.follow_up_prompt = data.follow_up_prompt;
      if (data.follow_up_capture) nodeData.follow_up_capture = data.follow_up_capture;
      if (data.gpt?.enabled) nodeData.gpt = { enabled: true };

      conversationTree.nodes[node.id] = nodeData;
    });

    const json = JSON.stringify(conversationTree, null, 2);
    const blob = new Blob([json], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'conversation_tree.json';
    a.click();
    URL.revokeObjectURL(url);
  };

  const handleImportJSON = (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (event) => {
      try {
        const json = JSON.parse(event.target.result);
        setSystemPrompt(json.settings?.default_system_prompt || '');

        const importedNodes = Object.entries(json.nodes || {}).map(([id, node], index) => ({
          id,
          position: { x: 100 + index * 50, y: 100 + index * 50 },
          data: {
            label: node.message.slice(0, 20) + (node.message.length > 20 ? '...' : ''),
            message: node.message,
            capture: node.capture || '',
            next: node.next || '',
            end: node.end || false,
            expected_inputs: node.expected_inputs || [],
            input_mapping: node.input_mapping || {},
            follow_up_prompt: node.follow_up_prompt || '',
            follow_up_capture: node.follow_up_capture || '',
            gpt: node.gpt || { enabled: false }
          },
          type: 'default'
        }));

        const importedEdges = [];
        Object.entries(json.nodes || {}).forEach(([sourceId, node]) => {
          if (node.options) {
            Object.entries(node.options).forEach(([label, targetId]) => {
              importedEdges.push({
                id: `e${sourceId}-${targetId}`,
                source: sourceId,
                target: targetId,
                data: { label }
              });
            });
          }
        });

        setNodes(importedNodes);
        setEdges(importedEdges);
      } catch (err) {
        alert('Invalid JSON format');
      }
    };

    reader.readAsText(file);
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', height: '100vh' }}>
      <div style={{ padding: 10, background: '#eee' }}>
        <button onClick={addNewNode} style={{ marginRight: 10 }}>Add Node</button>
        <button onClick={deleteSelectedNode} style={{ marginRight: 10 }}>Delete Node</button>
        <input type="file" accept=".json" onChange={handleImportJSON} />
        <div style={{ marginTop: 10 }}>
          <label><strong>System Prompt</strong></label>
          <textarea
            value={systemPrompt}
            onChange={(e) => setSystemPrompt(e.target.value)}
            rows={4}
            style={{ width: '100%' }}
          />
        </div>
      </div>
      {/* ‚¨áÔ∏è All the rest of your UI stays the same... */}
      {/* ...Node Editor, Edge Editor, ReactFlow, Export Button... */}
    </div>
  );
}

export default App;
