import "reactflow/dist/style.css";

const nodeTypes = {};
let nodeId = 1;

function App() {
  const [nodes, setNodes] = useState([]);
  const [edges, setEdges] = useState([]);
  const [manualEdges, setManualEdges] = useState([]);
  const [selectedNodeId, setSelectedNodeId] = useState(null);
  const [selectedEdgeId, setSelectedEdgeId] = useState(null);
  const [chatbotReady, setChatbotReady] = useState(false);
  const [chatHistory, setChatHistory] = useState([]);
  const [currentNodeId, setCurrentNodeId] = useState(null);
  const [userInputs, setUserInputs] = useState({});
  const [inputValue, setInputValue] = useState("");
  const [multiChoiceSelection, setMultiChoiceSelection] = useState([]);
  const [submittedFlow, setSubmittedFlow] = useState({
    settings: {
      system_prompt:
        "You are MindPeace, a compassionate mental health assistant. Based on the user's shared info and emotional state, continue the conversation empathetically.",
      gpt_model: "gpt-3.5-turbo"
    },
    nodes: [],
    edges: []
  });

  const onNodesChange = useCallback(
    (changes) => setNodes((nds) => applyNodeChanges(changes, nds)),
    []
  );

  const onEdgesChange = useCallback(
    (changes) => setEdges((eds) => applyEdgeChanges(changes, eds)),
    []
  );

  const onConnect = useCallback(
    (params) => {
      const newEdge = {
        ...params,
        id: `${params.source}-${params.target}`,
        label: "",
        type: "default"
      };
      setEdges((eds) => [...eds, newEdge]);
      setManualEdges((eds) => [...eds, newEdge]);

      const sourceNode = nodes.find((n) => n.id === params.source);
      const updatedNodes = nodes.map((node) => {
        if (node.id === sourceNode.id) {
          if (!node.data.options) node.data.options = {};
          node.data.options["Option"] = params.target;
        }
        return node;
      });
      setNodes(updatedNodes);
    },
    [nodes]
  );

  const addNode = () => {
    const newNode = {
      id: `${++nodeId}`,
      position: {
        x: Math.random() * 400,
        y: Math.random() * 400
      },
      data: {
        label: `Node ${nodeId}`,
        message: "",
        type: "text"
      },
      type: "default"
    };
    setNodes((nds) => [...nds, newNode]);
  };

  const deleteNode = () => {
    if (!selectedNodeId) return;
    setNodes((nds) => nds.filter((n) => n.id !== selectedNodeId));
    setEdges((eds) => eds.filter((e) => e.source !== selectedNodeId && e.target !== selectedNodeId));
    setSelectedNodeId(null);
  };

  const updateNodeField = (id, field, value) => {
    setNodes((nds) =>
      nds.map((n) => {
        if (n.id === id) {
          n.data = {
            ...n.data,
            [field]: value
          };
        }
        return n;
      })
    );
  };

  const updateEdgeLabel = (id, value) => {
    setEdges((eds) =>
      eds.map((e) => {
        if (e.id === id) {
          return {
            ...e,
            label: value
          };
        }
        return e;
      })
    );
    setManualEdges((eds) =>
      eds.map((e) => {
        if (e.id === id) {
          return {
            ...e,
            label: value
          };
        }
        return e;
      })
    );
  };
  const handleExport = () => {
    const exported = {
      settings: submittedFlow.settings,
      nodes: nodes.map((n) => ({
        id: n.id,
        message: n.data.message,
        type: n.data.type,
        capture: n.data.capture || "",
        next: n.data.next || "",
        options: n.data.options || {},
      })),
      edges: manualEdges.map((e) => ({
        source: e.source,
        target: e.target,
        label: e.label || "",
      }))
    };
    const blob = new Blob([JSON.stringify(exported, null, 2)], {
      type: "application/json"
    });
    const url = URL.createObjectURL(blob);
    const link = document.createElement("a");
    link.download = "conversation_data.json";
    link.href = url;
    link.click();
  };

  const handleSubmitFlow = () => {
    const flow = {
      settings: submittedFlow.settings,
      nodes: nodes.map((n) => ({
        id: n.id,
        message: n.data.message,
        type: n.data.type,
        capture: n.data.capture || "",
        next: n.data.next || "",
        options: n.data.options || {},
      })),
      edges: manualEdges.map((e) => ({
        source: e.source,
        target: e.target,
        label: e.label || "",
      }))
    };
    setSubmittedFlow(flow);
    setChatbotReady(true);
    setChatHistory([]);
    setCurrentNodeId(flow.nodes[0]?.id || null);
    setUserInputs({});
  };

  const updatePrompt = (e) => {
    setSubmittedFlow((prev) => ({
      ...prev,
      settings: {
        ...prev.settings,
        system_prompt: e.target.value
      }
    }));
  };

  const updateModel = (e) => {
    setSubmittedFlow((prev) => ({
      ...prev,
      settings: {
        ...prev.settings,
        gpt_model: e.target.value
      }
    }));
  };

  const fetchGPTResponse = async (userData) => {
    const summary = Object.entries(userData)
      .map(([key, value]) => `${key}: ${value}`)
      .join(", ");

    const userMessage = `User provided the following information: ${summary}. Continue the conversation empathetically.`;

    try {
      const res = await fetch("http://localhost:5000/gpt", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          system_prompt: submittedFlow.settings.system_prompt,
          gpt_model: submittedFlow.settings.gpt_model,
          user_message: userMessage
        })
      });
      const data = await res.json();
      setChatHistory((prev) => [...prev, { sender: "bot", text: data.response }]);
    } catch (err) {
      console.error("GPT fetch error:", err);
      setChatHistory((prev) => [...prev, { sender: "bot", text: "Sorry, something went wrong." }]);
    }
  };

  const renderChatInput = () => {
    const current = submittedFlow.nodes.find((n) => n.id === currentNodeId);
    if (!current) return null;

    if (current.type === "choice") {
      const buttons = Object.entries(current.options || {}).map(([label, target]) => (
        <button
          key={label}
          onClick={() => {
            setChatHistory((prev) => [...prev, { sender: "user", text: label }]);
            setCurrentNodeId(target);
          }}
          style={{ margin: "4px" }}
        >
          {label}
        </button>
      ));
      return <div>{buttons}</div>;
    }

    if (current.type === "multi_choice") {
      const options = Object.entries(current.options || {}).map(([label, target]) => (
        <div key={label}>
          <label>
            <input
              type="checkbox"
              checked={multiChoiceSelection.includes(label)}
              onChange={(e) => {
                if (e.target.checked) {
                  setMultiChoiceSelection((prev) => [...prev, label]);
                } else {
                  setMultiChoiceSelection((prev) =>
                    prev.filter((item) => item !== label)
                  );
                }
              }}
            />
            {label}
          </label>
        </div>
      ));
      return (
        <div>
          {options}
          <button
            onClick={() => {
              setChatHistory((prev) => [...prev, { sender: "user", text: multiChoiceSelection.join(", ") }]);
              if (current.capture) {
                setUserInputs((prev) => ({
                  ...prev,
                  [current.capture]: multiChoiceSelection
                }));
              }
              const firstTarget = current.options[multiChoiceSelection[0]];
              setCurrentNodeId(firstTarget || null);
              setMultiChoiceSelection([]);
            }}
            style={{ marginTop: "5px" }}
          >
            Submit Selection
          </button>
        </div>
      );
    }

    if (current.type === "gpt") {
      return (
        <div>
          <input
            type="text"
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
            placeholder="Type your message..."
            style={{ width: "100%", padding: "8px", marginBottom: "4px" }}
          />
          <button
            onClick={() => {
              const userInput = inputValue.trim();
              if (!userInput) return;
              setChatHistory((prev) => [...prev, { sender: "user", text: userInput }]);
              fetchGPTResponse({ ...userInputs, [current.data.capture]: userInput });
              setInputValue("");
            }}
          >
            Send
          </button>
        </div>
      );
    }

    return (
      <div>
        <input
          type="text"
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
          placeholder="Enter response..."
          style={{ width: "100%", padding: "8px", marginBottom: "4px" }}
        />
        <button
          onClick={() => {
            const userInput = inputValue.trim();
            if (!userInput) return;
            setChatHistory((prev) => [...prev, { sender: "user", text: userInput }]);
            if (current.capture) {
              setUserInputs((prev) => ({
                ...prev,
                [current.capture]: userInput
              }));
            }
            setCurrentNodeId(current.next || null);
            setInputValue("");
          }}
        >
          Submit
        </button>
      </div>
    );
  };
  return (
    <div style={{ display: "flex", height: "100vh" }}>
      <div style={{ width: "75%", position: "relative" }}>
        <div style={{ padding: "10px", borderBottom: "1px solid #ccc", background: "#f9f9f9" }}>
          <h2 style={{ margin: "0", fontSize: "18px" }}>Your AI Chatbot Designer â€” Fast. Visual. Code-Free</h2>
          <div style={{ marginTop: "10px" }}>
            <button onClick={addNode} style={{ background: "#007bff", color: "#fff", marginRight: "5px" }}>Add Node</button>
            <button onClick={handleExport} style={{ background: "#28a745", color: "#fff", marginRight: "5px" }}>Export JSON</button>
            <button onClick={deleteNode} style={{ background: "#dc3545", color: "#fff" }}>Delete Node</button>
            <button onClick={handleSubmitFlow} style={{ background: "#6f42c1", color: "#fff", marginLeft: "10px" }}>Submit Flow</button>
          </div>
        </div>
        <ReactFlow
          nodes={nodes}
          edges={edges}
          onNodesChange={onNodesChange}
          onEdgesChange={onEdgesChange}
          onConnect={onConnect}
          onNodeClick={(_, node) => setSelectedNodeId(node.id)}
          onEdgeClick={(_, edge) => setSelectedEdgeId(edge.id)}
          fitView
        >
          <Background />
          <Controls />
          <MiniMap />
        </ReactFlow>
      </div>

      <div style={{ width: "25%", padding: "10px", background: "#f1f1f1", overflowY: "auto" }}>
        <div style={{ marginBottom: "20px" }}>
          <h3>Prompt Settings</h3>
          <label>System Prompt:</label>
          <textarea
            value={submittedFlow.settings.system_prompt}
            onChange={updatePrompt}
            style={{ width: "100%", height: "100px", marginBottom: "10px" }}
          />
          <label>GPT Model:</label>
          <input
            value={submittedFlow.settings.gpt_model}
            onChange={updateModel}
            style={{ width: "100%", marginBottom: "20px" }}
          />
        </div>

        {selectedNodeId && (
          <div style={{ marginBottom: "20px" }}>
            <h3>Node Editor</h3>
            <label>Message:</label>
            <textarea
              value={nodes.find((n) => n.id === selectedNodeId)?.data.message || ""}
              onChange={(e) =>
                updateNodeField(selectedNodeId, "message", e.target.value)
              }
              style={{ width: "100%", height: "60px" }}
            />
            <label>Type:</label>
            <select
              value={nodes.find((n) => n.id === selectedNodeId)?.data.type || ""}
              onChange={(e) =>
                updateNodeField(selectedNodeId, "type", e.target.value)
              }
              style={{ width: "100%", marginBottom: "10px" }}
            >
              <option value="text">Text</option>
              <option value="choice">Choice</option>
              <option value="multi_choice">Multi Choice</option>
              <option value="gpt">GPT</option>
            </select>
            <label>Capture Field:</label>
            <input
              type="text"
              value={nodes.find((n) => n.id === selectedNodeId)?.data.capture || ""}
              onChange={(e) =>
                updateNodeField(selectedNodeId, "capture", e.target.value)
              }
              style={{ width: "100%" }}
            />
            <label>Next Node ID:</label>
            <input
              type="text"
              value={nodes.find((n) => n.id === selectedNodeId)?.data.next || ""}
              onChange={(e) =>
                updateNodeField(selectedNodeId, "next", e.target.value)
              }
              style={{ width: "100%" }}
            />
          </div>
        )}

        {selectedEdgeId && (
          <div>
            <h3>Edge Editor</h3>
            <label>Label:</label>
            <input
              type="text"
              value={edges.find((e) => e.id === selectedEdgeId)?.label || ""}
              onChange={(e) =>
                updateEdgeLabel(selectedEdgeId, e.target.value)
              }
              style={{ width: "100%" }}
            />
          </div>
        )}
      </div>

      {chatbotReady && (
        <div
          style={{
            position: "absolute",
            right: 0,
            top: 0,
            width: "25%",
            height: "100%",
            background: "#ffffff",
            borderLeft: "1px solid #ccc",
            padding: "10px",
            overflowY: "auto"
          }}
        >
          <h3>MindPeace Chat</h3>
          <div style={{ height: "80%", overflowY: "scroll", marginBottom: "10px" }}>
            {chatHistory.map((msg, idx) => (
              <div
                key={idx}
                style={{
                  background: msg.sender === "user" ? "#daf1ff" : "#e6e6e6",
                  padding: "8px",
                  borderRadius: "8px",
                  marginBottom: "5px",
                  alignSelf: msg.sender === "user" ? "flex-end" : "flex-start"
                }}
              >
                {msg.text}
              </div>
            ))}
          </div>
          {renderChatInput()}
        </div>
      )}
    </div>
  );
}

export default App;
