import React, { useState, useCallback, useEffect } from "react";
import ReactFlow, {
  addEdge,
  Background,
  Controls,
  MiniMap,
  applyNodeChanges,
  applyEdgeChanges,
  useReactFlow
} from "reactflow";
import "reactflow/dist/style.css";

const nodeTypes = {};

let nodeId = 1;

function App() {
  const [nodes, setNodes] = useState([]);
  const [edges, setEdges] = useState([]);
  const [manualEdges, setManualEdges] = useState([]);
  const [selectedNodeId, setSelectedNodeId] = useState(null);
  const [selectedEdgeId, setSelectedEdgeId] = useState(null);
  const [chatbotReady, setChatbotReady] = useState(false);
  const [chatHistory, setChatHistory] = useState([]);
  const [currentNodeId, setCurrentNodeId] = useState(null);
  const [userInputs, setUserInputs] = useState({});
  const [inputValue, setInputValue] = useState("");
  const [multiChoiceSelection, setMultiChoiceSelection] = useState([]);
  const [submittedFlow, setSubmittedFlow] = useState({
    settings: {
      system_prompt:
        "You are MindPeace, a compassionate mental health assistant. Based on the user's shared info and emotional state, continue the conversation empathetically.",
      gpt_model: "gpt-3.5-turbo"
    },
    nodes: [],
    edges: []
  });

  const onNodesChange = useCallback(
    (changes) => setNodes((nds) => applyNodeChanges(changes, nds)),
    []
  );

  const onEdgesChange = useCallback(
    (changes) => setEdges((eds) => applyEdgeChanges(changes, eds)),
    []
  );

  const onConnect = useCallback(
    (params) => {
      const newEdge = {
        ...params,
        id: `${params.source}-${params.target}`,
        label: "",
        type: "default"
      };
      setEdges((eds) => [...eds, newEdge]);
      setManualEdges((eds) => [...eds, newEdge]);

      const sourceNode = nodes.find((n) => n.id === params.source);
      const updatedNodes = nodes.map((node) => {
        if (node.id === sourceNode.id) {
          if (!node.data.options) node.data.options = {};
          node.data.options["Option"] = params.target;
        }
        return node;
      });
      setNodes(updatedNodes);
    },
    [nodes]
  );

  const addNode = () => {
    const newNode = {
      id: `${++nodeId}`,
      position: {
        x: Math.random() * 400,
        y: Math.random() * 400
      },
      data: {
        label: `Node ${nodeId}`,
        message: "",
        type: "text"
      },
      type: "default"
    };
    setNodes((nds) => [...nds, newNode]);
  };

  const deleteNode = () => {
    if (!selectedNodeId) return;
    setNodes((nds) => nds.filter((n) => n.id !== selectedNodeId));
    setEdges((eds) => eds.filter((e) => e.source !== selectedNodeId && e.target !== selectedNodeId));
    setSelectedNodeId(null);
  };

  const updateNodeField = (id, field, value) => {
    setNodes((nds) =>
      nds.map((n) => {
        if (n.id === id) {
          n.data = {
            ...n.data,
            [field]: value
          };
        }
        return n;
      })
    );
  };

  const updateEdgeLabel = (id, value) => {
    setEdges((eds) =>
      eds.map((e) => {
        if (e.id === id) {
          return {
            ...e,
            label: value
          };
        }
        return e;
      })
    );
    setManualEdges((eds) =>
      eds.map((e) => {
        if (e.id === id) {
          return {
            ...e,
            label: value
          };
        }
        return e;
      })
    );
  };
  const handleExport = () => {
    const exportData = {
      settings: submittedFlow.settings,
      nodes: nodes.map((n) => ({
        id: n.id,
        message: n.data.message,
        type: n.data.type,
        capture: n.data.capture,
        next: n.data.next,
        options: n.data.options,
        expected_inputs: n.data.expected_inputs,
        input_mapping: n.data.input_mapping,
        follow_up_prompt: n.data.follow_up_prompt,
        follow_up_capture: n.data.follow_up_capture,
        gpt: n.data.gpt,
        end: n.data.end
      })),
      edges: edges.map((e) => ({
        source: e.source,
        target: e.target,
        label: e.label
      }))
    };
    const blob = new Blob([JSON.stringify(exportData, null, 2)], { type: "application/json" });
    const url = URL.createObjectURL(blob);
    const link = document.createElement("a");
    link.download = "conversation_data.json";
    link.href = url;
    link.click();
  };

  const handleSubmitFlow = () => {
    const exportData = {
      settings: submittedFlow.settings,
      nodes: nodes.map((n) => ({
        id: n.id,
        message: n.data.message,
        type: n.data.type,
        capture: n.data.capture,
        next: n.data.next,
        options: n.data.options,
        expected_inputs: n.data.expected_inputs,
        input_mapping: n.data.input_mapping,
        follow_up_prompt: n.data.follow_up_prompt,
        follow_up_capture: n.data.follow_up_capture,
        gpt: n.data.gpt,
        end: n.data.end
      })),
      edges: edges.map((e) => ({
        source: e.source,
        target: e.target,
        label: e.label
      }))
    };
    setSubmittedFlow(exportData);
    setChatbotReady(true);
    const startNode = nodes.find((n) => n.data.type !== "gpt");
    if (startNode) {
      setCurrentNodeId(startNode.id);
      setChatHistory([{ sender: "bot", text: startNode.data.message }]);
    }
  };

  const renderChatInput = () => {
    const currentNode = nodes.find((n) => n.id === currentNodeId);
    if (!currentNode) return null;

    if (currentNode.data.type === "multi_choice" && currentNode.data.options) {
      const options = Object.entries(currentNode.data.options);
      return (
        <div>
          {options.map(([label, target]) => (
            <div key={label}>
              <input
                type="checkbox"
                id={label}
                checked={multiChoiceSelection.includes(label)}
                onChange={() => {
                  if (multiChoiceSelection.includes(label)) {
                    setMultiChoiceSelection(multiChoiceSelection.filter((l) => l !== label));
                  } else {
                    setMultiChoiceSelection([...multiChoiceSelection, label]);
                  }
                }}
              />
              <label htmlFor={label}>{label}</label>
            </div>
          ))}
          <button
            onClick={() => {
              setChatHistory((prev) => [...prev, { sender: "user", text: multiChoiceSelection.join(", ") }]);
              const targetNodeId = options[0][1]; // just use first option's target for now
              setCurrentNodeId(targetNodeId);
              const targetNode = nodes.find((n) => n.id === targetNodeId);
              if (targetNode) {
                setChatHistory((prev) => [...prev, { sender: "bot", text: targetNode.data.message }]);
              }
              setMultiChoiceSelection([]);
            }}
          >
            Submit Selection
          </button>
        </div>
      );
    }

    if (currentNode.data.type === "choice" && currentNode.data.options) {
      return (
        <div>
          {Object.entries(currentNode.data.options).map(([label, target]) => (
            <button
              key={label}
              onClick={() => {
                setChatHistory((prev) => [...prev, { sender: "user", text: label }]);
                setCurrentNodeId(target);
                const targetNode = nodes.find((n) => n.id === target);
                if (targetNode) {
                  setChatHistory((prev) => [...prev, { sender: "bot", text: targetNode.data.message }]);
                }
              }}
            >
              {label}
            </button>
          ))}
        </div>
      );
    }

    return (
      <form
        onSubmit={(e) => {
          e.preventDefault();
          const userInput = inputValue.trim();
          if (!userInput) return;
          setChatHistory((prev) => [...prev, { sender: "user", text: userInput }]);
          setInputValue("");
          const current = nodes.find((n) => n.id === currentNodeId);
          if (!current) return;
          if (current.data.capture) {
            setUserInputs({ ...userInputs, [current.data.capture]: userInput });
          }

          const nextNodeId = current.data.next;
          const nextNode = nodes.find((n) => n.id === nextNodeId);
          if (nextNode) {
            setCurrentNodeId(nextNodeId);
            if (nextNode.data.type === "gpt") {
              fetchGPTResponse({ ...userInputs, [current.data.capture]: userInput });
            } else {
              setChatHistory((prev) => [...prev, { sender: "bot", text: nextNode.data.message }]);
            }
          }
        }}
      >
        <input
          type="text"
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
          placeholder="Type your answer..."
        />
        <button type="submit">Send</button>
      </form>
    );
  };

  const updatePrompt = (e) => {
    setSubmittedFlow((prev) => ({
      ...prev,
      settings: {
        ...prev.settings,
        system_prompt: e.target.value
      }
    }));
  };

  const updateModel = (e) => {
    setSubmittedFlow((prev) => ({
      ...prev,
      settings: {
        ...prev.settings,
        gpt_model: e.target.value
      }
    }));
  };
  return (
    <div style={{ display: "flex", height: "100vh", fontFamily: "Arial, sans-serif" }}>
      <div style={{ width: "70%", height: "100%", position: "relative" }}>
        <h2 style={{ textAlign: "center", margin: "10px" }}>Your AI Chatbot Designer â€” Fast. Visual. Code-Free</h2>
        <div style={{ display: "flex", justifyContent: "center", marginBottom: "10px", gap: "10px" }}>
          <button style={{ backgroundColor: "#007bff", color: "white" }} onClick={addNode}>Add Node</button>
          <button style={{ backgroundColor: "#dc3545", color: "white" }} onClick={deleteNode}>Delete Node</button>
          <button style={{ backgroundColor: "#28a745", color: "white" }} onClick={handleExport}>Export JSON</button>
          <button style={{ backgroundColor: "#17a2b8", color: "white" }} onClick={handleSubmitFlow}>Submit Flow</button>
        </div>
        <ReactFlow
          nodes={nodes}
          edges={edges}
          onNodesChange={onNodesChange}
          onEdgesChange={onEdgesChange}
          onConnect={onConnect}
          onNodeClick={(_, node) => setSelectedNodeId(node.id)}
          onEdgeClick={(_, edge) => setSelectedEdgeId(edge.id)}
          fitView
        >
          <Background />
          <MiniMap />
          <Controls />
        </ReactFlow>
        {selectedNodeId && (
          <div style={{ position: "absolute", top: 50, left: 10, background: "#f0f0f0", padding: 10, borderRadius: 8 }}>
            <h4>Node Editor</h4>
            <label>Message:</label>
            <textarea
              value={nodes.find((n) => n.id === selectedNodeId)?.data.message || ""}
              onChange={(e) => updateNodeField(selectedNodeId, "message", e.target.value)}
            />
            <br />
            <label>Type:</label>
            <select
              value={nodes.find((n) => n.id === selectedNodeId)?.data.type || ""}
              onChange={(e) => updateNodeField(selectedNodeId, "type", e.target.value)}
            >
              <option value="text">text</option>
              <option value="choice">choice</option>
              <option value="multi_choice">multi_choice</option>
              <option value="gpt">gpt</option>
            </select>
            <br />
            <label>Capture:</label>
            <input
              type="text"
              value={nodes.find((n) => n.id === selectedNodeId)?.data.capture || ""}
              onChange={(e) => updateNodeField(selectedNodeId, "capture", e.target.value)}
            />
            <br />
            <label>Next Node ID:</label>
            <input
              type="text"
              value={nodes.find((n) => n.id === selectedNodeId)?.data.next || ""}
              onChange={(e) => updateNodeField(selectedNodeId, "next", e.target.value)}
            />
          </div>
        )}
        {selectedEdgeId && (
          <div style={{ position: "absolute", top: 50, right: 10, background: "#f0f0f0", padding: 10, borderRadius: 8 }}>
            <h4>Edge Editor</h4>
            <label>Label:</label>
            <input
              type="text"
              value={edges.find((e) => e.id === selectedEdgeId)?.label || ""}
              onChange={(e) => updateEdgeLabel(selectedEdgeId, e.target.value)}
            />
          </div>
        )}
      </div>

      <div style={{ width: "30%", height: "100%", padding: "10px", borderLeft: "1px solid #ccc", overflowY: "auto" }}>
        {chatbotReady ? (
          <>
            <h3>MindPeace Chatbot</h3>
            <div style={{ maxHeight: "80vh", overflowY: "auto", marginBottom: "10px" }}>
              {chatHistory.map((msg, idx) => (
                <div
                  key={idx}
                  style={{
                    backgroundColor: msg.sender === "bot" ? "#e1f5fe" : "#c8e6c9",
                    padding: "8px",
                    margin: "5px 0",
                    borderRadius: "10px"
                  }}
                >
                  <strong>{msg.sender === "bot" ? "MindPeace" : "You"}:</strong> {msg.text}
                </div>
              ))}
            </div>
            {renderChatInput()}
          </>
        ) : (
          <>
            <h3>Prompt Settings</h3>
            <label>System Prompt:</label>
            <textarea
              value={submittedFlow.settings.system_prompt}
              onChange={updatePrompt}
              rows={6}
              style={{ width: "100%", marginBottom: "10px" }}
            />
            <label>Model:</label>
            <select
              value={submittedFlow.settings.gpt_model}
              onChange={updateModel}
              style={{ width: "100%", marginBottom: "10px" }}
            >
              <option value="gpt-3.5-turbo">gpt-3.5-turbo</option>
              <option value="gpt-4">gpt-4</option>
            </select>
          </>
        )}
      </div>
    </div>
  );
}

export default App;
