import React from "react";

export default function ChatbotPanel({
  chatbotReady,
  chatHistory,
  currentNodeId,
  submittedFlow,
  inputValue,
  setInputValue,
  handleUserResponse
}) {
  const renderChatInput = () => {
    if (!submittedFlow || !currentNodeId) return null;
    const node = submittedFlow.nodes[currentNodeId];

    if (node.type === "choice" || node.type === "multi_choice") {
      return Object.keys(node.options || {}).map((opt) => (
        <button
          key={opt}
          onClick={() => handleUserResponse(opt)}
          style={{
            padding: "8px",
            margin: "4px 0",
            width: "100%",
            borderRadius: 6,
            border: "1px solid #ccc",
            background: "#e0f2fe",
            cursor: "pointer"
          }}
        >
          {opt}
        </button>
      ));
    }

    if (node.type === "input") {
      return (
        <div>
          <input
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
            onKeyDown={(e) => e.key === "Enter" && handleUserResponse(inputValue)}
            placeholder="Type your answer..."
            style={{
              width: "100%",
              padding: 8,
              marginBottom: 8,
              borderRadius: 6,
              border: "1px solid #ccc"
            }}
          />
          <button
            onClick={() => {
              handleUserResponse(inputValue);
              setInputValue("");
            }}
            style={{
              width: "100%",
              background: "#38bdf8",
              color: "white",
              borderRadius: 6,
              padding: "8px 12px",
              border: "none"
            }}
          >
            Submit
          </button>
        </div>
      );
    }

    return null;
  };

  return (
    <div
      style={{
        width: "320px",
        padding: 20,
        borderLeft: "1px solid #ddd",
        display: "flex",
        flexDirection: "column",
        justifyContent: "space-between"
      }}
    >
      <div style={{ fontWeight: "bold", marginBottom: 10 }}>MindPeace Chat</div>

      <div style={{ flexGrow: 1, overflowY: "auto", marginBottom: 10 }}>
        {chatbotReady ? (
          <div style={{ display: "flex", flexDirection: "column", gap: 10 }}>
            {chatHistory.map((entry, index) => (
              <div
                key={index}
                style={{
                  alignSelf: entry.sender === "user" ? "flex-end" : "flex-start",
                  background: entry.sender === "user" ? "#e0f2fe" : "#f3f4f6",
                  padding: 8,
                  borderRadius: 6,
                  maxWidth: "90%"
                }}
              >
                {entry.message}
              </div>
            ))}
          </div>
        ) : (
          <div>Please submit your flow to activate the chatbot.</div>
        )}
      </div>

      {chatbotReady && renderChatInput()}
    </div>
  );
}
