// PromptSettings.js
import React, { useState, useEffect } from "react";

export default function PromptSettings({ submittedFlow, setSubmittedFlow }) {
  const [systemPrompt, setSystemPrompt] = useState("");
  const [gptModel, setGptModel] = useState("gpt-3.5-turbo");

  useEffect(() => {
    if (submittedFlow?.settings) {
      setSystemPrompt(submittedFlow.settings.system_prompt || "");
      setGptModel(submittedFlow.settings.gpt_model || "gpt-3.5-turbo");
    }
  }, [submittedFlow]);

  const updateSettings = () => {
    if (!submittedFlow) return;

    const updatedFlow = {
      ...submittedFlow,
      settings: {
        ...submittedFlow.settings,
        system_prompt: systemPrompt,
        gpt_model: gptModel
      }
    };

    setSubmittedFlow(updatedFlow);
    alert("Prompt and model updated!");
  };

  return (
    <div style={{
      marginTop: 20,
      padding: 12,
      borderTop: "1px solid #ddd",
      background: "#f1f5f9",
      borderRadius: "8px"
    }}>
      <h4 style={{ marginBottom: 8 }}>Prompt Settings</h4>

      <label style={{ fontWeight: "bold" }}>System Prompt:</label>
      <textarea
        value={systemPrompt}
        onChange={(e) => setSystemPrompt(e.target.value)}
        style={{
          width: "100%",
          height: "80px",
          marginBottom: 10,
          borderRadius: 6,
          padding: 8,
          border: "1px solid #ccc"
        }}
      />

      <label style={{ fontWeight: "bold" }}>GPT Model:</label>
      <select
        value={gptModel}
        onChange={(e) => setGptModel(e.target.value)}
        style={{
          width: "100%",
          padding: 8,
          borderRadius: 6,
          border: "1px solid #ccc",
          marginBottom: 10
        }}
      >
        <option value="gpt-3.5-turbo">gpt-3.5-turbo</option>
        <option value="gpt-4">gpt-4</option>
        <option value="gpt-4o">gpt-4o</option>
      </select>

      <button
        onClick={updateSettings}
        style={{
          background: "#0ea5e9",
          color: "white",
          border: "none",
          borderRadius: 6,
          padding: "6px 12px",
          cursor: "pointer",
          width: "100%"
        }}
      >
        Save Settings
      </button>
    </div>
  );
}
